float sum(vec3 pos) {

    float val = 0.0;

    float t = 1.0;

    float warp = snoise(pos*0.3);
    vec3 p2 = pos*vec3(1.0,warp*8.0,1.0);//pos + vec3(0.0,warp*8.0,0.0);


    val += two(pos)*0.4; t *= 2.01;
    //val += snoise(pos*t)*0.05;
    //t *= 2.01;
    //val += snoise(pos*t)*0.125;
    //t *= 2.01;
    //t *= 2.01;
    //t *= 2.01;
    val += snoise(p2*t)*(val*0.1);

    return val - 0.08;
}


