vec2 map( in vec3 pos )
{
    vec2 res = opU( vec2( sdPlane(     pos), 1.0 ),
	                vec2( sum(pos), 10.0 ) ); //sdSphere(    pos-vec3( 0.0,1.0, 0.0), 1.0 )
    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

    float t = tmin;
    float m = -1.0;
    steps = 0.0;
    for( int i=0; i<MAX_RAY_STEPS; i++ )
    {
	    float precis = 0.0005*t;
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
        steps += 1.0;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}

float castRayF( in vec3 ro, in vec3 rd )
{
    float tmin = 0.1;
    float tmax = 10.0;
    float t = tmin;
    for( int i=0; i<128; i++ )
    {
	    float precis = 0.0005*t;
	    float tempT = map( ro+rd*t ).x;
        if( tempT<precis || t>tmax ) break;
        t += tempT;
    }
    if( t>tmax )t = -1.0;
    return t;
}

vec3 calcNormal( in vec3 pos)
{
	vec2 eps = vec2( 0.001, 0.0 );

	vec3 nor = vec3( map(pos+eps.xyy).x - map(pos-eps.xyy).x,
					 map(pos+eps.yxy).x - map(pos-eps.yxy).x,
    				 map(pos+eps.yyx).x - map(pos-eps.yyx).x);
	return normalize(nor);
}

mat3 base( in vec3 ww ) {
    vec3  vv  = vec3(0.0,0.0,1.0);
    vec3  uu  = normalize( cross( vv, ww ) );
    return mat3(uu.x,ww.x,vv.x,
                uu.y,ww.y,vv.y,
                uu.z,ww.z,vv.z);
}
vec3 sphereToCart(vec3 r) {
    return vec3(r.x*sin(r.z)*cos(r.y), r.x*sin(r.z)*sin(r.y), r.x*cos(r.z));
}
float simpleAO(vec3 p, vec3 n) {
    mat3 b = base(n);
    float ao = 0.0;

    int c = 3;
    float it = 1.0/float(c*c);

    for(int y = 0; y < c; y++) {
        for(int x = 0; x < c; x++) {
            vec3 rd = sphereToCart( vec3(0.1, (float(x)/4.0)*PI, (float(y)/4.0)*PI) )*b;
            float t = castRayF(p + rd*0.01, rd);
            if(t<0.0)ao += it;
        }
    }
    return ao;
}


//IQ's AO
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<2; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}


mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


