void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    vec3 ro = vec3( -0.5+3.5*cos(0.1*time + 6.0*mo.x), 1.0 + 10.0*mo.y, 0.5 + 4.0*sin(0.1*time + 6.0*mo.x) );
    vec3 ta = vec3( -0.5, 0.7, 0.5 );
    mat3 ca = setCamera( ro, ta, 0.0 );
    vec3 rd = ca * normalize( vec3(p.xy,2.0) );





    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;

    vec3 pos = ro + t*rd;
    vec3 nor = calcNormal( pos );
    vec3 light = vec3(cos(1.0),0.8,sin(1.0));

    if(m > -0.5) {
        vec3 pnor = normalize(pos - vec3(0.0,1.0,0.0));

        float c = max(dot(nor,pnor),0.0);

        c*= 30.0;

        m += c;
        m = 0.0;

        col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );

        col = test(nor);

        if( m<1.5 ) {

            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.3 + 0.1*f*vec3(1.0);

        }

        float diff = max(dot(nor,light),0.0);

        float ao = calcAO(pos, nor);

        col = col*diff*0.6 + col*ao*0.4;
    }

    vec2 uv = fragCoord.xy / iResolution.xy;
    if(uv.x > 0.5) {
        col = heatMap(steps/float(MAX_RAY_STEPS));
    }

    fragColor = vec4( col, 1.0 );
}

// Center of the screen in pixels and UV coordinates
#define CENTER (iResolution.xy / 2.0)
#define CENTER_UV vec2(0.0)

//center of sigmoids for the background
#define NEAR 0.2
#define FAR 0.7
#define WIDTH 0.1

//Center of sigmoids for the mouse
#define NEAR_MOUSE 0.05
#define FAR_MOUSE 0.1
#define WIDTH_MOUSE 0.01

// These parameters control the sine wave.
#define MID_ANGLE 0.5
#define SECTOR_SIZE 0.01
#define ROTATE_FREQ 0.4
#define TWIST_AMP 0.5
#define TWIST_FREQ 10.0
// Not sure if this is the right physics term. This
// is a multiplier of the phase angle in space.
#define TWIST_PHASE_VEL 5.0
// This prevents the sigmoids from having 0 width.
#define BLUR_BIAS 0.01
#define BLUR_AMP 0.4
#define BLUR_FREQ 0.3

// Fuzzy logic operations
#define NOT(x) (1.0 - (x))
#define AND min
#define OR max

// Fuzzy logic hedges
#define VERY(x) ((x) * (x))
#define SOMEWHAT sqrt

// I wish this came with GLSL out of the box.
#define PI 3.141592
#define TAU (2.0 * PI)

// Compute a fuzzy set for distance to a point.
// I use sigmoid-shaped boundaries with smoothstep()
// x is the input point
// center is the center of the distance field
// near, far are the center of the sigmoid-like curves that divide the sets.
// width is the width of the sigmoid-like curves
vec3 fuzzy_dist(float dist, float near_point, float far_point, float width) {
    float half_width = width / 2.0;

    // Make the sigmoid shapes for near and far using smoothstep()
    float near = smoothstep(near_point + half_width, near_point - half_width, dist);
    float far = smoothstep(far_point - half_width, far_point + half_width, dist);

    // We are at a medium distance if we are not near and not far.
    float mid = AND(NOT(near), NOT(far));

    // Return the membership for the three categories.
    return vec3(near, mid, far);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized coordinates from the center of the screen.
    // This accounts for the screen aspect ratio.
    vec2 uv = (fragCoord - CENTER)/iResolution.y;

    // Do the same for mouse coordinates. Note that for some reason
    // Shadertoy only updates the mouse when a button is pressed.
    vec2 mouse_uv = (iMouse.xy - CENTER)/iResolution.y;

    // Compute fuzzy sets ====================================

    // Compute the fuzzy set for distance to the center of the screen.
    float from_center = distance(uv, CENTER_UV);
    vec3 center_dist = fuzzy_dist(from_center, NEAR, FAR, WIDTH);

    // Compute the fuzzy set for distance to the mouse
    float from_mouse = distance(uv, mouse_uv);
    vec3 mouse_dist = fuzzy_dist(from_mouse, NEAR_MOUSE, FAR_MOUSE, WIDTH_MOUSE);

    // In principle this is computing the distance from pi radians
    // around the circle. However, I'm rotating and distorting the
    // polar coordinates with a sine wave for a cooler effect!
    // Polar angle
    float angle = atan(uv.y, uv.x);
    // Rotate cooordinates
    float rotate = ROTATE_FREQ * TAU * iTime;
    // Twist the coordinates with a high-frequency sine wave
    // in the radial directtion.
    float twist = TWIST_AMP * sin(
        TWIST_FREQ * TAU * from_center - TWIST_PHASE_VEL * TAU * iTime);
    // Apply the transformations
    float transformed = angle - rotate + twist;
    // Normalize from 0 (0 radians) to 1 (2 pi radiaans)
    float normalized = mod(transformed, TAU) / TAU;
    // Vary the shape of the fuzzy sets for an even cooler effect!
    // Specifically, stretch/shrink the width of the curves.
	// This needs to be a positive number else smoothstep() in my 
    // fuzzy_dist function
    //will misbehave.
    float width_blur = BLUR_BIAS + BLUR_AMP + BLUR_AMP * sin(
        BLUR_FREQ * TAU * iTime);

    //vec3 angle_dist = fuzzy_dist(normalized, NEAR_ANGLE, FAR_ANGLE, blur);
    vec3 angle_dist = fuzzy_dist(
        normalized, 
        MID_ANGLE - SECTOR_SIZE, 
        MID_ANGLE + SECTOR_SIZE, 
        width_blur);

    // =============================================================
    // Let's make some fuzzy logic propositions to make some abstract art!

    // If distance to mouse is very, very medium and distance to the
    // center of the screeen is somewhat medium, paint a ring.
    // This ring will only show up inside the middle distance field for 
    float mouse_vv_med = VERY(VERY(mouse_dist.y));
    float center_s_med = SOMEWHAT(center_dist.y);
    float ring = AND(mouse_vv_med, center_s_med);

    // If distance from the center is far or very near, paint a background.
    float background = OR(center_dist.z, VERY(center_dist.x));

    // If the angle is medium and the distance to the center of the
    // screen is somewhat medium, paint it in.
    float wave = AND(center_s_med, angle_dist.y);

    // Let's build the image. =======================================

    // The background is orange.
    vec4 image = vec4(1.0, 0.5, 0.0, 1.0) * background;


    // Texture the ring around the mouse.
    vec4 ring_color = texture(iChannel0, uv);
    image = mix(image, ring_color, ring);

    // Draw the sweeping ray in teal
    vec4 wave_color = vec4(0.0, 1.0, 0.8, 1.0);
    image = mix(image, wave_color, wave);

    // Output to screen ============================================
    fragColor = vec4(image);
}

#define PI 3.14159265359
#define TWO_PI (2. * PI)

#define _SPIRAL(a,d,w) (abs(((a) / (d)) - (w)))
float spiral(in float angle, in float dist, in float k, in float max_winding)
{
    float v = _SPIRAL(angle, dist, k);
    for(float i = 1.; i < max_winding; i += 1.)
    {
	    v = min(v, _SPIRAL(angle + i * TWO_PI, dist, k));
    }
    return v;
}

float get_angle(in vec2 uv)
{
    float uv_angle = atan(uv.y, uv.x);
    if(uv_angle < 0.0)
    	uv_angle += TWO_PI;
    return uv_angle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord - .5 * iResolution.xy ) /iResolution.x;

    float rt = iTime * 0.5;
    mat2 rmt = mat2(cos(rt), -sin(rt), sin(rt), cos(rt));
    uv = rmt * uv;

    vec3 col = vec3(0);

    float r = 0.5 * spiral(get_angle(uv), length(uv), 200. + 149. * sin(iTime), 20.);
    r = 2.5 * abs(sin(iTime)) - r;

    vec2 uv2 = vec2(uv.x + sin(iTime) * 0.1, uv.y + cos(iTime) * 0.1);
    float g = 0.5 * spiral(get_angle(uv2), length(uv2), 200. + 149. * sin(iTime), 20.);
    g = 2.5 * abs(sin(iTime + PI/4.)) - g;

    vec2 uv3 = vec2(uv.x + cos(iTime) * 0.2, uv.y + sin(iTime) * 0.2);
    float b = 0.5 * spiral(get_angle(uv3), length(uv3), 200. + 149. * sin(iTime), 20.);
    b = 2.5 * abs(sin(iTime + PI/2.)) - b;

    col = vec3(r, g, b);

    fragColor = vec4(col,1.0);
}

