const float PI = 3.14159;

vec2 rotate(vec2 p, float a)
{
    float sa = sin(a), ca = cos(a);
	return p * mat2(ca, -sa, sa, ca);
}

float pModPolar(inout vec2 p, float repetitions)
{
	float angle = 2.*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}

vec4 MellowPurple(vec2 pos)
{
    pos = abs(pos*4.0);			// Mirror, Zoom etc

    pos.y *= sin(pos.x);
    vec4 col = vec4(0.32,0.19,0.35,1.0);
    float tt = iTime*0.8;
    float iter = (pos.x*pos.y);
    iter *= 0.5+sin(pos.x*0.5+tt*0.95)+0.5;
    float r = sin(sin(tt)*pos.y);
    //r += .6;
    float d1 = sin(tt+pos.x+pos.y);
    float val = sin(tt+iter*PI );
    float brightness = 0.25 / abs( d1 * val - r);
    brightness = brightness/(brightness + 8.);
    col += brightness;
    return col;
}


