#define RADIUS 0.02

bool circle (vec2 uv, vec2 p, float radius)
{
    float dist = sqrt((uv.x - p.x)*(uv.x - p.x) + (uv.y - p.y)*(uv.y - p.y));
    if(dist<radius)
        return true;

    else return false;

}

float dist_to_line(vec2 p1, vec2 p2, vec2 uv)
{
	float a = abs(distance(p1, uv));
	float b = abs(distance(p2, uv));
	float c = abs(distance(p1, p2));

	float d = abs(sqrt(c*c + RADIUS*RADIUS));

	if (a >= d || b >= d)	{
        float dist1 = distance(p1, uv);
        float dist2 = distance(p2, uv);
        return min(dist1,dist2);
	}

	float p = (a + b + c) * 0.5;

	float h = 2.0 / c * sqrt( p * ( p - a) * ( p - b) * ( p - c));
    return h;
}

float falloff(float dist)
{
    if (dist*1.5 > RADIUS) {
        return 0.0;
    } else {
        return 1.;
    }
}

