    t = iTime;
    vec3	col = vec3(0., 0., 0.);
	vec2	R = iResolution.xy, uv  = (f-R*.5)/R.yy;
	vec3	dir = camera(uv);
    vec3	pos = vec3(-.0, .0, 2.-iTime);

