}

float	scene(vec3 p)
{
    float	mind = 1e5; 
    p.z -= iTime*-1.725;

    p.y += sin(iTime*-1.+p.z*.5)*.5;
    p.x += cos(iTime*-1.+p.z*.5)*.5;
    rotate(p.xy, p.z*.25 + 1.0*sin(p.z*.125 - iTime*0.5) + 0.*iTime);

    float	tube = max(-(length(p.yx)-2.), (length(p.yx)-8.));
    tube = max(tube, p.z-10.-0./length(p.yx*.06125) );
    tube = max(tube, -p.z-10.-0./length(p.yx*.06125) );
    vec3	pr = p;
    pr.xy = (fract(p.xy*.5)-.5)*1.;
    id = vec3(floor(p.xy*1.-.0), (floor(p.z*.5)-.0)*4.);

    pr.xy = abs(pr.xy)-.2505;
    #ifdef UNIFORM_ROTATION
    rotate(pr.xy, iTime);
    #else
    rotate(pr.xy, clamp( + (mod(id.x*.5, 2.)-1. <= 0. ? 1. : -1.) + (mod(id.y*.5, 2.)-1. <= 0. ? 1. : -1.), -2., 2.) * iTime*.5 );
    #endif
    pr.z = (fract(pr.z*.5)-.5)*4.;
	mind = mylength(vec2(mylength(pr.xyz)-.31, pr.z ))-.084;
    return(mind);
}


vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec4	step = vec4(0.0, 0.0, 0.0, 0.0);
	vec3	dirr;

    for (int i = -1; i < I_MAX; ++i)
    {
        dirr = dir;
    	rotate(dirr.xy, .51025*dist.y+iTime*-.0 );
    	p = pos + dirr * dist.y;
        dist.x = scene(p);
        dist.y += dist.x*.5;
        float	d = 5.;
        h -= vec3(.3, .2, .3)*.1/ (d+.0);
        h += (
            .001/(dist.x*dist.x+0.01) 
            -
            1./(dist.y*dist.y+40.)
             )
            *
            vec3
        (
    		1.+(sin(1.0*(id.y+id.x+id.z)+0.00) )
            ,
            1.+(sin(1.0*(id.x+id.y+id.z)+1.04) )
            ,
            1.+(sin(1.0*(id.z+id.x+id.z)+2.08) )
        )*.5;
        // log trick by aiekick
        if (log(dist.y*dist.y/dist.x/1e5)>0. || dist.x < E || dist.y >= FAR)
        {
            if (dist.x < E || log(dist.y*dist.y/dist.x/1e5)>0.)
	            step.y = 1.;
            break;
        }
        step.x++;
    }
    step.w = dist.y;
    return (step);
}

// Utilities

float	mylength(vec3 p)
{
	float	ret = 1e5;

    p = p*p;
    p = p*p;
    p = p*p;

    ret = p.x + p.y + p.z;
    ret = pow(ret, 1./8.);

    return ret;
}

float	mylength(vec2 p)
{
	float	ret = 1e5;

    p = p*p;
    p = p*p;
    p = p*p;

    ret = p.x + p.y;
    ret = pow(ret, 1./8.);

    return ret;
}

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}


vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

	return normalize(vec3(
           march(pos+eps.xyy, dir).w - march(pos-eps.xyy, dir).w,
           march(pos+eps.yxy, dir).w - march(pos-eps.yxy, dir).w,
           march(pos+eps.yyx, dir).w - march(pos-eps.yyx, dir).w ));
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

#define EPS 0.002
#define STEPS  64
#define FAR   10.
#define KIFS   30
// Uncomment for a different pattern
#define BOX

mat2 rot( float a )
{

	return mat2( cos( a ), -sin( a ),
		         sin( a ),  cos( a )
		   );

}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float map( vec3 p, out vec3 tra )
{

	mat3 rota = mat3( 1.0 );

    float wav = texture( iChannel0, vec2( 0.0, 0.10 ) ).x * 0.05;
    float fre = texture( iChannel0, vec2( 0.1, 0.20 ) ).x * 0.05;

    vec2 mou = iMouse.xy / iResolution.xy;

	for( int i = 0; i < KIFS; ++i )
	{

        if( mou.x == 0.0 ) mou.x = sin( iTime * 0.1 + wav ); // max( cos( iTime * 0.15 + wav ), 1e-5 );
        if( iMouse.z < 0.0 ) mou.x = sin( iTime * 0.1 + wav );

		p = abs( p * rota - vec3( 0.1, 0.0, 0.0 ) );
		p.xy = p.yx;        
		p.xz *= rot( 1.8 + mou.x + fre );
		p.yx *= rot( 1.8 + mou.y + wav );

        if( p.z < 0.1 )
        {

            tra = vec3( 2, 0, 0 ) * p.z * 2.0;

        }
        else if( p.z < 0.2 )
        {

            tra = mix( tra, vec3( 0.1, 0.6, 0.2 ) * p.z * 2.0, p.z );

        }
        else if( p.z < 0.3 )
        {

            tra = mix( tra, vec3( 0.1, 0.0, 0.2 ) * p.z * 2.0, p.z );

        }

	}

    #ifdef BOX

	return 1.0 - sdBox( p, vec3( 1.0 ) );

    #else

    return 1.0 - length( p );

    #endif

}

vec3 norm( vec3 p )
{

    vec3 tra = vec3( 0.0 );
	vec2 e = vec2( EPS, 0.0 );
	return normalize( vec3( map( p + e.xyy, tra ) - map( p - e.xyy, tra ),
			                map( p + e.yxy, tra ) - map( p - e.yxy, tra ),
			                map( p + e.yyx, tra ) - map( p - e.yyx, tra )
			              )
			         );

}

float ray( vec3 ro, vec3 rd, out float d )
{

	float t = 0.0; vec3 tra = vec3( 0.0 ); d = 0.0;

	for( int i = 0; i < STEPS; ++i )
	{

		d = 0.5 * map( ro + rd * t, tra );
		if( d < EPS || t > FAR ) break;

		t += d;

	}

	return t;

}

float softShadows( in vec3 ro, in vec3 rd )
{

    float res = 1.0; vec3 tra = vec3( 0.0 );
    for( float t = 0.1; t < 8.0; ++t )
    {

        float h = map( ro + rd * t, tra );

        if( h < 0.001 ) return 0.0;
        res = min( res, 14.0 * h / t );
        t += h;

    }

    return res;

}

vec3 shad( vec3 ro, vec3 rd )
{

	float d = 0.0, t = ray( ro, rd, d );
	vec3 p = ro + rd * t;
    vec3 tra = vec3( 0.0 );
    float ma = map( p, tra );
	vec3 n = norm( p );
	vec3 lig = normalize( vec3( sin( iTime * 0.2 ), cos( iTime * 0.2 ), 0.1 ) );
    vec3 blig = vec3( -lig );
	vec3 ref = reflect( rd, n );
	vec3 col = vec3( 0 );

	float amb = 0.5 + 0.5 * n.y;
	float dif = max( 0.0,  dot( lig, n ) );
    float bac = max( 0.0, 0.2 + 0.5 * dot( blig, n ) );
	float spe = pow( clamp( dot( ref, lig ), 0.0, 1.0 ), 16.0 );
    float speO = pow( clamp( dot( ref, blig ), 0.0, 1.0 ), 16.0 );
	float sha = softShadows( p, lig );
    float shaO = softShadows( p, blig );

    col = tra;
	col += 0.8 * amb;
	col += 0.2 * dif * bac * sha * shaO;
	col += 0.4 * spe;
    col += 0.4 * speO;

    col *= sqrt( col );

	return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )

{

    float wav = texture( iChannel0, vec2( 0.0, 0.10 ) ).x * 0.2;

	vec2 uv = ( -iResolution.xy + 2.0 * fragCoord.xy ) / iResolution.y;

	//vec3 ro = vec3( 0.0, 0.0, 3.0 * sin( iTime + wav ) );
    //vec3 ro = vec3( 0.0, 0.0, 2.6 );
	vec3 ro = vec3( sin( iTime * 0.2 ), -wav, cos( iTime * 0.2 ) );
    vec3 ww = normalize( vec3( 0 ) - ro );
	vec3 uu = normalize( cross( vec3( 0, 1, 0 ), ww ) );
	vec3 vv = normalize( cross( ww, uu ) );
	vec3 rd = normalize( uv.x * uu + uv.y * vv + 1.5 * ww );

	//vec3 rd = normalize( vec3( uv, -1.0 ) );

	float d = 0.0, t = ray( ro, rd, d );

	vec3 col = d < EPS ? shad( ro, rd ) : vec3( 0.0 );

	fragColor = vec4( vec3( col ), 1.0 );

}

float dtl(vec3 p, vec3 lo, vec3 ld, float l) {
    vec3 x = p - lo; // x's origin is now at lo
    float t = clamp(dot(ld, x), 0., l);
    return length(x - ld * t);
}

float get_bit_freq(float x) {
    float grid = floor(x * 32.)/32.;
    return .015 + 3./15.*(texture(iChannel0, vec2(grid, .25)).r*2.-1.);
}

float get_wave_sound(float x) {
    return texture(iChannel0, vec2(x, .75)).r*2.-1.;
}

float plane(vec3 p, vec3 pb1, vec3 pb2, vec3 pn, vec2 bound) {
    mat3 r3_to_plane_basis = transpose(mat3(pb1, pn, pb2));
    vec3 x = r3_to_plane_basis * p;
    x.y = abs(x.y);
    x.z -= get_bit_freq(x.x / bound.x*.5+.5);
    //x.z -= .1 + 4./15.*get_wave_sound(x.x / bound.x * .5 + .5);
    x.xz = max(abs(x.xz) - bound, vec2(0));
    return length(x);
}

float map(vec3 p) {
    //float d = plane(p, vec3(0,0,1), vec3(0,1,0), vec3(1,0,0), vec2(.5,.01));
    //*
	//or
	float d = length(vec2(p.y+.2*get_wave_sound(p.z+.5), p.x));
    if (abs(p.z)>.49)
        d = 1.;//*/

    p = abs(p);
    d = min(d, 1.3*dtl(p, vec3(.2,.2,.5), vec3(0,-1,0), .2));
    d = min(d, 1.3*dtl(p, vec3(.2,.2,.5), vec3(-1,0,0), .2));
    d = min(d, 1.3*dtl(p, vec3(.2,.2,.5), vec3(0,0,-1), .5));
    return d;
}

vec2 sphere(vec3 p, vec3 ro, vec3 rd, float r) {
    float a = dot(rd, rd);
    float b = 2.0 * dot(ro - p, rd);
    float c = dot(ro - p, ro - p) - r*r;
    float h = b*b - 4.0*a*c;
    if (h < 0.)
        discard;
    h = sqrt(h);
    return vec2(-b - h, -b + h);
}

#define iRes iResolution
void mainImage( out vec4 C, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iRes.xy * 2. - 1.;
    uv.x *= iRes.x / iRes.y;
    uv /= 2.;

    C = vec4(0);

    float away = 3.141592;
    float th = -iTime / 10. -iMouse.x/80.; float cs = cos(th); float sn = sin(th);
    vec3 ro = away*vec3(cs, .2, sn);
    vec3 ta = vec3(0);
    vec3 forward = normalize(ta - ro);
    vec3 up = vec3(0,1,0);
    vec3 right = vec3(-sn, 0., cs); // deriv of ro, which also is (-z,0,x)
    //vec3 right = normalize(cross(forward,up));
    vec3 rd = normalize(vec3(mat3(right, up, forward) * vec3(uv.x, uv.y, away)));

    vec2 bound = sphere(vec3(0), ro, rd, 3.);
    float t = bound.x;
    float dist;
    float csum = 0.;
    for (int i = 0; i < 128; ++i) {
        dist = map(ro+rd*t);
        csum += 1. / (dist*dist);
        t += dist*.2;
        if (t > bound.y)
            break;
    }
    C = vec4(.00001*csum);
}

#define PI 3.141592654
#define HASHSCALE1 .1031

const float EPS = 1e-2;
float OFFSET = EPS * 5.0;
float TIME;

vec3 hue(float hue) {
    vec3 rgb = fract(hue + vec3(0., 2. / 3., 1. / 3.));
    rgb = abs(rgb * 2. - 1.);
    return clamp(rgb * 3. - 1., 0., 1.);
}

vec3 hsvToRgb(vec3 hsv) {
    return ((hue(hsv.x) - 1.) * hsv.y + 1.) * hsv.z;
}

float hash11(float p) {
    vec3 p3 = fract(vec3(p) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float hash12(vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 rotate2d(vec2 p, float angle) {
    return p * mat2(cos(angle), -sin(angle),
        sin(angle), cos(angle));
}

vec3 rotateX(vec3 p, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return vec3(p.x, c * p.y + s * p.z, -s * p.y + c * p.z);
}

vec3 rotateY(vec3 p, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return vec3(c * p.x - s * p.z, p.y, s * p.x + c * p.z);
}

float qinticInOut(float t) {
    return t < 0.5 ?
        +16.0 * pow(t, 5.0) :
        -0.5 * pow(2.0 * t - 2.0, 5.0) + 1.0;
}

float sineInOut(float t) {
    return -0.5 * (cos(PI * t) - 1.0);
}

float box(vec2 p, float size) {
    p += 0.5;
    size = 0.5 + size * 0.5;
    p = step(p, vec2(size)) * step(1.0 - p, vec2(size));
    return p.x * p.y;
}

float postEffectPattern(vec2 p) {
    float t1 = fract(TIME);
    float e11 = qinticInOut(t1) - 0.5;

    float t2 = fract(TIME - 0.05);
    float e21 = qinticInOut(t2) - 0.5;
    float e22 = sineInOut(t2) - 0.5;

    float ofs = 0.6 + TIME * 0.2;
    float diff = 0.35;
    float scale = 1.3;

    float index = 2.;
    index = mix(index, 5., box(rotate2d(p, PI * e11 + ofs + diff * 1.), scale * 3.6 * abs(e11)));
    index = mix(index, 4., box(rotate2d(p, PI * e11 + ofs + diff * 2.), scale * 3.6 * abs(e11)));
    index = mix(index, 1., box(rotate2d(p, PI * e21 + ofs + diff * 3. + 0.2), scale * 3.6 * abs(e21)));
    index = mix(index, 0., box(rotate2d(p, PI * e21 + ofs + diff * 3. + 0.2), scale * 3.0 * abs(e21)));
    index = mix(3., index, box(rotate2d(p, PI * e22 + ofs + diff * 0.), 20. * abs(e22)));

    return index;
}

float sdHexPrism(vec3 p, vec2 h) {
    vec3 q = abs(p);
    return max(q.z - h.y, max((q.x * 0.866025 + q.y * 0.5), q.y) - h.x);
}

float sdPlane(vec3 p) {
    return p.y + 5.;
}

float udBox(vec3 p, vec3 b, float r) {
    return length(max(abs(p) - b, 0.0)) - r;
}

float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

float dGlass(vec3 p) {
    float it = mod(floor(TIME - 0.5), 4.);

    if (it == 0.)
        return sdSphere(p, .6);
    if (it == 1.)
        return sdTorus(rotateX(p, PI / 2.), vec2(.5, .2));
    if (it == 2.)
        return sdHexPrism(p, vec2(0.45));
    if (it == 3.)
        return udBox(rotateY(rotateX(p, PI / 4.), PI / 4.), vec3(0.4), 0.05);
}

float map(vec3 p) {
    float b = sdPlane(p);
    float c = dGlass(p);
    return min(b, c);
}

vec3 floorPattern(vec2 p) {
    return vec3(0.2) * mod(floor(p.x * 0.3) + floor(p.y * 0.3), 2.0);
}

vec2 intersect(vec3 ro, vec3 ray) {
    float t = 0.0;
    for (int i = 0; i < 256; i++) {
        float res = abs(map(ro + ray * t));
        if (res < 0.005) return vec2(t, res);
        t += res;
    }

    return vec2(-1.0);
}

vec3 normal(vec3 pos, float e) {
    vec3 eps = vec3(e, 0.0, 0.0);

    return normalize(vec3(
        map(pos + eps.xyy) - map(pos - eps.xyy),
        map(pos + eps.yxy) - map(pos - eps.yxy),
        map(pos + eps.yyx) - map(pos - eps.yyx)));
}

mat3 createCamera(vec3 ro, vec3 ta, float cr) {
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.0);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

vec3 renderScene(vec2 p) {
    float t1 = fract(TIME);
    float e1 = qinticInOut(t1) - 0.5;
    float e2 = sineInOut(t1) - 0.5;
    float ofs = 0.6 + TIME * 0.2;
    float t2 = abs(1. - e2 - ofs) * PI;

    vec3 ro = vec3(cos(t2) * 24. * e1, 2., sin(t2) * 24. * e1);
    vec3 ta = vec3(0);
    mat3 cm = createCamera(ro, ta, 0.);
    vec3 ray = cm * normalize(vec3(p, 10.0));

    vec3 fresnel = vec3(0);

    for (int i = 0; i < 6; i++) {
        vec2 res = intersect(ro, ray);

        if (res.y <= -0.5) {
            return vec3(1);
        }

        vec3 pos = ro + ray * res.x;
        vec3 nor = normal(pos, 0.008);

        if (dGlass(pos) > 0.005) {
            vec3 col = vec3(0);
            col += floorPattern(pos.xz);
            col += fresnel;
            return col + vec3(0.001, 0.002, 0.004) * res.x * 3.;
        }

        if (i == 0 && dot(-ray, nor) < 0.5) {
            float a = 1. - dot(-ray, nor) * 2.;
            fresnel = mix(fresnel, vec3(0., 0.8, 0.8), a);
        }

        float eta = 1.1;

        bool into = dot(-ray, nor) > 0.0;
        nor = into ? nor : -nor;
        eta = into ? 1.0 / eta : eta;

        ro = pos - nor * OFFSET;
        ray = refract(ray, nor, eta);

        if (ray == vec3(0.0)) {
            ro = pos + nor * OFFSET;
            ray = reflect(ray, nor);
        }
    }
}

vec3 render(vec2 p) {
    vec3 col = renderScene(p);
    float effect = postEffectPattern(p);
    float hue = hash11(floor(TIME + 0.5));

    if (effect == 0.)
        return col;
    if (effect == 1.)
        return hsvToRgb(vec3(hue + p.x * .08, 1, 1));
    if (effect == 2.)
        return mix(col, hsvToRgb(vec3(hue - 0.2 - p.y * .08, 1, 1)), 0.7);
    if (effect == 3.)
        return vec3(1.);

    return mix(col, hsvToRgb(vec3(hue + (effect - 4.) * 0.1, 1, 1)), 0.7);
}

vec3 aaRender(vec2 p) {
    vec3 col = vec3(0.0);
    const int num = 4;

    for (int i = 0; i < num; i++) {
        float fi = float(i + 1);
        col += render(p + vec2(step(fi, 2.001), mod(fi, 2.001)) * 0.0015);
    }

    return col / float(num);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float timeScale = 0.7;
    TIME = iTime * timeScale + 10.;

    vec3 col = aaRender(uv);
    fragColor = vec4(pow(col, vec3(1.0 / 2.2)), 1.0);
}

