void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 40. + 4. * cos(iTime);
    uv += vec2(6. + cos(iTime / 9.) * 6., 2.);

    vec2 uvCore = uv;

    uv *= 1. / (1. + pow(length(uvCore), 2.) * 0.2);

    uv += 1. + cos(iTime / 10.) * 0.5;

    //uv *= cos(uv.yx);
    uv += cos(uv.yx * 2.) * 0.5;
    //uv *= cos(uv.yx);
    uv += cos(uv.yx * 5.) * 0.25;
    //uv *= cos(uv.yx);
    uv += cos(uv.yx * 20.) * 0.125;

    float iTimeSlow = iTime * 2.;
    uv *= cos(uv.yx * (iMouse.xy / iResolution.xy - vec2(0.5)) * 1. + iTime);
    uv *= cos(uv.yx + iTimeSlow) * 5.;
    uv *= cos(uv.yx + iTimeSlow);
    uv *= cos(uv.yx + iTimeSlow);
    uv *= cos(uv.yx * 4. + iTimeSlow);

    vec3 col1 = 1. - ((1. + cos(uv)) / 2.).xyy;

    vec3 col = col1;//mix(bg, col1, 1. / (1. + pow(length(uvCore), 2.)) * 1.3); // * 1. / (1. + pow(length(uvCore), 2.) * 0.1);

    // Output to screen
    fragColor = vec4(col,1.0);
}

/*

The thought struck that HDR could be simulated on an 8 bit panel by dithering
at a higher resolution than the source. Sadly it's not an original thought :(

e.g. http://sandervanrossen.blogspot.com/2012/02/hdr-dithering.html

The lower half of the screen is using default quantisation, the upper half
has [0:1/256] noise added to it to eliminate the banding.

Noise function from:

https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl

changed slightly to do a divide by 256 in the bitmask

*/

/*
    static.frag
    by Spatial
    05 July 2013
*/

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}

// Compound versions of the hashing algorithm I whipped together.
uint hash( uvec2 v ) { return hash( v.x ^ hash(v.y)                         ); }
uint hash( uvec3 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z)             ); }
uint hash( uvec4 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z) ^ hash(v.w) ); }

// Construct a float with half-open range [0:1] using low 23 bits.
// All zeroes yields 0.0, all ones yields the next smallest representable value below 1.0.
float floatConstruct( uint m ) {
    // const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint ieeeMantissa = 0x00007FFFu;
    const uint ieeeOne      = 0x3F800000u; // 1.0 in IEEE binary32

    m &= ieeeMantissa;                     // Keep only mantissa bits (fractional part)
    m |= ieeeOne;                          // Add fractional part to 1.0

    float  f = uintBitsToFloat( m );       // Range [1:2]
    return f - 1.0;                        // Range [0:1]
}

// Pseudo-random value in half-open range [0:1].
// NL because of >>8 mantissa returns in range [0:1/256] which is perfect for quantising
float random( float x ) { return floatConstruct(hash(floatBitsToUint(x))); }
float random( vec2  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float random( vec3  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float random( vec4  v ) { return floatConstruct(hash(floatBitsToUint(v))); }

/* stuff by nomadic lizard */

vec3 quantise(in vec3 fragColor, in vec2 fragCoord)
{
    return fragColor + random(vec3(fragCoord, iTime));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 posn = fragCoord/iResolution.xy;
    vec2 uv = posn / 5.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xxx);

    // Quantise with random when in top half of screen
    if(posn.y > 0.5) {
    	col = quantise(col, fragCoord);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

