float createCircle(vec2 uv, vec2 pos, float r){
    float d = length(pos - uv);

    // smoothstep to correct aliasing
    return smoothstep(r, r-d*0.05, d);
}

float createRect(vec2 uv, vec2 pos, float w, float h){
    uv -= pos;
    float left = -w*0.5;
    float right = w*0.5;
    float bottom = -h*0.5;
    float top = h*0.5;
    float blur = 0.01;
    float leftEdge = smoothstep(left-blur, left, uv.x);
    float rightEdge = smoothstep(right+blur, right, uv.x);
    float bottomEdge = smoothstep(bottom-blur, bottom, uv.y);
    float topEdge = smoothstep(top+blur, top, uv.y);

    return leftEdge * rightEdge * bottomEdge * topEdge;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // remapping coordinates from (0 <> 1) to (-0.5 <> -0.5)
    uv -= .5;

    // adjusting screen ratio to avoid distorted image
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    // circle 1
    vec3 color1 = vec3(sin(iTime*0.1)*0.5+0.5, sin(iTime*0.8)*0.5+0.5, sin(iTime*1.6)*0.5+0.5);
    vec2 pos1 = vec2(sin(iTime * 2.) * 0.3, sin(iTime) * 0.3);
    float radius1 = 0.1;
    vec3 circle1 = createCircle(uv, pos1, radius1) * color1;


    // circle 2
    vec3 color2 = vec3(sin(iTime*0.7)*0.5+0.5, sin(iTime*1.2)*0.5+0.5, sin(iTime*1.8)*0.5+0.5);
    vec2 pos2 = vec2(sin(iTime * 0.8) * 0.3, sin(iTime*1.5) * 0.3);
    float radius2 = 0.15;
    vec3 circle2 = createCircle(uv, pos2, radius2) * color2;

    // rect 1
    vec3 color3 = vec3(sin(iTime*0.2)*0.5+0.5, sin(iTime*1.9)*0.5+0.5, sin(iTime*0.8)*0.5+0.5);
    vec2 pos3 = vec2(sin(iTime * 0.8) * 0.3, sin(iTime*0.4) * 0.25);
    vec3 rect1 = createRect(uv, pos3, 0.15, 0.15) * color3;

    // rect 2
    vec3 color4 = vec3(sin(-iTime*2.)*0.5+0.5, sin(iTime*-1.4)*0.5+0.5, sin(iTime*0.8)*0.5+0.5);
    vec2 pos4 = vec2(sin(iTime * -0.6) * 0.3, sin(iTime*-0.9) * 0.25);
    vec3 rect2 = createRect(uv, pos4, 0.1, 0.2) * color4;

    // combining shapes
    vec3 c = circle1 + circle2 + rect1 + rect2;

    // Output to screen
    fragColor = vec4(c,1.0);
}

float map(vec3 p, float size) {
    return length(fract(p) * 2. - 1.) - size ;
}

float trace(vec3 ro, vec3 rd, float size) {
	float t = 0.;

    for(int i = 0; i < 32; ++i)
        t += map(ro + rd * t, size) / 2.;

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
	uv = (uv * 2. - 1.) * vec2(iResolution.x / iResolution.y, 1.);

    vec2 m = (1. - iMouse.xy / iResolution.xy) * (iResolution.x / iResolution.y);

    vec3 rd = normalize(vec3(uv, m));
    float t = trace(vec3(0., 0., iTime), rd, fract(abs(uv.x) / 2.));

    fragColor = vec4(1. / ( 1. + t * t * 0.1));
}

// Code by Flopine
// Thanks to wsmind, leon, lsdlive, lamogui, XT95 and Coyhot for teaching me <3
// cookie collective rulz!

#define ITER (64.)
#define PI (3.141592)
#define time (iTime*0.5)


mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

vec2 moda (vec2 p, float per)
{
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a-per/2.,per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5 *(u+a+abs(mod(u-a+st,2.*st)-st)));
}

vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b*cos(2.*PI*(c*t+d));}

float odile (vec3 p, float d)
{return dot(p, normalize(sign(p)))-d;}

float torus (vec3 p, vec2 d)
{
    vec2 q = vec2(length(p.xz)-d.x,p.y);
  	return length(q)-d.y;
}

float prim1 (vec3 p)
{
    float steps = 3.;
	float t1 = torus(p, vec2(1.5,.15));
    float od = odile(p,.8);
    p.yz *= rot(PI/2.);
    float t2 = torus(p,vec2(1.6,0.15));
    return stmin(od,stmin(t1,t2, 0.5, steps),0.5,steps);
}

float fractal(vec3 p)
{
    float prim = prim1(p);
    float prout = .3;
    for (int i = 0; i<5; i++)
    {
        prout += .4;
        p.xz *= rot(sin(iTime*prout));
        p.xy = moda(p.xy, 2.*PI/5.);
        p.x -= 4.;
      	prim = smin(prim, prim1(vec3(p.x, p.yz*rot(PI/4.))), 2.);  
    }
    return prim;
}


vec4 SDF(vec3 p)
{
  	p.xz *= rot(time);
    p.yz *= rot(time);
    p.y += sin(p.x+iTime*3.)*length(p)*0.1;
    float d = fractal(p);
    return vec4(d, p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.001,0.001,-15.); vec3 p = ro;
    vec3 dir = normalize(vec3(uv,1));

    float shad = 0.;
    float t = 0.;
    float tmax = 100.;
    vec3 c = vec3(0.);

    for (float i = 0.; i<ITER; i++)
    {
        float d = SDF(p).x;
        t += d;
        if (d<0.001 || t>tmax)
        {
            shad = i/ITER;
            break;
        }
        p+= d*dir*0.8;
    }                 
    vec3 pal = palette (length(SDF(p).yzw),
                        vec3(0.2,0.6,0.3),
                        vec3(0.5,0.1,0.5),
                        vec3(.08),
                        vec3(0.,0.5,0.1));
    if (t<tmax) c = vec3(1.-shad)*pal*1.5;
	c = mix(c, vec3(0.5,0.4,0.2)*0.35, 1.-(exp(-.002*t*t)));

    // Output to screen
    fragColor = vec4(c,1.0);
}

mat2 r2d (in float degree)
{
    float rad = radians (degree);
    float c = cos (rad);
    float s = sin (rad);
	return mat2 (c, s, -s, c);
}

vec2 cart2polar (in vec2 cart)
{
    float r = length (cart);
    float phi = atan (cart.x, cart.y);
    return vec2 (phi, r);
}

vec2 polar2cart (in vec2 polar)
{
    float x = polar.x*cos (polar.y);
    float y = polar.x*sin (polar.y);
    return vec2 (y, x);
}

float graph (in vec2 p)
{
    vec2 polar = cart2polar (p);
    polar.y = abs (4.*sin (.75*polar.y) * cos (2.*polar.x));
    p = polar2cart (polar);

	return length (p.x / p.y);
}

vec2 gradient (in vec2 p)
{
    //return vec2 (dFdx (graph (p)), dFdy (graph (p))) / fwidth (graph (p));
    vec2 e = vec2 (.001, .0);
    return vec2 (graph (p - e.xy) - graph (p + e.xy),
                 graph (p - e.yx) - graph (p + e.yx)) / (.75*e.x);
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec2 p = (5. + 10.*(.5 + .5*cos(3.*iTime)))*uv;
    p *= r2d (34.*iTime);
    p += vec2 (3.*cos (iTime), 3.*sin (iTime));
    p.x += .5*cos (p.y + 2.*iTime);
    p.y += .5*sin (p.x + 4.*iTime);

    float d = abs (graph (p)) / length (gradient (p));
    float thickness = 20. / iResolution.y;
    vec3 col = vec3 (smoothstep (1.*thickness, 2.*thickness, d));

	vec3 c = vec3 (1., .95, .9);
	c *= .8 + .2*smoothstep (.0, 25.*(2./iResolution.y), d);
	col = mix (c, col, 1. - smoothstep (.0, 12.*(1./iResolution.y), d));

    col *= 1. - .4*length (fragCoord/iResolution.xy*2. - 1.);
    col = col / (1. + col);
    col = pow (col, vec3 (1./2.2));
    fragColor = vec4 (col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float grey = 1.0;    
    vec3 img = texture(iChannel1, uv).rgb;

    vec2 uv0 = (uv*2.0 - 1.0) * 0.5;
    uv0.x = 1.0-abs(uv0.x);
    float length0 = sqrt(dot(uv0, uv0));
    float time = fract(iTime / 2.0) * 5.0;
    float mask = 1.0 - (length0 * 5.0 - (time*(grey*0.5+0.5)));

    // background	 
	vec3 color = vec3(1.0);

    // bubbles	
    uv *= 2.0;
	for( int i=0; i<40; i++ )
	{
        // bubble seeds
		float pha =      sin(float(i)*546.13+1.0)*0.5 + 0.5;
		float siz = pow( sin(float(i)*651.74+5.0)*0.5 + 0.5, 4.0 );
		float pox =      sin(float(i)*321.55+4.1) * iResolution.x / iResolution.y;

        // buble size, position and color
		float rad = 0.1 + 0.1*siz;
		vec2  pos = vec2( pox, -1.0-rad + (2.0+2.0*rad)*mod(pha+0.1*iTime*(0.2+0.8*siz),1.0));
		float dis = length( uv - pos );
		vec3  col = 3.0 * mix( vec3(0.94,0.3,0.0), vec3(0.1,0.4,0.8), 0.5+0.5*sin(float(i)*1.2+1.9));
		//    col+= 8.0*smoothstep( rad*0.95, rad, dis );

        // render
		float f = length(uv-pos)/rad;
		f = sqrt(clamp(1.0-f*f,0.0,1.0));
		color -= col.zyx *(1.0-smoothstep( rad*0.95, rad, dis )) * f;
	}
    mask += 1.0 - dot(color,vec3(1.0/3.0));

    // Output to screen
    fragColor.rgb = (1.0-mask) * img + mask*vec3(1.0,1.0,1.0);
    fragColor.a = 1.0;
}

