// changes: 
// - optimized three-zero case
// - removed incompatible sinh(.) and tanh(.) calls
// - added abs to specular (fixes smartphone bug)

const vec3 c = vec3(1.,0.,-1.);
const float pi = acos(-1.);

//hash function
float rand(vec2 a0)
{
    return fract(sin(dot(a0.xy ,vec2(12.9898,78.233)))*43758.5453);
}

//distance to spline with parameter t
float dist(vec3 p0,vec3 p1,vec3 p2,vec3 x,float t)
{
    t = clamp(t, 0., 1.);
    return length(x-pow(1.-t,2.)*p0-2.*(1.-t)*t*p1-t*t*p2);
}

//minimum distance to spline
float dsp(vec3 p0, vec3 p1, vec3 p2, vec3 x)
{
    //coefficients for 0 = t^3 + a * t^2 + b * t + c
    vec3 E = x-p0, F = p2-2.*p1+p0, G = p1-p0,
    	ai = vec3(3.*dot(G,F), 2.*dot(G,G)-dot(E,F), -dot(E,G))/dot(F,F);

	//discriminant and helpers
    float tau = ai.x/3., p = ai.y-tau*ai.x, q = - tau*(tau*tau+p)+ai.z, dis = q*q/4.+p*p*p/27.;

    //triple real root
    if(dis > 0.) 
    {
        vec2 ki = -.5*q*c.xx+sqrt(dis)*c.xz, ui = sign(ki)*pow(abs(ki), c.xx/3.);
        return dist(p0,p1,p2,x,ui.x+ui.y-tau);
    }

    //three distinct real roots
    float fac = sqrt(-4./3.*p), arg = acos(-.5*q*sqrt(-27./p/p/p))/3.;
    vec3 t = c.zxz*fac*cos(arg*c.xxx+c*pi/3.)-tau;
    return min(
        dist(p0,p1,p2,x, t.x),
        min(
            dist(p0,p1,p2,x,t.y),
            dist(p0,p1,p2,x,t.z)
        )
    );
}

mat3 rot(vec3 p)
{
    vec3 cp = cos(p), sp = sin(p);
    mat3 m = mat3(cp.y*cp.x, cp.x*sp.z+cp.z*sp.x*sp.y, sp.x*sp.z-cp.x*cp.z*sp.y, 
           -cp.y*sp.z, cp.x*cp.z-sp.x*sp.y*sp.z, cp.z*sp.x+cp.x*sp.y*sp.z, 
           sp.y, -cp.y*sp.x, cp.x*cp.y);
    return m;
}

vec2 scene(vec3 x)
{
    vec3 p1 = -sin(iTime)*.3*c.xxx, 
        p2 = .4*rot(vec3(1.1,1.2,1.3)*iTime)*c.xxy,
        p3 = sin(iTime)*.2*c.xxx;
    return vec2(dsp(p1,p2,p3,x)-.06, 216.);
}

vec3 normal(vec3 x)
{
    float s = scene(x).x, dx=1.e-4;
    return normalize(vec3(scene(x+dx*c.xyy).x-s, scene(x+dx*c.yxy).x-s, scene(x+dx*c.yyx).x-s));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //camera setup
    vec2 uv = fragCoord/iResolution.xy-.5, s = c.xy;
	vec3 o = c.yyx+.3*vec3(cos(.5*iTime), sin(.5*iTime), 0.), rt = uv.x*c.xyy+uv.y*c.yxy, d = normalize(rt-o), x = c.yyy, col=c.yyy;
	float di = 0.;

    //raymarching
    for(int i=0; i<100; ++i)
    {
        x = o + di*d;
        s = scene(x);
        if(s.x < 1.e-4)break;
        if(i==99)
        {
            fragColor = vec4(mix(.2*c.xxx, .5*c.xxx, round(1.*(.5+.5*sin(6.*atan(uv.y/uv.x)+3.e-1*iTime)))/1.), 1.);
            return;
        }
        di += s.x;
    }

    //colorize
    vec3 n = normal(x), l = 2.*c.yyx, v = normalize(x-o), r = normalize(reflect(-l,n));

    col = .4*abs((rot(vec3(1.3, 1.45, 1.56)*(iTime+12.4*s.y))*.3*c.xyy))
        + .4*abs((rot(vec3(1.3, 1.45, 1.56)*(iTime+3.6*s.y))*.4*c.xyy*dot(l, n)))
        + abs((rot(vec3(1.3, 1.45, 1.56)*(iTime+15.1*s.y))*2.5*c.xxy*pow(abs(dot(r,v)), 5.)));
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 f )
{
    vec2 uv = f/iResolution.y-0.5;
    fragColor = vec4(sin(atan(uv.y, uv.x)*iTime*10.0));
}

