    // Kind of fire flame effect
    //c.rgb = 1.*abs(m(o+d)*vec3(.3,.15,.1)+m(o*.5)*vec3(.1,.05,0))*(12.-o.x/2.);
}

// fuck your digits
#define pi (acos(-1.))

float noise(vec2 uv)
{
  return fract(sin(dot(uv, vec2(12.43243,4.543))*46573.71254));
}

float mlength(vec2 a)
{
  a=abs(a);
  return max(a.x,a.y);
}

vec2 R(vec2 a, float b)
{
  float c =cos(b);
  float s =sin(b);
  return vec2(
    a.x*c-a.y*s,
    a.x*s+a.y*c
  );
}

// :)
float IKEDA(vec2 uv,float th)
{
  th = min(.5,th);
  uv = R(uv,sin(floor(iTime*2.)));

  //return mlength(uv)<4. ? 1. : 0.;

  uv.x *= 15.;


  float sc = mix(.4,.1,noise(vec2(0,floor(uv.y))));
  float sp = noise(vec2(1,floor(uv.y)))+2.;
  uv.x+=sp*iTime*5.;
  uv.x*=sc;
  float n = noise(floor(uv));
  n = fract(n+iTime*sp);
  uv.x += iTime*10.;
  return n>th&&fract(uv.y)>.35 && fract(uv.x*.5) < .8 ?1.:0.;
}

vec4 plas( vec2 v, float time )
{
  float c = 0.5 + sin( v.x * 10.0 ) + cos( sin( time + v.y ) * 20.0 );
  return vec4( sin(c * 0.2 + cos(time)), c * 0.15, cos( c * 0.1 + time / .4 ) * .25, 1.0 );
}

vec4 pal(vec4 a, vec4 b, vec4 c, vec4 d, float t)
{
  return a+b*cos(2.*pi*(c*t+d));
}

// <3
vec4 logichroma(float t)
{
  return pal(
    vec4(.5),
    vec4(.5),
    vec4(1.),
    vec4(0,.33,.67,1),
    t
  );
}

vec3 SG(vec2 uv)
{
  return vec3(
    (2.*uv) /  (1.+dot(uv,uv)),
    (-1.+dot(uv,uv)) /  (1.+dot(uv,uv))
  );
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
  vec2 suv = fragCoord.xy / iResolution.xy;
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  float v = (1.-length(uv))*3.;
  v=clamp(v,0.,1.);

  vec3 s = SG(uv*3.);
  s.xz = R(s.xz,iTime*1.9);
  float m = s.x*s.z > 0. ? 1. : 0.;

  float f = texture(iChannel0, vec2(0.005,0)).r*.5;


  m = s.x*40.+.5 + f*4.;
  m = clamp(m,0.,1.);


  uv *= 1.-f*.8;
  out_color=vec4(.0);
  for(int i=0;i<30;++i)
  {
    out_color += vec4(IKEDA(uv*12.,.2+f*.5)) * logichroma(1.-float(i)/30.) * (1.-(float(i)/30.));
    uv *= pow(0.992,1.-f);
    uv.x+=sin(iTime)*.002;
    uv.y+=cos(iTime)*.002;
  }
  out_color *= .1;
  out_color = mix(out_color, 1.-out_color, m);
  out_color *= v;
}

