	return min(min(v.x, v.y), v.z);
}
float max3(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

vec2 rotate2d(vec2 v, float a) {
	float sinA = sin(a);
	float cosA = cos(a);
	return vec2(v.x * cosA - v.y * sinA, v.y * cosA + v.x * sinA);	
}

float sdSphere(vec3 p, float d) { return length(p) - d; } 

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float map(vec3 p) {
    //return min(max(-sdSphere(p, 7.5 * scale), sdBox(p, vec3(6.0) * scale)), -sdSphere(p, 25.0 * scale));
    return max(-sdSphere(p, 7.5), sdBox(p, vec3(6.0)));
}



float hitBox(vec3 ro, vec3 rd, vec3 box0, vec3 box1, float minMax) {
	vec3 pos0 = (box0 - ro) / rd;
	vec3 pos1 = (box1 - ro) / rd;
	float tmin = max3(min(pos0, pos1));
	float tmax = min3(max(pos0, pos1));

    float t = (bool(minMax))? tmax : tmin;
	return (tmin < tmax && tmax > 0.0)? t : -1.0;
}


float cast_ray( in vec3 ro, in vec3 rd, float t, float tmin,float tmax)
{
	float precis = 0.005;
    float delta = 0.05;
    int steps = int((tmax - tmin) / delta);
    for( int i=0; i < steps; i++ )
    {
	    float tempT = map( ro+rd*t);
        if( tempT<precis || t>tmax ) break;
        t += delta;
    }

	if(t + delta*1.5 > tmax)t = -1.0;

    return t;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	vec3 cameraDir = vec3(0.0, 0.0, 0.8);
	vec3 cameraPlaneU = vec3(1.0, 0.0, 0.0);
	vec3 cameraPlaneV = vec3(0.0, 1.0, 0.0) * iResolution.y / iResolution.x;
	vec3 rd = cameraDir + screenPos.x * cameraPlaneU + screenPos.y * cameraPlaneV;
	vec3 ro = vec3(0.0, 2.0 * sin(iTime), -25.0);
	ro.xz = rotate2d(ro.xz, iTime*0.1);
	rd.xz = rotate2d(rd.xz, iTime*0.1);


    float tEntry = hitBox(ro,rd,vec3(-6.0), vec3(6.0), 0.0);
    float tExit = hitBox(ro,rd,vec3(-6.0), vec3(6.0), 1.0);
    float dt = tExit - tEntry;

    float t = -1.0;
    vec3 col = rd;

    if(tEntry > 0.0)
        t = cast_ray(ro,rd,tEntry,tEntry,tExit);


    col = vec3(t / 64.0);




	fragColor = vec4(col, 1.0);
}

// Use camera as background instead of generated stars?
#define ENABLE_TEXTURE_BG false
#define NB_CATS 10
#define NB_STARS 10
#define NB_RAINBOW_COLS 6

const float PI = 3.1415926535897932384626433832795;

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

#define STAR_W 7
#define STAR_H 7
#define STAR_NB_FRAMES 4

bool bounding_box_check(vec2 uv, vec2 top, vec2 bottom)
{
    return all(lessThan(top, uv)) && all(lessThan(uv, bottom));
}

vec4 nyancat_rainbow(in vec2 uv, in float factor)
{
    const vec3 colors[] = vec3[](
        vec3(0.5, 0.0, 1.0),
        vec3(0.0, 0.7, 1.0),
        vec3(0.0, 1.0, 0.0),
        vec3(1.0, 1.0, 0.0),
        vec3(1.0, 0.5, 0.0),
        vec3(1.0, 0.0, 0.0)
    );
    const float start = 0.21;
    const float size = 0.57;
    const float ssize = size / float(NB_RAINBOW_COLS);
    const float shift = 0.014;
    // TODO replace this with parameter, set properly from
    // caller
    const float rear = .25;
    float pos = start;
    vec4 col;

#define RAINBOW_LENGTH 4.    
    // Early return if bounding box check fails
    if (!bounding_box_check(uv, vec2(-RAINBOW_LENGTH, start),
                                vec2(rear, start + size + shift)))
        return vec4(0.);

    if (int(mod(uv.x * 6.0 + round(iTime * 3.0), 2.0)) == 1)
        pos += shift;

    for (int i = 0; i < NB_RAINBOW_COLS; i++) {
	    float c;
        float end = pos + ssize;
	    c = 1.0 - step(uv.y, pos);
	    c *= step(uv.y, end);
	    c *= step(uv.x, rear);    
	    col.rgb += colors[i] * c;
        col.a += c;
        pos = end;
    }

    // Make fade out vary with time.
    float rstart = -RAINBOW_LENGTH - abs(sin(iTime * 3.0) / 4.);
    col.a = col.a != 0. ?
        smoothstep(rstart, rear, uv.x - rear) : 
    	0.0;

    return vec4(col);
}

vec4 nyancat(in vec2 uv, in float factor, in float angle)
{
	int frame = int(floor(mod((iTime + factor) * 13.0, 6.0)));

    vec4 c = nyancat_rainbow(uv + 0.5, factor);

    uv *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));    
    uv += 0.5;

    uv = clamp(uv, 0.0, 1.0);

    // Return early is bounding box test fails
    if (!bounding_box_check(uv, vec2(0., 0.), vec2(1., 1.)))
        return c;

    // Scale to 1 frame
    uv.x *= 40.0 / 256.0;
    // Choose frame
    uv.x += float(frame) * (40.0 / 256.0);

    vec4 t = texture(iChannel0, uv);

    return vec4(mix(c.rgb, t.rgb, t.a), clamp(c.a + t.a, 0.0, 1.0));
}

// TODO return Z as well so we know how to mix colors
vec4 nyancat_moving(in vec2 uv, in float factor)
{
	const float rotCenter = 0.5;
    // Rotation center
    uv -= rotCenter;
    // Fix aspect ration
    uv.x /= iResolution.y / iResolution.x;

    // Initial position
    uv += vec2(-.6 + rand(vec2(factor * 1.5, factor)), -.5 + rand(vec2(factor * 2.3, factor)));

    // Translate
    //uv -= vec2(sin(iTime * factor / 2.0) / 2.0, sin(iTime / 3.3 * factor) / 3.0);

    uv += vec2(sin(iTime + factor) / (factor + 1.0),
               sin(iTime + factor) / (factor + 10.0));

    // Scale
    float scalefactor = ((sin(iTime) / 2.0) + factor + 1.7);
    uv *= mat2(scalefactor, 0.0, 0.0, scalefactor);

    // Rotate
    const float fac = 15.0;
    const float speed = 7.0;
    float angle = sin(iTime * speed) / factor / fac;
    angle = abs(angle);

    return nyancat(uv, factor, angle);
}

vec4 gen_bg(in vec2 uv)
{
    vec2 bguv = uv / iResolution.xy;
    if (!ENABLE_TEXTURE_BG) {
	    float scalefactor2 = (sin(iTime) / 5.0) + 1.0;
   		bguv *= mat2(scalefactor2, 0.0, 0.0, scalefactor2);
    	bguv.x += iTime / 1.2;
    }

    return texture(iChannel1, bguv);
}

// Star is drawn from (-.5, -.5) to (.5, .5)
vec4 gen_star(in vec2 uv, float factor)
{
    const float xstep = 1.0 / float(STAR_W);
    const float ystep = 1.0 / float(STAR_H);

    // Move to center
    uv -= .5;

    // Fix aspect ratio
    uv.x /= iResolution.y / iResolution.x;

#define SCALE_DOWN 10.
    // Scale down
    // TODO vary size and speed down as star is further away?
    uv *= SCALE_DOWN;

    // Randomize y and starting x according to factor
    // TODO also randomize y according to iTime modulo width of scene
    uv.y += ((factor * SCALE_DOWN) - (SCALE_DOWN / 2.));
    uv.x += ((rand(vec2(factor)) * SCALE_DOWN) - (SCALE_DOWN / 2.));

    // Scroll x according to time and speed
    // TODO aspect ratio not taken into account?
    float speed = SCALE_DOWN / 1.2;
    uv.x = -(SCALE_DOWN / 2.) + mod(uv.x + iTime * speed, SCALE_DOWN);

    // Return early if bounding box test fails
    if (!bounding_box_check(uv, vec2(-.5, -.5), vec2(.5, .5)))
        return vec4(0.);

    int frame = int(mod((iTime + factor * PI * 10.) * 6.,
                        float(STAR_NB_FRAMES) + 10.));

    // Early return if end of animation
    if (frame >= STAR_NB_FRAMES)
        return vec4(0.);

    // DEBUG: is the correct frame selected?
    // return vec4(vec3(float(frame) / float(STAR_NB_FRAMES)), 1.);

    // Find the pixel coordinate of the star 
    int py = int(floor((uv.y + .5) * float(STAR_H)));
    int px = int(floor((uv.x + .5) * float(STAR_W)));

    // Mirror x and y to get the correct pixel data
    py = abs(py - 3);
    px = abs(px - 3);

    vec4 p1 = vec4(1.);
    vec4 p0 = vec4(0.);

    // Early filter of most transparent pixels
	if (frame != 3 && (px != 0 && py !=0))
        return p0;

    // For frame 0, the second pixel on each axis is set
    if (frame == 0)
        return (px | py) == 1 ? p1 : p0;

    // For frame 1, the second and third pixel on each axis is set
    if (frame == 1) {
        px |= py;
        return (px == 1 || px == 2) ? p1 : p0;
    }

    if (frame == 2)
        return (px | py) != 1 ? p1 : p0;

    if (frame == 3) {
        if (px == 0 || py == 0) {
            px |= py;
            return px == 3 ? p1 : p0;
        }

        return px == 2 && py == 2 ? p1 : p0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

  	vec3 col;
    vec4 fg;

    // Background
	if (ENABLE_TEXTURE_BG)
	    col = gen_bg(fragCoord.xy).rgb;
    else
