//     A tunnel effect only created with 2D tricks  
// --------------------------------------------------

vec2 rot (vec2 uv,float angle)
{
    return uv*mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	vec2 uv = ((fragCoord.xy / iResolution.xy)-0.5)/vec2(iResolution.y / iResolution.x, 1);

    float gridSize = 50.; // Higher value mean more triangular shapes
    float angle = 3.1416/6.; // Angle used to simulate the reflection
    float refFactor = 0.65; // Initial reflection factor (on the ground)

    uv = rot(uv,sin(iTime)/10.); // Rotating the whole image a bit
    uv = rot(uv,sin((uv.y*4.+iTime/2.)*10.)/300.); // Add a tiny distortion to whole structure

	uv.y += cos(uv.y*1.1); // Simulate the lens distortion
    uv.y -= 0.9; // Offset to move the horizon down

    if (uv.y+abs(uv.x)/3.8 < -0.20) // Define the ground shape
    {
        refFactor = 3.; // Factor used to reduce the luminosity of the reflection on the ground
	}   

    float transFac = smoothstep(-0.1,-0.25,uv.y+abs(uv.x)/3.8)+0.75; // Fake Wall/Ground Ambien Occlusion

    uv.x = -abs(uv.x); // Symetrize to lines to create a triangular shape
    uv.y += 0.2; // Align the whole shape to fil the fake reflection
    uv =rot(uv,-angle*3.5); // Rotate the shapes to create the reflection

	uv.y *= 0.9; // Strech uv verticaly a bit, to enhance the fake lens effect

    uv.x = -abs(uv.x);
    uv.y = abs(uv.y+0.2);

    uv =rot(uv,-angle*1.4); // Change the 1.4 valu to tweak the "verticality" of the structure

    uv.y -= 0.35;   // Offset do define if we gonna catch the lines from the top (tiny/slow)
    				// or from the bottom (big/fast) of the image.

    uv.y /= exp(uv.y); // Strech lines to make them accelerate (to simulate the perspective of the tunnel), from the bottom to the top

    float zDepth = 1.-(length (vec2(uv.x,uv.y-(5./7.)))*(1.+(abs(sin(iTime/2.))-0.35))); // Create a fake (animated) Z-Depth, only based on UV

    uv.y -=(iTime/7.0); // Move the lines UP (and make you fly through the tunnel) 


    float hLine = sin(uv.y*gridSize); // Draw the lines
	float grid = clamp(((hLine))*1.25,0.,1.);  // Clamp the result to avoid negative values

    vec3 gridColor = vec3(grid/(refFactor*transFac)); // Dim the reflection off the ground


    // Create a small bevel on the edges shape
    uv.y -= 0.01; // Offset the UV (so, go a few milliseconds away in the future) 
    hLine = sin(uv.y*gridSize);
	grid = clamp(((hLine))*2.25,0.,1.0);   
    gridColor += vec3(grid/(refFactor*transFac)/5.,0.25,0.); // Add this "echo shape"


    // Add a thickness to the shapes   
    uv.y -= 0.025; // Offset the UV (and travel to the future ... but far away than previously)
    hLine = sin(uv.y*gridSize);    
	grid = clamp(((hLine))*2.25,0.,1.0);   
    gridColor += vec3(grid/(refFactor*transFac)/6.);// Add this "echo shape"

    gridColor *= vec3(zDepth); //Multiply the image by the Z-Depth Mask

	// AND FINALY : Add more colors !
    gridColor += (vec3(sin(uv.x)/2.,abs(sin(uv.y*10.)/15.),0.1))*(abs(sin(iTime)+1.5));

    // Output to screen
    fragColor = vec4(gridColor,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.0, 0.0, 0.0);
    float sqrt3 = 1.73205;
    r *= 2.5;

    float the = 0.0 + iTime * 1.0;
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec2 r1 = abs(r);
    r1.y += 1.0 * r1.x / sqrt3;
    if(r1.x <= 0.5 *  sqrt3 && r1.y  <= 1.0){
    	ret = vec3(1.0);
    }
    vec2 r2 = abs(r - vec2(sqrt3 * 0.5, -1.5));
    r2.y += 1.0 * r2.x / sqrt3;
    if(r2.x <= 0.5 *  sqrt3 && r2.y  <= 1.0){
    	ret = vec3(1.0, 0.0, 0.0);
    }
    vec2 r3 = abs(r - vec2(sqrt3 * 0.5, 1.5));
    r3.y += 1.0 * r3.x / sqrt3;
    if(r3.x <= 0.5 *  sqrt3 && r3.y  <= 1.0){
    	ret = vec3(0.0, 1.0, 0.0);
    }
    vec2 r4 = abs(r - vec2(sqrt3, 0.0));
    r4.y += 1.0 * r4.x / sqrt3;
    if(r4.x <= 0.5 *  sqrt3 && r4.y  <= 1.0){
    	ret = vec3(1.0, 1.0, 0.0);
    }
    vec2 r5 = abs(r - vec2(-sqrt3, 0.0));
    r5.y += 1.0 * r5.x / sqrt3;
    if(r5.x <= 0.5 *  sqrt3 && r5.y  <= 1.0){
    	ret = vec3(0.0, 0.0, 1.0);
    }
    vec2 r6 = abs(r - vec2(-sqrt3 * 0.5, 1.5));
    r6.y += 1.0 * r6.x / sqrt3;
    if(r6.x <= 0.5 *  sqrt3 && r6.y  <= 1.0){
    	ret = vec3(0.0, 1.0, 1.0);
    }
    vec2 r7 = abs(r - vec2(-sqrt3 * 0.5, -1.5));
    r7.y += 1.0 * r7.x / sqrt3;
    if(r7.x <= 0.5 *  sqrt3 && r7.y  <= 1.0){
    	ret = vec3(1.0, 0.0, 1.0);
    }

	fragColor = vec4(ret,1.0);
}

