void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y; //many thanks to FabriceNeyret2

    float radius = 0.3;
    float ringThick = 0.05;

    vec2 stPolar = angleRadius(uv);

    float sPolar = stPolar.x * 3.0 + iTime * 10.0;
    //float sPolar = stPolar.x * 6.0;
    float cosSPolarTemp = cos(sPolar);
    float cosSPolar = filterPositive(cosSPolarTemp);

    vec3 color = vec3(cosSPolar);


    float inCircleAA = smoothstep(radius, radius + 0.005, angleRadius(uv).y); //AA version
    float smallCircleAA = smoothstep(radius - ringThick, radius - ringThick + 0.005, angleRadius(uv).y); //AA version
    vec3 col = 1.0 - vec3(inCircleAA);
    vec3 col_2 = 1.0 - vec3(smallCircleAA);
    vec3 colorGap = col - col_2;
    vec3 finalColor = color * colorGap;
    vec3 colorMask = vec3(10, 1.5, 1.0);
    finalColor /= 10.0;
    finalColor *= colorMask;

    float centerCircleAA = smoothstep(0.1, 0.1 + 0.005, angleRadius(uv).y); //AA version
    vec3 centerCircleColor = 1.0 - vec3(centerCircleAA);
    centerCircleColor /= 10.0;
    centerCircleColor *= colorMask;

    vec2 centerC = vec2(0.0, 0.0);
    float bubbleRadius = abs(sin(iTime * 3.0)) / 3.0;
    float bubbleCircleColor = circle(bubbleRadius, centerC, uv);
    vec4 bubbleColor = vec4(vec3(bubbleCircleColor) / 10.0 * colorMask, 1.0);

    fragColor = vec4(finalColor + centerCircleColor, 1.0);
    fragColor += bubbleColor;


}

//https://www.shadertoy.com/view/XlKSzw//
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 uv = 0.5 * (2. * fragCoord.xy - iResolution.xy) / iResolution.y;

    // warp uv pre-perspective shift
    float displaceAmp = 0.1;
    float displaceFreq = 2.5;
    uv += vec2(displaceAmp * sin(time + uv.x * displaceFreq));

    // 3d params
    // 3d plane technique from: http://glslsandbox.com/e#37557.0 
    float horizon = 0.2 * cos(time); 
    float fov = 0.35 + 0.15 * sin(time); 
	float scaling = 0.3;
    // create a 2nd uv with warped perspective
	vec3 p = vec3(uv.x, fov, uv.y - horizon);      
	vec2 s = vec2(p.x/p.z, p.y/p.z) * scaling;

    // wobble the perspective-warped uv 
    float oscFreq = 12.;
    float oscAmp = 0.04;
    s += vec2(oscAmp * sin(time + s.x * oscFreq));

	// normal drawing here
    // lines/lattice
    float color = max(
        smoothstep(0.2, 0.8, 0.94 - pow(sin(s.y * 100.), 0.3)), 
        smoothstep(0.2, 0.8, 0.94 - pow(sin(s.x * 100.), 0.3))
    );

	// 
	color *= p.z * p.z * 20.0;
    // 
	fragColor = vec4( vec3(0, color, 0), 1 );
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv += vec2(0.2, 0.1);
    uv *= 80.;

    // [ 0, 1 ]
    float spirally = cos(iTime / 10.);

    float normy = 1. + pow(iMouse.x / iResolution.x, 4.) * 50.;
    float flava = 20. * iMouse.y / iResolution.y;

    uv += (rotate2d(cos(iTime / 10.) * 2.6) * uv);

    uv += (rotate2d(length(uv) * spirally) * uv * -2.) * 0.5;

    uv += (iMouse.xy / iResolution.xy) * 50.;

    uv += cos(uv) * 2.;

    uv = mix(uv, uv * 1. / (1. + length(uv.yx)), 0.8);
    uv += sin(uv.yx * 1.) * 0.5;

    uv *= 0.1 / spirally;

    uv *= normy / (normy + pow(length(uv.yx), 2.));

    uv += sin(uv.yx * flava) * 0.25;

    uv += cos(uv + cos(uv*12.) * 0.25) * 0.5;
    uv += (rotate2d(iTime + length(uv) / 4.) * (uv.yx * -4.)) * 0.25;

    uv += cos(uv + cos(uv*12.) * 0.25) * 0.5;
    uv += (rotate2d(iTime + length(uv) / 8.) * (uv.yx * -8.)) * 0.02;

    // Time varying pixel color
    vec3 col = (cos(uv)).xyy; // * vec3(log(1. + length(uv)) * 1.);

    // vec3 col = vec3(log(length(uv)) / 2.);

    // Output to screen
    fragColor = vec4(col,1.0);
}

