vec3 list(vec2 uv, vec2 f){
        float t = iTime * 0.2;

    vec3 c = cc(uv.x);
    if(uv.y < .04){
        c = cc(iTime*0.01-uv.x);
    }

    if(uv.y > .3){
       c = vec3(mod(t/float(colors.length()),1.0));
    }else if(uv.y < .037 ){
       c = vec3(0);
    }

    if(uv.y > .5){
        vec3 a = mix(k(t-1.),k(t),uv.x),
  	    b = mix(k(t),k(t+1.),uv.x);
        c = mix(a,b,mod(t,1.));
    }

    if(uv.x-mod(t/float(colors.length()),1.0)<0.001){
        if(abs(uv.y-0.5)<0.01){
            c = vec3(mod(t/float(colors.length()),1.0));

        }
    }

     if(iResolution.y - f.y < 2. ){
       c = vec3(1);
    }

    return c;
}


vec3 detail(vec2 uv){  
    float fade = clamp(uv.y+sin(iTime),0.0,1.0);
    vec2 m =  iMouse.xy/iResolution.xy;
    vec3 solid = cc(uv.y > .5 ? m.x : m.y),
     gradient = mix ( cc(m.x), cc(m.y),fade),
     c = uv.x > 1./ gold ? solid : gradient;
    if(uv.x<.1){        
        c = mix ( cc(m.x), cc(m.y),floor(sin(uv.y*200.0)+1.0));     
    }
    return c;
}

