// This shader converts a hue from 0.0 to 1.0
// onto the corresponding RGB value with full saturation.
// I came up with this after noticing a similarity between this image:
// https://en.wikipedia.org/wiki/HSL_and_HSV#/media/File:HSV-RGB-comparison.svg
// and fuzzy sets.

// Define where the pure values of RGB are
// on the HSV color wheel. Convert the angle to
// a value between 0 and 1.
#define GREEN (120.0 / 360.0)
#define BLUE (240.0 / 360.0)
// Red is both at 0 degrees and 360 degrees.
#define RED1 (0.0 / 360.0)
#define RED2 (360.0 / 360.0)

// Size of a sector 
#define SECTOR (1.0 / 6.0)
// Slope of the trapezoid boundary
// (rise = 1, run = 1/6)
#define SLOPE (1.0 / SECTOR)

// Define fuzzy logic operations
// by aliasing built-in functions!
#define AND min
#define OR max

// Calculate the trapezoidal fuzzy membership function
// one of R, G, B, given a hue from 0.0 to 1.0.
// For example, hue_trapezoid(hue, GREEN)
// computes how "green" the hue is.
float fuzzy_rgb(float h, float center) {
    // This makes an upside down V shape
    // with maximum at 2. It's scaled
    // to match the values in the diagram (see the link above)
    // 2          /\
    //           /  \
    // 1        /    \
    //         /      \
    //        /        \
    // 0     /          \
    //      /      | center
    //     /              \
    // -1 /                \ 
    float triangle = 2.0 - SLOPE * abs(h - center);

    // Clamp it between 0.0 and 1.0 to turn it into 
    // a trapezoid
    //           ____
    // 1        /    \ <- the top spans 120 degrees. (2/6)
    //         /      \
    //        /        \
    // 0     /__________\ <- the base spans 240 degrees (4/6)
    //             | center
    return clamp(triangle, 0.0, 1.0);
}

// Convert a hue value to an RGB vector
// with full saturation. This asssumes
// hue is in the range 0.0 to 1.0.
vec3 hue2rgb(float hue) {
    // Compute membership in "green" and "blue"
    float green = fuzzy_rgb(hue, GREEN);
    float blue = fuzzy_rgb(hue, BLUE);

    // Red has to be handled differently since
    // it is both at 0 and 360 degrees. Compute
    // two half-trapezoids and union them together.
    float red1 = fuzzy_rgb(hue, RED1);
    float red2 = fuzzy_rgb(hue, RED2);
    float red = OR(red1, red2);

    // Return the results for each fuzzy set.
    return vec3(red, green, blue);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Paint a rainbow across the screen
    // horizontally. Make the rainbow move a bit with time.
    // the fract() ensures the hue is always in [0.0, 1.0]
    float hue = fract(uv.x + 0.1 * iTime);

    // compute the fuzzy set membership
    vec3 color = hue2rgb(hue);

    // From here, we could use the three components
    // to do some fuzzy logic operations. I'll do this
    // in upcoming shaders. For now, I just wanted
    // to display the results.

    // Output to screen
    fragColor = vec4(color, 1.0);
}

float aspectScreen = 1.0;
vec2  pixels = vec2(0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	pixels = fragCoord.xy / iResolution.xy;
    aspectScreen = iResolution.y / iResolution.x;

	vec3 color = vec3 (0.5,0.0,0.7);

    vec2 center = vec2 (0.5,0.5);
    center.x += 0.02 * sin(iTime);
    center.y += 0.03 * cos(iTime);

    vec2 q = pixels - center;
    float radio = length(q);

    float distord  = radio + 0.2* cos( 2.0 * atan(q.y*aspectScreen,q.x) + 0.1 * sin(iTime));

    float value = smoothstep(fract(15.0 * distord + fract(-iTime / 1.0))- 0.005,0.05,0.5); 

    color *= value * radio * 1.5; // Bright

    // Output to screen
    fragColor = vec4(color,1.0);
}

