#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359
#define TWO_PI 6.28318530718

uniform vec2 u_mouse;
uniform float u_time;

int gridSize = 50;
float lineWidth = 0.01;

/*
  circle()
    st - current pixel coordinates
    xy - normalized circle center position
    r  - circle radius (0.0 to 1.0)
    fade - edge fade (0.0 to 1.0, as a percent of radius)
*/
float circle(in vec2 st, in vec2 xy, in float r, in float fade) {
    vec2 dist = st - xy;

    return 1.0 - smoothstep(
      r - r * fade,
      r + r * fade,
      dot(dist, dist * 4.0)
    );
}

/*
  polygon()
*/
vec3 polygon(in vec2 st, in vec2 xy, in int n, in float angle, in float size, in float fade, in float pull) {//, in vec2 xy, in float r, in float fade) {
   float d = 0.0;

  // Remap the space to -1. to 1.
  vec2 mappedSt = (st + xy) *2.-1.;

  // Angle and radius from the current pixel
  float a = atan(mappedSt.x,mappedSt.y) + PI * (angle * 2.0 + 1.0);
  float _r = TWO_PI / float(n);

  // Shaping function that modulate the distance
  d = cos(floor((0.5 + pull) + a / _r) * _r - a) * length(mappedSt);

  return vec3(1.0 - smoothstep(size,size + fade + 0.01,d));
}

/*
  polyRipple()
*/
vec3 polyRipple(in vec2 st, in vec2 xy, in int n, in float angle, in float size, in float fade, in float pull) {//, in vec2 xy, in float r, in float fade) {
   float d = 0.0;

  // Remap the space to -1. to 1.
  vec2 mappedSt = (st + xy) *2.-1.;

  // Angle and radius from the current pixel
  float a = atan(mappedSt.x,mappedSt.y) + PI * (angle * 2.0 + 1.0);
  float _r = TWO_PI / float(n);

  // Shaping function that modulate the distance
  d = cos(floor((0.5 + pull) + a / _r) * _r - a) * length(mappedSt);

  // return vec3(1.0 - smoothstep(size,size + fade + 0.01, d));
  return vec3(clamp(fract(d * 10.0) + fade, 0.0, 1.0));
}

/*
  ripple()

*/

vec3 ripple(in vec2 st, in vec2 offset, in float freq, in float pos, in float grade) {
  // Remap the space to -1. to 1.440
  vec2 xy = st *2.-1.;

  // Make the distance field
  float d = length( xy - offset );

  // Visualize the distance field
  return vec3(clamp(fract(d*freq + pos) + grade, 0.0, 1.0));
}

/*
  grid()
    st - current pixel coordinates
    lineWidth - width of line stroke
    gridCount - number of grid lines in X and Y axes
    flatten - flatten XY grid colors
    dots - draw dots instead of line strokes
*/
vec2 grid(in vec2 st, in float lineWidth, in float gridCount, in bool flatten, in bool dots) {
  vec2 xy = fract(st * gridCount);

  vec2 color = step(lineWidth, vec2((xy.x) , (xy.y)));
  color *= step(lineWidth, 1.0 - vec2((xy.x) , (xy.y)));

  vec2 _out;

  if(!flatten) {
    return color;
  }

  if(dots != true) {
    _out = vec2(min(color.x, color.y));
  } else {
    _out = vec2(max(color.x, color.y));
  }

  return _out;
}

/*
  posSin()
  transforms sine function from -1 - 1 range to 0 - 1
*/
float posSin(in float n) {
  return (sin(n) + 1.0) / 2.0;
}

/*
  posSin()
  transforms cosine function from -1 - 1 range to 0 - 1
*/
float posCos(in float n) {
  return 0.5 + cos(n) / 2.0;
}

mat2 rotate2d(float _angle) {
    return mat2(
      cos(_angle), -sin(_angle),
      sin(_angle), cos(_angle)
    );
}

vec3 colorA = vec3(0.149,0.141,0.912);
vec3 colorB = vec3(1.000,0.833,0.224);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float u_time = iTime;
  vec2 st = fragCoord/iResolution.xy;
  // st.x *= st.x/st.y;
  vec3 color = vec3(1.0);
  float d = 0.0;

  vec3 pct = vec3(st.x);

  st.x = st.x + sin((fract(u_time / 5.0) + st.y) * 20.0) / 100.0;

  // offset grid position by time
  vec2 xy = st + fract(u_time / 20.0);

  pct.r += polyRipple(st, vec2(0.0), 3, fract(-u_time / 10.0) , 0.33, 0.0, 0.0).r / -4.0;
  pct.g += polyRipple(st, vec2(0.0), 4, fract(u_time / 10.0) , 0.33, 0.0, 0.0).r / -4.0;
  pct.b += polyRipple(st, vec2(0.0), 5, fract(u_time / 10.0 + 0.5) , 0.33, 0.0, 0.0).r / -4.0;

  st.x = st.x + sin((fract(u_time / 2.0) + st.y) * 50.0) / 50.0;

  // move space from the center to the vec2(0.0)
  st -= vec2(0.5);
  // rotate the space
  st = rotate2d( sin(u_time / 2.0 * PI) ) * st;
  // move it back to the original place
  st += vec2(0.5);

  pct.r -= circle(st, vec2(0.1, 0.9), 0.333, posSin(u_time * 2.0)) ;
  pct.r -= circle(st, vec2(0.9, 0.1), 0.333, posSin(u_time * 2.0)) ;
  pct.g -= circle(st, vec2(0.1, 0.1), 0.333, posSin(u_time * 2.0)) ;
  pct.g -= circle(st, vec2(0.9, 0.9), 0.333, posSin(u_time * 2.0)) ;

  color = mix(colorA, colorB, pct);
  fragColor = vec4(color, 1.0);
}

