vec2 ran2( vec2 p ) 
{
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),
			  			  dot(p,vec2(269.5,183.3))))
                 			* 43758.5453123);
}

float TileCell(vec2 tc, int grid, float seed)
{
    vec2 p   = tc * float(grid);
    vec2 ftc = fract(p);
    vec2 itc = floor(p);
    float m  = 1.0;

    for(int i = -1; i < 2; i++)
    {
        for(int j = -1; j < 2; j++)
        {
            vec2 n = vec2(i,j);
            vec2 q = itc + n;

            if(q.x == -1.0)q.x = float(grid-1);
            else if(q.x == float(grid))q.x = 0.0;

            if(q.y == -1.0)q.y =float(grid-1);
            else if(q.y == float(grid))q.y = 0.0;

            vec2 rp 	= ran2(q * seed);
            vec2 diff 	= n + rp - ftc;
            float dist 	= length(diff);
            m 			= min(m,dist);
        }
    } 

    return pow(1.0 - m,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv 	= fragCoord/iResolution.xy;
    float t 	= (sin(iTime * 0.25) * 4.0) + 6.0;
    uv 			= fract(uv*t);   
    // Cell noise 
    float c 	= TileCell(uv,4,1.0);
    fragColor.xyz = vec3(0.3,0.25,0.67) * c;
}

// An infinite corridor with infinitely high stairs on both sides.

// The sky is filled in analytically based on the angle of the camera ray,
// so no rays are wasted on the sky and the horizon is in the correct spot
// regardless of pathtracing distance or detail.

// I am using blue noise to randomly jitter the position of the camera for each pixel.
// This does a decent job of hiding aliasing in motion and I think it also works well
// for simulating the slightly fuzzy effect of mesoptic (low-light) vision.
// To turn the effect off, disable CAMERA_JITTER.


//quick hack to loop every 20 seconds (fix problems when shader runs for a long time)
#define iTime mod(iTime, 20.0)

#define PI			3.14159265359
#define HALF_PI		1.57079632679
#define TAU     	6.28318530718

#define FOV						PI*0.5
#define CAMERA_HEIGHT			1.0
#define CAMERA_SPEED			2.0
#define CAMERA_JITTER

#define LIGHT_POSITION			ro+vec3(0.5, -0.75, 0.0)
#define SHADOW_HARDNESS			250.0
#define NORMAL_MAPPING

#define MAX_STEPS_PER_RAY   	100
#define MAX_RAY_LENGTH     		200.0
#define EPSILON					0.00005		//error tolerance for ray-object collision
#define SHADOW_ERROR			0.01

//#define DEBUG


///////////////////////////////////////////////////////////////////////////////////////
//	MODEL															  		 		 //
///////////////////////////////////////////////////////////////////////////////////////

//displacements (use in place of 'p' in distance function)
vec3 repeat ( vec3 p, vec3 d ) { return mod(p+d*0.5,d)-d*0.5; }

vec3 xrotate (vec3 p, float r) { return vec3( p.x, p.y*cos(r)-p.z*sin(r), p.y*sin(r)+p.z*cos(r) ); }
vec3 yrotate (vec3 p, float r) { return vec3( p.x*cos(r)+p.z*sin(r), p.y, -p.x*sin(r)+p.z*cos(r) ); }
vec3 zrotate (vec3 p, float r) { return vec3( p.x*cos(r)-p.y*sin(r), p.x*sin(r)+p.y*cos(r), p.z ); }

//primatives
float plane ( vec3 p ) { return abs(p.y); }
float sphere ( vec3 p, float r ) { return length(p)-r; }
float cube ( vec3 p, float l ) { vec3 d=abs(p)-l; return min(max(d.x,max(d.y,d.z)),0.0)+length(max(d,0.0)); }
float box ( vec3 p, vec3 l ) { vec3 d=abs(p)-l; return min(max(d.x,max(d.y,d.z)),0.0)+length(max(d,0.0)); }
float ubox ( vec3 p, vec3 l ) { return length(max(abs(p)-l,0.0)); }

float xbar ( vec3 p, vec2 l ) { vec2 d=abs(p.yz)-l; return min(max(d.x,d.y),0.0)+length(max(d,0.0)); }
float ybar ( vec3 p, vec2 l ) { vec2 d=abs(p.xz)-l; return min(max(d.x,d.y),0.0)+length(max(d,0.0)); }
float zbar ( vec3 p, vec2 l ) { vec2 d=abs(p.xy)-l; return min(max(d.x,d.y),0.0)+length(max(d,0.0)); }

float xcylinder ( vec3 p, float r ) { return length(p.yz)-r; }
float ycylinder ( vec3 p, float r ) { return length(p.xz)-r; }
float zcylinder ( vec3 p, float r ) { return length(p.xy)-r; }

float xcylinder ( vec3 p, float r, float l ) { return max(length(p.yz)-r,abs(p.x)-l); }
float ycylinder ( vec3 p, float r, float l ) { return max(length(p.xz)-r,abs(p.y)-l); }
float zcylinder ( vec3 p, float r, float l ) { return max(length(p.xy)-r,abs(p.z)-l); }

float torus ( vec3 p, float ra, float rb ) { return length(vec2(length(p.xz)-ra, p.y))-rb; }
float cone ( vec3 p, float m ) { return dot(normalize(vec2(m, 1.0)), vec2(length(p.xz), p.y)); }


float model ( vec3 p ) {
    float stepsize = 0.5;
    float stairwidth = 1.0;
    float stairrepeat = 10.0;
    float railheight = 3.0;
    float wallwidth = 15.0;
    float trenchwidth = 5.0;

    float ground = p.y+2.0;																//ground plane
    //ground = max( ground, -zbar(p, vec2(trenchwidth, 5.0)) );							//cut trench

    p.x = abs(p.x);																		//mirror horizontally
    vec3 sp = zrotate(p-vec3(wallwidth,0.0,5.0), -PI*0.25);								//rotate space to match wall angle
    float wall = sp.y-1.25;																//wall

    sp = repeat( sp, vec3(0.0,0.0,stairrepeat) );										//repeat space along z-axis
    float stepsides = xbar(sp, vec2(1.5, stairwidth+0.25 ));							//slopes up side of stairs
    wall = max( wall, -stepsides );														//cut groove in wall for staircase
    stepsides = max( stepsides, -xbar(sp, vec2(2.0, stairwidth )) );					//cut space for steps

    float railing = 		ycylinder( repeat(p+vec3(0.5,0.0,15.0-stairwidth-0.125), vec3(1.0, 0.0, stairrepeat)), 0.05);
    railing = min( railing, ycylinder( repeat(p+vec3(0.5,0.0,15.0+stairwidth+0.125), vec3(1.0, 0.0, stairrepeat)), 0.05) );
    railing = max( railing, sp.y-railheight );											//cut balustrade above handrail
    railing = min( railing, xcylinder(sp-vec3(0.0, railheight, stairwidth+0.125), 0.075 ) );	//handrail #1
    railing = min( railing, xcylinder(sp-vec3(0.0, railheight,-stairwidth-0.125), 0.075 ) );	//handrail #2

    sp = repeat( sp, vec3(stepsize,0.0,0.0) );											//repeat space along stair run
    sp = zrotate(sp, PI*0.25);															//rotate space back to normal axes
    float steps = ubox( sp, vec3(1.0,1.0,stairwidth) )-0.01;							//staircase steps
    float stairs = min( steps, stepsides );												//combine steps with staircase sides
    stairs = min( stairs, railing );													//combine stairs with railings
    stairs = max( stairs, wallwidth-p.x-4.0 );											//cut stairs at bottom

    float trh = sqrt(2.0*railheight*railheight);
    float trenchrail = ycylinder( repeat(p-vec3(trenchwidth+0.1, -2.0, 0.0), vec3(0.0, 0.0, 1.0)), 0.05, trh*0.5);	//trenchside balustrade
    trenchrail = min(trenchrail, zcylinder(p-vec3(trenchwidth+0.1, trh*0.5-2.0, 0.0), 0.075 ) );					//trenchside handrails

    float d = min( wall, stairs );														//combine wall and stairs
    d = min( d, ground );																//combine with ground
    d = min( d, trenchrail );															//combine with trench railing
    d = max( d, -zbar(p, vec2(trenchwidth, 5.0)) );										//cut trench

	return d;
}


//////////////////////////////////////////////////////////////////////////////////////
//	MATH																			//
//////////////////////////////////////////////////////////////////////////////////////

float calcintersection ( vec3 ro, vec3 rd ) {
	//use sphere tracing to advance along ray
	float h = 100.0;
	float d = 0.0;
    for ( int i=0; i<MAX_STEPS_PER_RAY && h>EPSILON && d<MAX_RAY_LENGTH; i++ ) {
		h = model( ro+rd*d );
        d += h;
	}
	return d<MAX_RAY_LENGTH ? d : -1.0;
}


float softshadow( vec3 ro, vec3 rd, float ldist ) {  
    float res = 1.0;
    float dmin = SHADOW_ERROR;
    for( float t=dmin; t<ldist-dmin; ) {
        float h = model(ro + rd*t);
        if( h<SHADOW_ERROR*0.001 )
            return 0.0;				//full shadow - break early
        res = min( res, SHADOW_HARDNESS*h/t );
        t += h;
    }
    return res*res*(3.0-2.0*res);	//adjust penumbra falloff for smoother results
}


vec3 getdata ( vec3 ro, vec3 rd ) {
	//use sphere tracing to advance along ray
	float h = 100.0;
	float d = 0.0;
    int steps = 0;
    while ( steps<MAX_STEPS_PER_RAY && h>EPSILON && d<MAX_RAY_LENGTH ) {
		h = model( ro+rd*d );
        d += h;
        steps++;
	}
	return vec3(d<MAX_RAY_LENGTH ? d : -1.0, steps, h);
}


vec3 getnormal ( vec3 p ) {
	const float e = EPSILON*100.0;
	vec3 nor = vec3( model( p - vec3(e,0.0,0.0) ),
					 model( p - vec3(0.0,e,0.0) ),
					 model( p - vec3(0.0,0.0,e) ) );
    nor = normalize( vec3(model(p)) - nor );

    #ifdef NORMAL_MAPPING
    vec3 bump = texture( iChannel1, mix(p.xz,p.yz,abs(nor.x))*0.05 ).rgb - 0.5;
    nor += bump*0.3;
    nor = normalize(nor);
    #endif

    return nor;
}


