vec3 getrendersample ( vec3 ro, vec3 rd ) {
    if( (rd.x-rd.y)<0.0 && (rd.x+rd.y)>0.0 ) {
    	//ray hit sky
        return vec3(0.0,0.002,0.01)*(1.1-rd.y);
    }

    float rl = calcintersection( ro, rd );
    if ( rl > -0.5 ) {
        //draw object
		vec3 xyz = ro + rd*rl;
		vec3 nor = getnormal( xyz );

        vec3 ld = normalize(LIGHT_POSITION-xyz);								//direction to light
        float ldist = distance(xyz,LIGHT_POSITION);								//distance to light
        vec3 lcol = vec3(0.75,0.7,0.5);											//light colour
        float li = 20.0/(ldist*ldist);											//distance falloff
        float shad = softshadow(xyz, ld, ldist);								//soft shadows

        //float tex = texture( iChannel1, mix(xyz.xz,xyz.yz,abs(nor.x))*0.05 ).r;

        float diff = max(dot(ld, nor), 0.0);									//Lambertian diffuse

        vec3 hv = normalize(ld-rd);
        float m = 0.2;
        float a = acos(dot(nor,hv));
        float ta = tan(a);
        float ca = cos(a);
        float spec = exp(-(ta*ta)/(m*m))/(PI*m*m*ca*ca*ca*ca);					//Beckmann specular

        vec3 c = mix( diff*lcol, vec3(spec), 0.15) * li*shad;
        c += lcol*lcol*2.0/pow(distance(xyz,LIGHT_POSITION),2.0);				//bounce light approximation
        c *= 1.0 - clamp( (rl-50.0)/(MAX_RAY_LENGTH-50.0), 0.0, 1.0);			//black fog to better hide render limit
        return c;
	} else {
		return vec3(0.0);
	}
}


vec3 getheatmap ( vec3 ro, vec3 rd ) {
    //ray hit sky
    if( (rd.x-rd.y)<0.0 && (rd.x+rd.y)>0.0 ) return vec3(0.0);

    vec3 gd = getdata(ro, rd);

    float steps = gd.y/float(MAX_STEPS_PER_RAY);

    if ( gd.x > -0.5 ) {
		vec3 xyz = ro + rd*gd.x;
        vec3 ld = normalize( LIGHT_POSITION-xyz );
        vec3 ldata = getdata( LIGHT_POSITION, -ld );

        float lightsteps = ldata.y/float(MAX_STEPS_PER_RAY);
        float error = float(gd.z>EPSILON) + float(ldata.z>EPSILON);
        return vec3( steps, error*0.5, lightsteps );
	} else {
		return vec3( steps, 0.0, 0.0 );
	}
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy*2.0 - 1.0;	//camera-space position (-1 => 1)

    vec3 campos = vec3(0.0,CAMERA_HEIGHT,iTime*CAMERA_SPEED);
    //campos = vec3( iTime, iTime, 0.0 )*CAMERA_SPEED;			//stair climbing camera

    vec3 noise = texelFetch(iChannel0, ivec2(mod(fragCoord+float(iFrame)*30.0,1024.0)), 0).rgb-0.5;

    #ifdef CAMERA_JITTER
    uv += noise.xy / iResolution.xy * (iResolution.x*0.002); 				//positional noise
    //uv += noise.xy / iResolution.xy * (iResolution.x*0.01) * dot(uv,uv);
    #endif

    uv.y *= iResolution.y/iResolution.x;

    float d = 1.0/tan(FOV/2.0);						//distance from aperture/lens to picture plane
    vec3 camray = normalize( vec3(uv,d) );

    if (iMouse.z>0.0) {
    	camray = xrotate(camray, HALF_PI-iMouse.y/iResolution.y*PI);
    	camray = yrotate(camray, PI+iMouse.x/iResolution.x*TAU);
    }

    vec3 col;

    #ifdef DEBUG

    col = getheatmap( campos, camray );

    #else

    col = getrendersample( campos, camray );
    col *= 1.0-dot(uv,uv)*0.5;						//vignette
    col /= col+1.0;									//Reinhard tonemapping
    col = pow( col, vec3(0.45) );					//gamma adjustment
    col += noise/20.0;								//noise

    #endif

    fragColor = vec4( col, 1.0 );
}

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Material {
    vec3 ambient;
    vec3 emission;
    vec3 diffuse;
    vec3 specular;
    float shininess;
};

struct HitInfo {
    Ray ray;
    bool isHit;
    float dist;
    vec3 normal;
    Material material;
};

struct Sphere {
    vec3 center;
    float radius;
    Material material;
};

struct Light {
    bool directional;
    vec3 position;
    vec3 color;
    float attenuation_constant;
    float attenuation_linear;
    float attenuation_quadratic;
};

vec3 camera_position;
vec3 camera_target;
vec3 camera_up;
float aspect;
float fovy;
vec2 mouse;
float PI = acos(-1.);
float EPS = 0.001;
vec3 ambientLight;
Light lights[4];
int numLights = 0;
const int maxDepth = 2;

void addLight( Light light ) {
    lights[numLights] = light;
    numLights++;
}

HitInfo hitSphere( Ray ray, Sphere sphere ) {
    // p = o + d * t
    // (p - c) . (p - c) - r * r = 0
    // (o + d * t - c) . (o + d * t - c) - r * r = 0
    // (d . d) tt + 2 (d . (o - c)) t + ((o - c) . (o - c) - rr) = 0


    vec3 co = ray.origin - sphere.center;
    float a = dot(ray.direction, ray.direction);
    float b = 2. * dot(ray.direction, co);
    float c = dot(co, co) - sphere.radius * sphere.radius;

    float det = b * b - 4. * a * c;

    HitInfo hit;
    hit.ray = ray;
    if (det < 0.) {
        hit.isHit = false;
    } else {
        hit.isHit = true;

        float t1 = (-b + sqrt(det)) / (2. * a);
        float t2 = (-b - sqrt(det)) / (2. * a);

        if (t1 < 0. && t2 < 0.) {
            hit.isHit = false;
        } else if (t1 < 0.) {
            hit.dist = t2;
        } else if (t2 < 0.) {
            hit.dist = t1;
        } else {
            hit.dist = min(t1, t2);
        }
    }

    if (hit.isHit) {
        vec3 p = ray.origin + ray.direction * hit.dist;
        hit.normal = normalize(p - sphere.center);
        hit.material = sphere.material;
    }

    return hit;
}

HitInfo selectHit( HitInfo a, HitInfo b ) {
    if (!a.isHit) return b;
    if (!b.isHit) return a;
    if (a.dist < b.dist) return a;
    else return b;
}

HitInfo castRay( Ray ray ) {
    HitInfo hit;

    for (float i=0.; i<5.; i++) {
        for (float j=0.; j<5.; j++) {
            Sphere sp;
            sp.center = vec3(j-2., i-2., 2.*sin(3.*iTime+i+j));
            sp.radius = 0.4;
            sp.material.ambient = vec3(1.);
            sp.material.diffuse = vec3(i/5., 1., 1.);
            sp.material.specular = vec3(0.5);
            sp.material.shininess = (j+1.) * 10.;

            hit = selectHit(hit, hitSphere(ray, sp));
        }
    }

    return hit;
}

HitInfo castRayToLight( Ray ray ) {
    HitInfo hit;

    for (int i=0; i<numLights; i++) {
        Light l = lights[i];
        Sphere sp;
        sp.center = l.position;
        sp.radius = 0.1;
        sp.material.emission = l.color;
        hit = selectHit(hit, hitSphere(ray, sp));
    }

    return hit;
}

vec3 getColor( HitInfo hit ) {
    HitInfo hitStack[maxDepth];
    int depth = 0;
    hitStack[depth] = hit;

    while (depth < maxDepth-1) {
        hit = hitStack[depth];
        if (!hit.isHit) break;
        if (dot(hit.material.specular, hit.material.specular) <= 0.) break;

        vec3 p = hit.ray.origin + hit.ray.direction * hit.dist;
        vec3 N = hit.normal;
        vec3 E = normalize(- hit.ray.direction);

        Ray ray;
        ray.direction = 2. * dot(N, E) * N - E;
        ray.origin = p + ray.direction * EPS;
        HitInfo h = castRay(ray);
        hitStack[depth+1] = h;

        depth++;
    }

    depth++;

    vec3 lastColor = vec3(0.);
    while (depth > 0) {
        depth--;

        hit = hitStack[depth];
        if (!hit.isHit) {
            lastColor = vec3(0.);
            continue;
        }

        vec3 p = hit.ray.origin + hit.ray.direction * hit.dist;
        vec3 N = hit.normal;
        vec3 E = normalize(- hit.ray.direction);

        // reflection
        vec3 color = lastColor * hit.material.specular;

        // ambient
        color += ambientLight * hit.material.ambient + hit.material.emission;
        for (int i=0; i<numLights; i++) {
            Light l = lights[i];

            float dist = distance(l.position, p);

            vec3 L;
            if (l.directional) L = normalize(l.position);
            else L = normalize(l.position - p);

            vec3 H = normalize(E + L);

            // attenuation
            float attenuation;
            if (l.directional) attenuation = 1.;
            else attenuation = l.attenuation_quadratic * dist * dist +
                                l.attenuation_linear * dist +
                                l.attenuation_constant;

            // shadow
            Ray ray;
            ray.direction = L;
            ray.origin = p + ray.direction * EPS;
            HitInfo h = castRay(ray);

            if (!h.isHit || h.dist > dist) { // if there's no other object obstructing

                // diffuse
                color += l.color / attenuation * hit.material.diffuse * max(dot(N, L), 0.);

                // specular
                color += l.color / attenuation * hit.material.specular * pow(max(dot(N, H), 0.), hit.material.shininess);

            }
        }

        lastColor = color;
    }

    return lastColor;
}

vec3 getImage( vec2 coord ) {
    // setup camera
    float camera_distance = 6.;
    camera_position = vec3(-sin(mouse.x * PI) * camera_distance, mouse.y * camera_distance, -cos(mouse.x * PI) * camera_distance);
    camera_target = vec3(0., 0., 0.);
    camera_up = vec3(0., 1., 0.);
    fovy = 50.; // in degrees

    // setup lighting
    ambientLight = vec3(0., 0., 0.1);

    Light pl1;
    pl1.directional = false;
    pl1.position = vec3(4. * cos(3.*iTime), 4. * sin(3.*iTime), 2.);
    pl1.color = vec3(0.5);
    pl1.attenuation_quadratic = 0.1;
    addLight(pl1);

    Light pl2;
    pl2.directional = false;
    pl2.position = vec3(3. * cos(2.*iTime), 0., 3. * sin(2.*iTime));
    pl2.color = vec3(0.7, 0.3, 0.5);
    pl2.attenuation_linear = 0.3;
    addLight(pl2);

    Light dl1;
    dl1.directional = true;
    dl1.position = vec3(3., 3., 3.);
    dl1.color = vec3(0.7);
    addLight(dl1);

    Light dl2;
    dl2.directional = true;
    dl2.position = vec3(-3., -3., -3.);
    dl2.color = vec3(0.7, 0.3, 0.);
    addLight(dl2);

    // make camera coordinate
    vec3 w = normalize( camera_position - camera_target );
    vec3 u = normalize(cross( camera_up, w ));
    vec3 v = normalize(cross( w, u ));
    mat3 giri = mat3(u, v, w);

    float hasil_tan = tan(radians(fovy / 2.));
    vec3 ali;
    ali.y = hasil_tan * coord.y;
    ali.x = aspect * hasil_tan * coord.x;
    ali.z = -1.;

    Ray ray;
    ray.origin = camera_position;
    ray.direction = giri * ali;

    HitInfo hit = selectHit(castRay(ray), castRayToLight(ray));

    return getColor(hit);
}

