    fragColor = vec4(getImage(uv), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Inputs
    float gridRows=5.0;
    float gridCols=5.0;
    float satFactor=0.5;
    float movingSpeed=1.0;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //We find out in which grid the pixel is
    int pixCol=int(uv.x*gridCols);
    int pixRow=int(uv.y*gridRows);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = satFactor + satFactor*cos(iTime*movingSpeed+vec3(pixCol,pixRow,pixCol)+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float band(float t, float start, float end, float blur){
	float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);
    return step1 * step2;
}

float rect(vec2 uv, float left, float right, float bottom, float top, float blur) {
    float mask = band(uv.x, left, right, blur);
    mask *= band(uv.y, bottom, top, blur);
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    uv.x += sin(iTime);
    uv.y += cos(iTime) * 0.6;

    float x = uv.x;
    float y = uv.y;
    float m = -(x - 0.5) * (x + 0.5);
    y = uv.y + m * cos(iTime);

    float blur = 2. * sin(x)* sin(x);

    float mask = rect(vec2(x, y), -1.0, 1.0, -0.1, 0.1, blur);

    vec3 color = vec3(0.8, 0.8, 1.) * mask; 

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 0.1 * (fragCoord - iResolution.xy / 2.0) / iResolution.xy;
    uv *= vec2(iResolution.x / iResolution.y, 1.0);
    uv += vec2(-0.01, 0.0);

    float iterations = 0.0;
    float iterations_g = 0.0;
    float iterations_b = 0.0;

    float max_iterations = 100.;
    vec2 z = vec2(0.0, 0.0);

    // Twist Matrix
    mat2x2 twist_mat = mat2x2(
        vec2(cos(10.0 * length(uv * 4.0) * length(uv * 4.0)), - sin(10.0 * length(uv * 4.0) * length(uv * 4.0))),
        vec2(sin(10.0 * length(uv * 4.0) * length(uv * 4.0)), cos(10.0 * length(uv) * length(uv * 4.0)))
    );

    // Rot Matrix
    mat2x2 rot_mat = mat2x2(
        vec2(cos(iTime), - sin(iTime)),
        vec2(sin(iTime), cos(iTime))
    );

    // for red and blue
    for(float i = 0.0; i < max_iterations; i++)
    {        
        z = i * twist_mat * inverse(rot_mat) * vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) +  uv;

        if(length(z) > 2.0)
            break;            


        iterations += 1.0;
    }

    // for green
    for(float i = 0.0; i < max_iterations; i++)
    {        
        z = i * twist_mat * inverse(rot_mat) * inverse(rot_mat) * vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) +  uv;

        if(length(z) > 2.0)
            break;            


        iterations_g += 1.0;
    }

    // for blue
    for(float i = 0.0; i < max_iterations; i++)
    {        
        z = i * twist_mat * inverse(rot_mat) * inverse(rot_mat) * inverse(rot_mat) * vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) +  uv;

        if(length(z) > 2.0)
            break;            


        iterations_b += 1.0;
    }

    vec3 col = vec3(iterations / max_iterations, iterations_g / max_iterations, iterations_b / max_iterations);


    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = fragCoord.xy * .03;

    u += sin(u.yx*69.)*(cos(iTime+3.14159265359)+1.);

    vec2 fl = floor(u);
    float l = fract(sin(dot(fl,vec2(1.,1.123)))*1111.);

    u = fract(u)*2.-1.;

    vec4 c = vec4(
        fract(sin(dot(fl,vec2(1.123,1.234)))*1111.),
        fract(sin(dot(fl,vec2(1.345,1.456)))*1112.),
        fract(sin(dot(fl,vec2(1.567,1.678)))*1113.),
        1.);

	fragColor = sqrt(c*(dot(u,u)-l));
    fragColor = sqrt(c*(l-dot(u,u)));
}

#define STEPS 64

float map(vec3 p, float r, float dist) {
    vec3 pos = p + vec3(0., 0.,0.);
    p = fract(pos) * 2.0 - 1.0;
    return length(p) - r;
}

float trace(vec3 o, vec3 r) {
    float t = 0.0;
    int i = 1;
    for(i = 1; i < STEPS; ++i) {
       vec3 p = o + r * t;
       float dist = length(p);
       float d = map(p, .5, dist);
       t += d * .5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;

    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0));

    vec3 o = vec3(0., 0., -iTime);

    float d = trace(o, r);

    vec3 c = 1.0 / vec3(1.0 + d * d * .3);

    fragColor = vec4(1.0 - c, 1.0);
}

#define TAU 6.28318530718
#define saturate(x) clamp(x, 0., 1.)

float hash(vec2 uv) {
    float f = fract(cos(sin(dot(uv, vec2(.009123898, .00231233))) * 48.512353) * 1111.5452313);
    return f;
}

float noise(vec2 uv) {
    vec2 fuv = floor(uv);
    vec4 cell = vec4(
        hash(fuv + vec2(0, 0)),
        hash(fuv + vec2(0, 1)),
        hash(fuv + vec2(1, 0)),
        hash(fuv + vec2(1, 1))
    );
    vec2 axis = mix(cell.xz, cell.yw, fract(uv.y));
    return mix(axis.x, axis.y, fract(uv.x));
}

float fbm(vec2 uv) {
    float f = 0.;
    float r = 1.;
    for (int i = 0; i < 8; ++i) {
        f += noise((uv += .25) * r) / (r *= 2.);
    }
    return f / (1. - 1. / r);
}

vec4 blend(vec4 c1, vec4 c2)
{
    return vec4(mix(c1.rgb, c2.rgb, c2.a), max(c1.a, c2.a)); 
}

float mask(vec2 uv) {
    uv *= .9 - fbm(-uv * 2. + vec2(0, -iTime)) * (texture(iChannel0, vec2(.25, .25)).x) * .5;
    return length(uv) - .56;
}

vec4 spiral(vec2 uv) {
    if (mask(uv) > 0.) return vec4(0);
    float angel = atan(uv.x, uv.y) / TAU + .5 - iTime / 5. - texture(iChannel0, vec2(.1, .25)).x * .1;
    angel -= (uv.y + uv.x) / 20.;
    float dist = length(uv);
    float _smooth = dist * 15.;
    float alpha = saturate(sin(angel * 17. * TAU + sin(dist * 6. + 2.) * 2.) * _smooth);
    float base = .64 - texture(iChannel0, vec2(.9, .25)).x / 5.;
    float scratch = smoothstep(base, base + .01, fbm((uv - vec2(0, -iTime * .2)) * vec2(30., 2.)));
    alpha = saturate(alpha - scratch);
    alpha = saturate(alpha - smoothstep(-.1, .0, -dist));
    vec3 color = vec3(.2, .5, .0) + noise(uv * 4.) * .3;
	return vec4(color, alpha);
}

vec4 circle(vec2 uv) {
    float width = .05;
    float m = mask(uv);
    float alpha = smoothstep(-width, -width + .005, m)* smoothstep(-width - .005, -width, -m);
    vec3 color = vec3(.0, .5, .0) + (noise(uv * 03.) - .65) * .1;
    return vec4(color, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
	fragColor = blend(vec4(.0, .0, .0, 0.), spiral(uv));
    fragColor = blend(fragColor, circle(uv));
    uv *= 1000.;
    float amount = .1;
    fragColor.r += (hash(uv) - .5) * amount;
    uv += 100.;
    fragColor.g += (hash(uv) - .5) * amount;
    uv += 100.;
    fragColor.b += (hash(uv) - .5) * amount;
}

#define FAR     80.0
#define NRM_OFS 0.001

float sphere(vec3 p, float radius)
{
  return length(p) - radius;
}

float capsule(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float box(vec3 p, vec3 b)
{
    return length(max(abs(p) - b, 0.0));
}

float rbox(vec3 p, vec3 b, float r)
{
	return length(box(p, b)) - r;
}

float blend(float a, float b, float k)
{
    float h = clamp(0.5 + 0.5 * (a - b) / k, 0.0, 1.0);
    return mix(a, b, h) - k * h * (1.0 - h);
}

float world(vec3 p)
{
    float a = mod(iTime * 1.25, 3.141592 * 2.0);
    mat3 rx = mat3(
        cos(a), 0, sin(a),
