        blend(
        	capsule(p, vec3(0.33, 0.18, 0.12), vec3(0.56, -0.22, 0.16), 0.13),
            capsule(p, vec3(0.56, -0.24, 0.16), vec3(0.32, -0.65, -0.12), 0.13),
            0.02
        ),
        min(
