                capsule(p, vec3(-0.29, 0.15, -0.04), vec3(-0.45, -0.05, -0.4), 0.13),
                capsule(p, vec3(-0.45, -0.05, -0.4), vec3(-0.32, 0.45, -0.37), 0.13),
