#define tau 6.2831

const float cellCount = 9.;


float polygon(vec2 p, float vertices, float size) 
{
    float a = atan(p.x, p.y);
    float b = tau / vertices;
    return cos(round(a / b) * b - a) * length(p) - size;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    float cellSize = 2. / cellCount;
    vec2 s = sign(uv);
    vec2 node = floor(abs(uv) / cellSize) * s * cellSize + cellSize * .5 * s;
    uv = node - uv;

    fragColor.rgb = vec3(0.);
    float val = sin(-iTime  * 3. + node.y * 2.);

    uv.y /= ((val + 1.) / 2. + 1.) / 1.5;
    float colorAmount = smoothstep(.01, 0., polygon(uv, 5. - val * 2., .035));
    vec3 color = mix(vec3(0.7, .0, 0.3), vec3(0., 0.7, .4), (val + 1.) / 2.);
    fragColor.rgb = mix(vec3(.4, .2, .7) * .2, color, colorAmount);
}

