// Better to watch while listening to music

#define KALEIDOSCOPE_SPEED_X    9.0*0.01
#define KALEIDOSCOPE_SPEED_Y  -20.0*0.001
#define KALEIDOSCOPE_SPLITS     25.0

#define PI 3.14159265359

vec2 kaleidoscope(vec2 uv, vec2 offset, float splits)
{
    // XY coord to angle
    float angle = atan(uv.y, uv.x);
    // Normalize angle (0 - 1)
    angle = ((angle / PI) + 1.0) * 0.5;
    // Rotate by 90
    angle = angle + 0.25 * iTime * 0.05;
    // Split angle 
    angle = mod(angle, 1.0 / splits) * splits;

    // Warp angle
    angle = -abs(2.0*angle - 1.0) + 1.0;

    angle = angle*0.1;

    // y is just dist from center
    float y = length(uv);

    angle = angle * (y*3.0);

    return vec2(angle, y) + offset;
}

vec3 hsv2rgb(vec3 c)
{
  // igo Qulez
  // https://www.shadertoy.com/view/MsS3Wc
  vec3 rgb = clamp(abs(mod(c.x*6.+vec3(0.,4.,2.),6.)-3.)-1.,0.,1.);
  rgb = rgb * rgb * (3. - 2. * rgb);
  return c.z * mix(vec3(1.), rgb, c.y);
}

#define RAYS 20.0
#define ROTATION_SPEED 			0.05
#define SPINNING_SPEED 			0.1
#define COLOR_SHIFTING_SPEED 	0.5

// Here's where the accident comes into place
// Abomination that makes no sense if we already have polar coordinates
// But hey, it gives pretty results
vec3 radialThing(vec2 uv)
{
    vec2 center = vec2(0.0, 0.5) * sin(iTime*0.1);

    float c = distance(uv, center);

    vec3 res = vec3(0.0, 0.0, 0.0);

    vec2 pos = (uv - center);

    // Cartesian to polar coordinates
    float t = atan(pos.y, pos.x);
    float t2 = (t + PI) / (PI*2.0);

    // Spin with distance
    t2 += c * sin(iTime * SPINNING_SPEED) * 0.5;

    // Rotation
    t2 = mod(t2 + (iTime * ROTATION_SPEED), 1.0);

    // Rays
    float angle = mod(t2, 1.0 / RAYS);
    angle = ceil(angle * 2.0 * RAYS);

    float colorShiftingSpeed = iTime * COLOR_SHIFTING_SPEED;

    // Yuck
    vec3 color1 = hsv2rgb(vec3((cos(colorShiftingSpeed + c * 0.2 + (t2*PI*2.0)) + 1.0), 0.8, 1.0));
    vec3 color2 = hsv2rgb(vec3((cos((colorShiftingSpeed + 1.32) + c * 0.2 + (t2*PI*2.0)) + 1.0), 0.8, 1.0));
    vec3 color = mix(color2, color1, angle);

    res = color;

#if 1
    res.rgb = res.rgb * (1.0 - c);
    res.rgb = res.rgb * smoothstep(-1.5, 1.0, c*10.0);
#endif

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Mobile friendly UVs
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime;

    // Start with a good color
    time += 1207.0;

    vec2 A = vec2(time * KALEIDOSCOPE_SPEED_X * 0.005, 
                  time * KALEIDOSCOPE_SPEED_Y * 0.005);

	uv = kaleidoscope(uv, A, KALEIDOSCOPE_SPLITS);

	fragColor.rgb = radialThing(uv);
}

