vec4 aalias(float radius, float dist, vec4 bg, vec4 color, float ratio){
    return mix(bg, color, smoothstep(radius, radius * ratio, dist));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
    vec2 pos = (fragCoord -.5 * lM) / lM.x;

    pos.y += GAP;

    float dist = length(pos - vec2(0., GAP));

    if (dist < SIZE * 3.8) fragColor = aalias(SIZE * 3.8, dist, BLUE, WHITE, .99);
    else {
        if (dist < SIZE * 4.15) fragColor = aalias(SIZE * 4.15, dist, GREEN, BLUE, .99);
        else {
            fragColor = GREEN;
            return;
        }
    }

    pos *= .7 + abs(-abs(sin(iTime * 3.)) + .5);

    if (abs(pos.x) < SIZE / 2. && abs(pos.y) < SIZE / 2.) fragColor = RED; // Heart center
    else if (pos.y < RADIUS * .2 - GAP) { // Bottom heart
        float f = abs(pos.x) - .0855;
        if (pos.y > f) fragColor = mix(WHITE, RED, smoothstep(f, f + .002, pos.y));
    }
    else {
        float dist = length(vec2(abs(pos.x), pos.y + GAP) - vec2(.043));
        if (dist < RADIUS) fragColor = aalias(RADIUS, dist, WHITE, RED, .97); // Top heart
    }
}

#define CENTER (iResolution.xy / 2.0)
#define MAX_ITER 1024
#define MAX_DIST 1024.0
#define MIN_ZOOM 0.1
#define MAX_ZOOM 0.00001

// Spiral in the seahorse valley of the mandelbrot set.
#define POINT_OF_INTEREST vec2(-0.76158, -0.0847616)

// Fuzzy logic operations
#define NOT(x) (1.0 - (x))
#define AND min
#define OR max

// Constants for this mandelbrot set coloring
#define HUE_FREQ 100.0
#define NEAR_POINT 0.0001
#define FAR_POINT 0.01
#define WIDTH 0.005
#define ZOOM_SPEED 0.1

// Complex multiplication
vec2 cmult(vec2 z1, vec2 z2) {
    float real = z1.x * z2.x - z1.y * z2.y;
    float imag = z1.x * z2.y + z1.y * z2.x;
    return vec2(real, imag);
}

// Square a complex number.
vec2 csqr(vec2 z) {
    return cmult(z, z);
}

// Compute the length squared of a vector.
float length_squared(vec2 x) {
    return dot(x, x);
}


/**
 * This distance estimator is based on the
 * code from Inigo Quilez's article here:
 * http://iquilezles.org/www/articles/distancefractals/distancefractals.htm
 */
float mandelbrot_dist(vec2 c) {
    vec2 z = vec2(0.0);
    vec2 dz = vec2(0.0);

    float dist_mask = 1.0;
    float dist_squared = 0.0;
    for (int i = 0; i < MAX_ITER; i++) {
        // Compute the next derivative of the mandelbrot set sequence.
        // dz = 2 * z * dz + 1
        dz = 2.0 * cmult(z, dz) + vec2(1.0, 0.0);

        // Compute the next z value in the mandelbrot set sequence
        // z = z^2 + c
        z = csqr(z) + c;

        dist_squared = length_squared(z);

        if (dist_squared > MAX_DIST) {
            dist_mask = 0.0;
            break;
        }
    }

    // Distance estimator as described in the algorithm.
    // d(c) = |z| * log|z| / |dz|
    float d = sqrt(dist_squared / length_squared(dz)) * 0.5 * log(dist_squared);
    return d;// * dist_mask;
}

// Compute a fuzzy set for distance to a point.
// I use sigmoid-shaped boundaries with smoothstep()
// x is the input point
// center is the center of the distance field
// near, far are the center of the sigmoid-like curves that divide the sets.
// width is the width of the sigmoid-like curves
vec3 fuzzy_dist(float dist, float near_point, float far_point, float width) {
    float half_width = width / 2.0;

    // Make the sigmoid shapes for near and far using smoothstep()
    float near = smoothstep(near_point + half_width, near_point - half_width, dist);
    float far = smoothstep(far_point - half_width, far_point + half_width, dist);

    // We are at a medium distance if we are not near and not far.
    float mid = AND(NOT(near), NOT(far));

    // Return the membership for the three categories.
    return vec3(near, mid, far);
}

// Define where the pure values of RGB are
// on the HSV color wheel. Convert the angle to
// a value between 0 and 1.
#define GREEN (120.0 / 360.0)
#define BLUE (240.0 / 360.0)
// Red is both at 0 degrees and 360 degrees.
#define RED1 (0.0 / 360.0)
#define RED2 (360.0 / 360.0)

// Size of a sector 
#define SECTOR (1.0 / 6.0)
// Slope of the trapezoid boundary
// (rise = 1, run = 1/6)
#define SLOPE (1.0 / SECTOR)

// Calculate the trapezoidal fuzzy membership function
// one of R, G, B, given a hue from 0.0 to 1.0.
// For example, hue_trapezoid(hue, GREEN)
// computes how "green" the hue is.
float hue_trapezoid(float h, float center) {
    // This makes an upside down V shape
    // with maximum at 2. It's scaled
    // to match the values in the diagram (see the link above)
    // 2          /\
    //           /  \
    // 1        /    \
    //         /      \
    //        /        \
    // 0     /          \
    //      /      | center
    //     /              \
    // -1 /                \ 
    float triangle = 2.0 - SLOPE * abs(h - center);

    // Clamp it between 0.0 and 1.0 to turn it into 
    // a trapezoid
    //           ____
    // 1        /    \ <- the top spans 120 degrees. (2/6)
    //         /      \
    //        /        \
    // 0     /__________\ <- the base spans 240 degrees (4/6)
    //             | center
    return clamp(triangle, 0.0, 1.0);
}

// Convert a hue value to an RGB vector
// with full saturation. This asssumes
// hue is in the range 0.0 to 1.0.
vec3 fuzzy_hue(float hue) {
    // Compute membership in "green" and "blue"
    float green = hue_trapezoid(hue, GREEN);
    float blue = hue_trapezoid(hue, BLUE);

    // Red has to be handled differently since
    // it is both at 0 and 360 degrees. Compute
    // two half-trapezoids and union them together.
    float red1 = hue_trapezoid(hue, RED1);
    float red2 = hue_trapezoid(hue, RED2);
    float red = OR(red1, red2);

    // Return the results for each fuzzy set.
    return vec3(red, green, blue);
}


// Normalized sine function from [0 to 1] instead of [-1 to 1]
float norm_sin(float x) {
    return 0.5 + 0.5 * sin(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - CENTER)/iResolution.y;

    float zoom = mix(MIN_ZOOM, MAX_ZOOM, norm_sin(ZOOM_SPEED * iTime));

    vec2 c = POINT_OF_INTEREST + uv * zoom;

    float dist = mandelbrot_dist(c);

    // Determine if we are "near", "medium", "far" to the Mandelbrot set.
    vec3 dist_set = fuzzy_dist(dist / zoom, NEAR_POINT, FAR_POINT, WIDTH);

    // Map the distance onto a hue.
    float hue = fract(dist / zoom * HUE_FREQ);
    // Determine the R, G, B fuzzy sets from the hue. This vector
    // of membership functions can be used as a color
    vec3 color = fuzzy_hue(hue);

    // Start with a black screen
    vec3 image = vec3(0.0);

    // Fuzzy logic time!
    // If distance is near the mandelbrot set, color it navy
    vec3 navy = vec3(0.0, 0.0, 0.35);
    image = mix(image, navy, dist_set.x);
    // If distance is medium, paint the hue color
    image = mix(image, color, dist_set.y);
    // If distance is far, draw a background.
    vec3 background = texture(iChannel0, uv).rgb;
    image = mix(image, background, dist_set.z);

    //Note: it would be more correct to compost these layers from background
    // to foreground. However, I'm happy with how this looks.


    // Output to screen
    fragColor = vec4(image, 1.0);
}

