#define TWO_PI 6.28318530718

float getDistortion(float amplitude, float frequence) {
	return amplitude*sin(frequence+iTime*0.5);
}
vec3 generateCheckerboard(vec2 screen_space, vec3 black, vec3 white)
{
    // make the chequered pattern smaller towards the center 
   	float checksum = ceil(((screen_space.x)/(5.-screen_space.x))+iTime)+ceil((screen_space.y)+4.0*iTime);

    // odd or even checkerboard position
    bool check = mod(checksum,2.0) == 0.0;



    // set colour according to which board position we are on 
	vec3 color = (check)? black:white;
    color = vec3(color);
    return color; 
}
vec3 colorCheckerboard(vec3 color, vec2 st)
{
  	color.r = 0.2*smoothstep(0.3, 4.6, st.y) ;
    color.r += getDistortion(.1,st.y);
	color.g = 0.2*smoothstep(0.3, 4.6, st.x) ;
    color.g += getDistortion(0.1,st.x);
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    // scale up coordinate system for a 5*5 checkerboard
    st*=5.0;
    vec3 color = vec3(.0);
    vec3 black = vec3(0.0);
    vec3 white = vec3(1.0);


    vec2 toCenter = vec2(2.5)-0.68*st;
 	float angle = atan(toCenter.x, toCenter.y);
    float radius = length(toCenter)*2.;
    // to polar coordinates


    st.x = radius+4.0;
    st.y = angle*TWO_PI*1.5;
    color = generateCheckerboard(st, black, white);
    color = mix(color,black,1.-radius/10.);
    color.r += getDistortion(0.2,0.2);
    color = color-vec3(0.1);

    fragColor = vec4(color,1.0);
}

float hash13(vec3 p3) {
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

// how many values per channels
// this is 256 for most displays
const float SIZE = 8.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // color to display, use floating point precision here
    vec3 col = vec3(fragCoord/iResolution.xy, sin(iTime)*0.5+0.5);

    // get some noise
    float noise = 0.0;
    // you can use different noises
    // best is to use the dither texture, or use white noise
    noise = texture(iChannel0, fragCoord/8.0).r;
    if (fragCoord.x<iResolution.x*0.5)
    	noise = hash13(vec3(fragCoord, iFrame));


    // to do dithering, simply add noise to your frame
    // that's it
    col += (noise-0.5)/SIZE*2.0;


    // do quantization, this will simulate a lower color depth
    // essentialy, go from a float to an int
    col = (floor(col*SIZE)+0.5)/SIZE;
    fragColor = vec4(col, 1);

}

