//changes: fixed clamping of t.

const vec2 k = vec2(1.,0.);
const float pi = acos(-1.);

float dist(vec2 p_0,vec2 p_1,vec2 p_2,vec2 x,float t)
{
    return length(x-pow(1.-t,2.)*p_0-2.*(1.-t)*t*p_1-t*t*p_2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy-.5;

    //control points. Uncomment to make it move ;-)
    vec2 p_0 = vec2(-.25,-.25)+.15*vec2(sin(iTime), cos(3.*iTime)), 
        p_1 = vec2(.25,-.25)+.5*vec2(sin(iTime), .5-sin(iTime)),
        p_2 = vec2(.25,.25)+.15*vec2(cos(iTime), sin(iTime));

	//coefficients for 0 = t^3 + a * t^2 + b * t + c
    vec2 E = uv-p_0, F = p_2-2.*p_1+p_0, G = p_1-p_0;
    vec3 coeff = vec3(3.*dot(G,F), 2.*dot(G,G)-dot(E,F), -dot(E,G))/dot(F,F);

    float a = coeff.x, b = coeff.y, c = coeff.z;

	//discriminant and helpers
    float p = b-a*a/3., q = 2.*a*a*a/27.-a*b/3.+c, dis = q*q/4.+p*p*p/27.,
        tau = a/3.;

    //solution with cardan formula
    float k1 = -.5*q+sqrt(dis), k2 = -.5*q-sqrt(dis);
    float u = sign(k1)*pow(abs(k1), 1./3.), v = sign(k2)*pow(abs(k2), 1./3.);

    float t_min = 1.;
    if(dis > 0.)
    {
        t_min = u+v-tau;
        t_min = clamp(t_min, 0.,1.);
    }
    else if(dis < 0.)
    {
        float fac = sqrt(-4./3.*p), arg = -.5*q*sqrt(-27./p/p/p),
            t1 = -fac*cos(acos(arg)/3.+pi/3.)-tau,
            t2 = fac*cos(acos(arg)/3.)-tau,
            t3 = -fac*cos(acos(arg)/3.-pi/3.)-tau;
        t1 = clamp(t1,0.,1.);
        t2 = clamp(t2,0.,1.);
        t3 = clamp(t3,0.,1.);
        float b1 = dist(p_0,p_1,p_2,uv,t1),
            b2 = dist(p_0,p_1,p_2,uv,t2),
            b3 = dist(p_0,p_1,p_2,uv,t3);
        t_min = mix(mix(t1, t2, step(b2, b1)), t3, step(b3, min(b1, b2)));
    } 
    float dis_min = dist(p_0,p_1,p_2,uv,t_min);

    vec3 col = k.yyy;
    //spline distance isolines
    col += (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)))*mod(dis_min,0.1)*10.+vec3(1.,0.,0.)*step(dis_min,.05);
    //control points
    col += k.yxx*step(length(uv-p_0), .05)+k.xyx*step(length(uv-p_1), .05)+k.yxx*step(length(uv-p_2), .05);

    fragColor = vec4(col,1.0);
}

// Let's say you want to smooth a continuously changing value temporally using weighted average,
// such as user input, camera target position etc., and you'd do something like:
// currValue = lerp(currValue, targetValue, 0.3);
// You are doing this every frame and you've found a nice lerp factor for your frame rate.
// Now, perhaps you need to change your target frame / physics rate and you notice the factor
// you're using no longer gives the same smoothing.
// Your lerps basically make a geometric series, although the target value can change every frame.
// geometricRatio returns a corrected ratio for lerping with your new timestep,
// referenceRatio is assumed to work correctly on the reference timestep.

#define REFRATIO 0.5
#define REFDELTA 0.2

float geometricRatio(float referenceRatio, float referenceDt, float targetDt)
{
	return 1.0 - pow(1.0 - referenceRatio, targetDt / referenceDt);
}

// ----- The rest is just for demo

float geometricCurve(float x, float ratio, float delta)
{
    x = max(x, 0.0);

    float xSteps = x / delta;

    int left = int(floor(xSteps));
    int right = int(ceil(xSteps));
    float fr = fract(xSteps);

    // A series of lerps, start value = 0, target value = 1
    float l = 0.0;
    float r = 0.0;
    for (int i = 0; i < right; i++)
    {
        l = r;
        r = mix(r, 1.0, ratio);
    }
    l = (left == right) ? r : l;

    return mix(l, r, fr);
}

vec2 xy;		// current graph coords
vec2 dxy; 		// pixel size in graph units
vec4 fColor;	// current fragment color

void mixColor(vec4 color, float alpha)
{
    fColor = vec4(mix(fColor.rgb, color.rgb, alpha * color.a), 1.0);
}

void drawGrid(float stepSize, vec4 gridCol)
{
	float mul = 1.0 / stepSize;
	vec2 g = abs(vec2(-0.5) + fract((xy + vec2(stepSize) * 0.5) * mul)); // g passes 0 at stepSize intervals
	g = vec2(1.0) - smoothstep(vec2(0.0), dxy * mul * 1.5, g);
    mixColor(gridCol, max(g.x, g.y));
}

void drawCurve(float value, vec4 curveCol)
{
	// Using y distance to curve, modifying distance threshold with approximated slope
    // Derivative breaks with drastic changes of value and discontinuities, but so be it
    float yDist = abs(value - xy.y);
    float dValue = abs(dFdx(value));
	float c = 1.0 - smoothstep(0.0, (dxy.y + dValue) * 2.0, yDist);
	mixColor(curveCol, c);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    // graph setup
	float aspect = iResolution.x / iResolution.y;
	vec2 graphSize = vec2(aspect * 1.2, 1.2);
	vec2 graphPos = 0.5 - graphSize * 0.5;

	xy = graphPos + uv * graphSize;	// graph coords
	dxy = graphSize / iResolution.xy;	// pixel size in graph units

    // new delta value
    float auto = 0.51 + sin(iTime * 0.5) * 0.49;
	vec2 mouse = iMouse.xy / iResolution.xy;
    mouse = graphPos + mouse * graphSize;
    float newDelta = iMouse.z <= 0.0 ? auto : (mouse.x);
    newDelta = max(0.02, newDelta);

    // background
	fColor = mix(vec4(1.0, 1.0, 1.0, 1.0), vec4(0.7, 0.7, 0.7, 1.0), pow(length(0.5 - uv) * 1.414, 3.5));

	// grid
    drawGrid(0.1, vec4(0.0, 0.0, 0.0, 0.2));
	drawGrid(0.5, vec4(0.0, 0.0, 0.0, 0.3));
	drawGrid(1.0, vec4(0.0, 0.0, 0.0, 0.4));

    // curves
    drawCurve(geometricCurve(xy.x, REFRATIO, REFDELTA), vec4(0.91, 0.13, 0.23, 0.5));
    float newRatio = geometricRatio(REFRATIO, REFDELTA, newDelta);
    drawCurve(geometricCurve(xy.x, newRatio, newDelta), vec4(0.15, 0.45, 0.85, 0.8));

	fragColor = fColor;
}

float map(vec3 p)
{
    vec3 q = fract(p)* 2.0 - 1.0;
    return length(q)- 0.2;
}
float trace(vec3 o, vec3 r)
{
    float t = 0.0;
    for (int i=0; i<32; ++i){
        vec3 p= o+r*t;
        float d =map(p);
        t +=d*0.5;
    }
    return t;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv=uv*2.0 - 1.0;
    uv.x*=iResolution.x/iResolution.y;
    vec3 r=normalize(vec3(uv, 1.0));
    float the = iTime*0.014;
    r.xz *=mat2(cos(the), -sin(the), sin(the), cos(the));
    vec3 o = vec3(0.0, iTime,iTime);
    float t = trace(o, r);
    float fog=2.0/(1.0 + t*t*0.1);
    vec3 fc = vec3(fog);
    fragColor=vec4(fc, 1.0);
}

#define PI 3.141592653589

float time;

float polydist(vec2 p,float angleoffset)
{
    float d=0.;
    const int N=6;
    for(int i=0;i<N;++i)
    {
        float a=float(i)/float(N)*PI*2.+angleoffset;
        vec2 n=vec2(cos(a), sin(a));
        d=max(d,dot(n,p));
    }
    return d;
}

vec2 intersectSphere(vec3 ro, vec3 rd, vec3 org, float rad)
{
    float a = dot(rd, rd);
    float b = 2. * dot(rd, ro - org);
    float c = dot(ro - org, ro - org) - rad * rad;
    float desc = b * b - 4. * a * c;
    if (desc < 0.)
        return vec2(1., 0.);

    return vec2((-b - sqrt(desc)) / (2. * a), (-b + sqrt(desc)) / (2. * a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.a=1.0;
    fragColor.rgb=vec3(0);
    time=iTime;
    vec2 tt = (fragCoord/iResolution.xy*2.-1.)*.75;
    tt.x*=iResolution.x/iResolution.y;
    float an=0.;

    vec3 ro=vec3(0.,1.,5);
    vec3 rd=normalize(vec3(tt.xy,-1.3));
    vec2 ot=tt.xy;

    rd.xy=normalize(rd.xy)*(length(rd.xy)-pow(length(rd.xy),4.)/1.5);

    an=0.2;
    rd.yz=mat2(cos(an),sin(an),sin(an),-cos(an))*rd.yz;
    ro.yz=mat2(cos(an),sin(an),sin(an),-cos(an))*ro.yz;

    an=-2.5;
    rd.xz=mat2(cos(an),sin(an),sin(an),-cos(an))*rd.xz;
    ro.xz=mat2(cos(an),sin(an),sin(an),-cos(an))*ro.xz;

    vec3 rdx=rd+dFdx(rd);
    vec3 rdy=rd+dFdy(rd);

    float t=-ro.y/rd.y;

    vec3 spherepos=vec3(0.,sin(time*4.)*.1,0.);

    vec2 dt=intersectSphere(ro, rd, spherepos, 1.);

    if(dt.x<dt.y&&dt.x<t)
    {
        vec3 rp=ro+rd*dt.x-spherepos;
        an=3.8;
        rp.xz=mat2(cos(an),sin(an),sin(an),-cos(an))*rp.xz;

        an=cos(time*4.)*.05;
        rp.yz=mat2(cos(an),sin(an),-sin(an),cos(an))*rp.yz;

        fragColor.rgb=vec3(.5,1,.5)/1.3;
        float a=smoothstep(0.,.0005,dot(normalize(vec3(abs(rp.x),rp.y,rp.z)),normalize(vec3(.6,1.,-1)))-.99);

        if(mod(time-.5,3.)<.1)
            a=step(abs(rp.y-.65),.01)*step(abs(abs(rp.x)-.4),.1);

        a=max(a,1.-smoothstep(0.,.005,abs(length(rp.xy-vec2(0.,.3))-.15)-.02-.01));

        fragColor.rgb=mix(fragColor.rgb,vec3(.8),a);
    }
    else if(t>0.&&(t<dt.x||dt.x>dt.y))
    {
        vec3 rp=ro+rd*t;
        vec3 rpx=ro+rdx*-ro.y/rdx.y;
        vec3 rpy=ro+rdy*-ro.y/rdy.y;

        float d=length(rp-spherepos)-1.;

        float a=0.;
        float wsum=0.;
        const int N=3;
        for(int i=-N;i<=+N;++i)
        {
            for(int j=-N;j<=+N;++j)
            {
                float w=1.;
                float u=float(i)/float(N)/2., v=float(j)/float(N)/2.;
                vec3 rp2=rp+(rpx-rp)*u+(rpy-rp)*v;

                rp2.xz+=normalize(rp2.xz)*cos(d*3.-time)*exp(-d)*2.;

                rp2.xz*=1.5;
                a+=w*step(abs(cos(rp2.z+sin(rp2.x)+time/3.+cos(rp2.z+rp2.x/7.)*1.5)*cos(time/2.+rp2.x+sin(rp2.z/2.+15.*sin(rp2.z/20.)))),
                          -pow(smoothstep(10.,240.,length(rp2.xz)),.5)+.2+cos(time*2.+rp2.x/3.-rp2.z/2.)*.05);
                wsum+=w;
            }
        }

        a/=wsum;

        a+=step(fract(d*2.-time/2.),.3-d/4.);

        fragColor.rgb=mix(vec3(0.3,0.35,1.),vec3(1),clamp(a,0.,2.)/10.);


        fragColor.rgb+=exp(-length(rp.xz-ro.xz)/4.)*.3;
    }

    {
        float a=0.;
        float u=(atan(rd.z,rd.x)/PI)+time/400., v=acos(abs(rd.y))/PI+.02;

        for(int i=0;i<45;++i)
        {
            float u2=u+float(i)*.2;
            a=max(a,(cos(float(i)*13.)*.3+.7)*(1.-(cos(float(i)*9.)*.3+.7)*smoothstep(0.,.03,(polydist(vec2(mod(u2*8.,1.)-.5,(v-pow(.45+cos(float(i)*18.+floor(u2*8.)*21.)*.08,.8))*10.),floor(u2*8.)+time/15.))-(.11+cos(float(i))*.12))));
        }

        fragColor.rgb+=mix(vec3(0.3,0.35,1)/1.4*mix(.1,1.,step(0.,rd.y)),vec3(1),clamp(a,0.,1.)/3.)*mix(.2,1.,step(0.,rd.y));
    }

    fragColor.rgb+=pow(exp(-abs(rd.y)*(1.+100.*step(rd.y,0.))),.24)*.3;
    fragColor.rgb+=exp(-length(ot)/2.)*.2;

    float b=smoothstep(max(1e-4,length(max(abs(ot)-.67,0.))-.05),.0,.001);

    fragColor.rgb=mix(min(fragColor.rgb,1.),vec3(.3),b);

    fragColor.rgb=pow(fragColor.rgb,vec3(1./2.4));
}

#define FieldOfView 1.
#define gt iGlobalTime * 0.1 // to make tunnel next
//#define sgt(i) abs(sin(i * iGlobalTime))
#define Giter 5 //iterations of the glowing lines fractal



float DE(in vec3 z)
{	
	/* Folding 'tiling' of 3D space;
	z  = abs(1.0-mod(z,2.0));   */

    z = fract(z);// + 0.1*fract(2.*z);

    float d =length(z.xy-vec2(0.5));
    d = min(d, length(z.xz-vec2(0.5)));
    d = min(d, length(z.yz-vec2(0.5)));

    return d-0.01;
}

vec3 Glow(vec3 z, float b){
    z = fract( z + 2.0) * 2. -1.; // divides plane again

    //z.z = b; // THIS PART MAKES THE FLASHES!
    for( int i = 0; i < Giter; ++i){ 
        float d = clamp( dot(z, z), .05, .65); 
        z = abs( z ) / d - 1.31;
    }
    //z.x += 0.2;
    return z;
}


vec3 fog(float a,vec3 b,float c){ 
    //a *= c;
    return 1. / (( 1. + 2.*b/a + b * b/(a*a))*c ); // must be a fog function
}

// taes vec3 from Glow, v (random), u (ball at end of raytracer), and an int that is the transparency
vec3 W(vec3 fr,float b,float c,float d){
    vec3 e= fog(0.01, abs(fr), d) * 2.; 
    return (e * 7. + e.yzx * 1.5 + e.zxy * 1.5) * max( 1. -c * 200. / d, 0. ) / d * 70.;
}




void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float A = 3. * iTime;
    vec2 uv = fragCoord.xy / iResolution.xy *2. -1.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 base = vec3(.2, 0.5*sin(0.01*iTime), 0.5*cos(0.01*iTime));
    //vec3 base = vec3(1.0, 0.7, 0.8);

    vec3 camPos = 0.5*iTime * base;
	vec3 target = camPos + base;
	vec3 camUp  = vec3(1.0,1.0,0.0);

	vec3 camDir   = normalize(target-camPos); // direction for center ray
	camUp = normalize(camUp-dot(camDir,camUp)*camDir); // orthogonalize
	vec3 camRight = normalize(cross(camDir,camUp));

    vec3 rayDir = normalize( (camRight * uv.x  + camUp * uv.y + camDir));
    vec3 pos, color; float totalDistance, distance, v;

    for(int i = 0; i < 32; ++i){
        pos = camPos + rayDir * totalDistance;
        distance = min(abs(fract( pos.z ) - 0.5), DE(pos));
        v = sin( A * .01 + pos.z);
        vec3 x = Glow( pos, v);
        totalDistance += distance  + .001; 
        color += ( W( x, v, distance, 0.5) ) * (distance + 0.001);
    }
    fragColor = vec4( pow( color, vec3( .45 )), .5);
}

