void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // primitive adaptive sampling
    vec3 col = renderPixel(fragCoord);
    vec3 col2 = renderPixel(fragCoord+vec2(0.5));
    if ( isAdaptiveAAEnabled() || length(col-col2) > 0.2 ) {
        // there be antialiasing, sample more with slightly randomized offsets (frankly, it's not worth the hassle)
        for ( int i = 1 ; i < 16 ; i++ ) {
            if ( i == 10 ) {
                col += col2;
            } else {
                vec2 coord = fragCoord + vec2(float(i & 3 )*0.25, float((i>>2) & 3 )*0.25);
                if ( isRandomAAEnabled() )
                	coord += vec2( rand(fragCoord+vec2(float(i))) )*0.2;

        		col += renderPixel(coord);
            }
        }
        col = col / 16.0;
    } else if ( isSimpleAAEnabled() ) {
        col += col2;
    	col = col / 2.0;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/148.0;

    // Time varying pixel color
    vec3 col = 0.5 - 1.5*tan(iTime+uv.xxx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(1.0,0.5,0.0,1.0);
    fragColor = vec4(col,1.0);
}

