    // Time varying pixel color
    vec3 col = background;

    for(float i = 0.0; i < number; i++)
    {
        float ste = 1.0/number;
        float turn = mod(iTime * speed, 2.0) - 1.0;

        vec3 circleColor = vec3(0., ste * i, ste * i);
        vec2 circleCenter = center - vec2(0.0,i * (ste/2.));
        float circleRadius = radius - i * (ste/2.);

        circleCenter = circleCenter - center;
        circleCenter = rotate2d(turn*PI ) * circleCenter;
        circleCenter = circleCenter + center;

        col = mix(col,circleColor,circle(circleCenter,uv,circleRadius));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n = float(uint(fragCoord.x) 
        + uint(fragCoord.y * iResolution.x) 
        + uint(iTime * iResolution.x * iResolution.y));

    float g = 1.22074408460575947536;
    float a1 = 1.0/g;
    float a2 = 1.0/(g*g);
    float a3 = 1.0/(g*g*g);
    vec3 col;
    col.x = fract(0.5+a1*n);
    col.y = fract(0.5+a2*n);
    col.z = fract(0.5+a3*n);

    fragColor = vec4(col,1.0);
}

#define GRADMAG(f) (sqrt(dFdx(f) * dFdx(f) + dFdy(f) * dFdy(f)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cellDensity = 7.0; // sin(iTime) * 1.0 + 5.0;
    float lineWidth   = 2.0;

    vec2 ms = iMouse.xy;
    if (ms.x < 10.0 && ms.y < 10.0) {
        ms.x = sin(iTime * 0.31) * 0.5 + 0.5;
        ms.y = cos(iTime * 0.34) * 0.5 + 0.5;
        ms *= iResolution.xy;
    }

    vec2 p = (fragCoord - iResolution.xy / 2.0) / iResolution.y;

    p *= cellDensity;
    p.y += 0.5;
  //  p += iTime * vec2(1.0, 0.0);

    p = (fract(p) - 0.5) * 2.0;

    float d = length(fragCoord - ms) / 100.0;
    d = d - length(p);

    // d = 1.0 / (0.0 + d);

    // because we're dividing by fwidth below,
    // d can be scaled by any value here without effect !
    d *= 10000.0;

    float l = smoothstep(0.1, 0.0, abs(d) / GRADMAG(d) / (10.0 * lineWidth));

    fragColor = vec4(vec3(l),1.0);
}

