// Approximate sRGB interpolation by using gamma2 space. When the to and from transform
// are combined some of the sqrt terms cancel.
vec3 color_mix(vec3 a, vec3 b, float t) {
    //return a * (1.0-t)*(1.0-t) + 2 * sqrt(a*b) * t * (1.0-t) + b * t*t;

    float omt = 1.0-t;
    float t2 = t * t;
    vec3 bt2 = b * t2;
    vec3 abt2 = a * bt2;
    return (a * omt + 2.0 * sqrt(abt2)) * omt + bt2;
}

// This is a variation of the above with the square root term removed. No good reason
// to do that, other than making it cheaper, but looks surprisingly OK.
vec3 color_mix2(vec3 a, vec3 b, float t) {
    float omt = 1.0-t;
    return a * omt*omt + b * t*t;
}

vec3 color_mix3(vec3 a, vec3 b, float t) {
    float omt = 1.0-t;
    return a * omt*omt + b * t*t;
}


float linear_srgb(float x) {
    return mix(1.055*pow(x, (1./2.4)) - 0.055, 12.92*x, step(x,(0.0031308)));
