{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float c;
    for(int i = 0; i < 20; i++){
        c += circle(uv,vec2(
            snoise(vec2(iTime*.2, rand(float(i)*123.512) )),
            snoise(vec2(rand(float(i+7890123)),iTime*.2))
        )*.2,(.8+.5*sin(iTime*3.0))*.05,.08);    
    }
    //c*=.5;
    vec3 u_colorMult = vec3(.5,.5,1);
    fragColor = vec4(vec3(c)*u_colorMult.rgb,c);
}

