// Color Appearance Model (or 'reference') white point
const vec3 whiteCam = whiteD50I;

// camMat is an easy way to set the transformation matrix, but both
// toCam and frCam are what should be used, as they're adapted to the
// color appearance model white point
const mat3 camMat = CIECAM97_1;
const mat3 toCam = inverse(diag(camMat*Bright(whiteCam)))*camMat;
const mat3 frCam = inverse(camMat)*diag(camMat*Bright(whiteCam));

// Modify XYZ↔RGB matrices to perform whitepoint adaptation using a
// wrong Von Kries transformation matrix (CIECAM97_1 has nonlinearities
// in the 'S' cone that is not accounted for in ICC profiles)
const mat3 toRgb = xyzToRgb(disp)*frCam*diag((toCam*Bright(disp.white))/(toCam*Bright(whiteCam)))*toCam;
const mat3 toXyz = frCam*diag((toCam*Bright(whiteCam))/(toCam*Bright(disp.white)))*toCam*rgbToXyz(disp);

// This might be easier to understand (← shows matrix multiplication):
// toRgb = XYZtoRGB←LMStoXYZ←diag(whiteDispInLMS/whiteCamInLMS)←XYZtoLMS
// toXyz = LMStoXYZ←diag(whiteCamInLMS/whiteDispInLMS)←XYZtoLMS←RGBtoXYZ


/*
 * Conversion Functions
 */

// Converts display RGB colors to a linear light scale
vec4 toLinear(vec4 color, const transfer trc)
{
	bvec4 cutoff = lessThan(color, vec4(trc.cutoffToLinear));
	vec4 higher = pow((color + trc.off)/(1.0 + trc.off), vec4(trc.power));
	vec4 lower = color/trc.slope;

	color = mix(higher, lower, cutoff);

	return color;
}

// Gamma-corrects RGB colors to be sent to a display
vec4 toGamma(vec4 color, const transfer trc)
{
	bvec4 cutoff = lessThan(color, vec4(trc.cutoffToGamma));
	vec4 higher = (1.0 + trc.off)*pow(color, vec4(1.0/trc.power)) - trc.off;
	vec4 lower = color*trc.slope;

	color = mix(higher, lower, cutoff);

	return color;
}

// Turns colors that are out-of-gamut transparent
vec4 gamutTrim(vec4 color, float luma)
{
	float low = min(color.r, min(color.g, min(color.b, 0.0)));
	float high = max(color.r, max(color.g, max(color.b, 1.0)));

	color.a = float(int(low == 0.0 && high == 1.0));

	return color;
}

// Converts from XYZ to RGB, with white point adaptation
vec4 convert(vec4 color)
{
	float luma = color.y;

	// Convert from XYZ to RGB, then remove out-of-gamut colors
	color.rgb = toRgb*color.xyz;
	color = gamutTrim(color, luma);

	return color;
}


