struct point_light
{
    vec3 p;
    vec3 i;
};

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

vec3 opRep( in vec3 p, in vec3 c)
{
    vec3 q = mod(p,c)-0.5*c;
    return q;
}

float sdf(in vec3 p, out int matid)
{
    vec2 c = vec2(4.5);
	p.xz = mod(p.xz, c) - 0.5 * c;

    float sphere_sdf = length(p - vec3(0, 1, 0)) - 1.0;
    float box_sdf = sdBox(p - vec3(0, 1, 0), vec3(0.8));
    float obj_sdf = max(-sphere_sdf, box_sdf);
    float floor_sdf = abs(p.y);

    float sdf = min(obj_sdf, floor_sdf);
    if (sdf == obj_sdf)
    {
        matid = 1;
    }
    else
    {
        matid = 0;
    }

    return sdf;
}

vec3 sdf_n(in vec3 p)
{
    vec3 res = vec3(0);

    int ignored;
    res.x = sdf(p + vec3(0.001, 0, 0), ignored) - sdf(p, ignored);
    res.y = sdf(p + vec3(0, 0.001, 0), ignored) - sdf(p, ignored);
    res.z = sdf(p + vec3(0, 0, 0.001), ignored) - sdf(p, ignored);

    return normalize(res);
}

float intersect(in vec3 ro, in vec3 rd, out int matid)
{
	float t = 0.0;
    matid = -1;
    for (int i = 0; i < 256; ++i)
    {
        int curr_matid = -1;
		float d = sdf(ro + t*rd, curr_matid);
        if (d < 0.0001)
        {
            matid = curr_matid;
            break;
        }
        t += d;
    }
    return t;
}

float dir_shadow(in vec3 p, in vec3 l)
{
    float t = 0.15;
    float t_max = 20.0;

    float res = 1.0;
    for (int i = 0; i < 256; ++i)
    {
        if (t > t_max) break;

        int ignored;
        float d = sdf(p + t*l, ignored);
        if (d < 0.01)
        {
            return 0.0;
        }
        t += d;
        res = min(res, 8.0 * d / t);
    }

    return res;
}

float point_shadow(in vec3 p, in vec3 light_p)
{
    vec3 l = normalize(light_p - p);

    float t = 0.15;
    float t_max = distance(light_p, p);

    float res = 1.0;
    for (int i = 0; i < 256; ++i)
    {
        if (t > t_max) break;

        int ignored;
        float d = sdf(p + t*l, ignored);
        if (d < 0.01)
        {
            return 0.0;
        }
        t += d;
        res = min(res, 64.0 * d / t);
    }

    return res;
}

float ao(in vec3 p, in vec3 n)
{
    float e = 0.1;
    float res = 0.0;

#define AO_ITER 5

    int ignored;
    float weight = 0.5;
    for (int i = 1; i <= AO_ITER; ++i)
    {
        float d = e * float(i);
        res += weight * (1.0 - (d - sdf(p + d * n, ignored)));
        weight *= 0.5;
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float cam_d = 3.0;
    float time = 0.3 * iTime;
    vec3 ro = vec3(cam_d*sin(time), 2.5, cam_d*cos(time));
    vec3 at = vec3(0, 1, 0);
    vec3 cam_z = normalize(at - ro);
    vec3 cam_x = normalize(cross(vec3(0,1,0), cam_z));
    vec3 cam_y = cross(cam_z, cam_x);
    vec3 rd = normalize(uv.x * cam_x + uv.y * cam_y + 1.73 * cam_z);

    vec3 col = vec3(0);

    int matid = -1;
    float t = intersect(ro, rd, matid);
    if (matid != -1)
    { 
        vec3 p = ro + t*rd;
        vec3 n = sdf_n(p);

#define PLIGHT_COUNT 5 
        point_light plights[PLIGHT_COUNT];
        plights[0] = point_light(vec3(3.0*sin(2.0*iTime), 1, 1.0+cos(iTime)), vec3(1, 0, 0));
        plights[1] = point_light(vec3(3.0*sin(0.5*iTime), 1.0, 3.0*cos(iTime+10.0)), vec3(0, 1, 1));
        plights[2] = point_light(vec3(6.0*cos(1.5*iTime+2.0), 1.0, 3.0*sin(0.2*iTime-4.0)), vec3(1, 1, 0));
        plights[3] = point_light(vec3(3.0*cos(0.2*iTime-1.0), 1.0, 4.0*sin(0.5*iTime)), vec3(1, 0.2, 0.5));
        plights[4] = point_light(vec3(cos(iTime), 1.0, sin(iTime)), vec3(1, 0.4, 1));

        vec3 direct_light = vec3(0);
        for (int plight_index = 0; plight_index < PLIGHT_COUNT; ++plight_index)
        {
            vec3 light_i = plights[plight_index].i;
            vec3 light_p = plights[plight_index].p;
        	float light_r = dot(light_p - p, light_p - p);
        	vec3 l = normalize(light_p - p);

        	direct_light += point_shadow(p, light_p) * max(0.0, dot(n, l)) * light_i / (light_r * light_r);
        }

        vec3 indirect_light = ao(p, n) * vec3(0.3);

        vec3 albedo = vec3(0.9);      
		col = albedo * (0.7 * direct_light + indirect_light);

        col = mix(col, vec3(0), clamp(pow(t / 30.0, 2.0), 0.0, 1.0));
    }

    fragColor = vec4(col, 1.0);
}

float sphere2( vec2 p, float radius )
{
    return max( 0.0, radius - length( p ) );
}

#define mod3_      vec3(.1031, .11369, .13787)

vec3 hash3(vec3 p3) {
  p3 = fract(p3 * mod3_);
    p3 += dot(p3, p3.yxz + 19.19);
    return -1. + 2. * fract(vec3((p3.x + p3.y) * p3.z, (p3.x+p3.z) * p3.y, (p3.y+p3.z) * p3.x));
}

float perlin_noise3(vec3 p) {
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3. - 2. * pf);

    return   mix(
      mix(
            mix(
                dot(pf - vec3(0, 0, 0), hash3(pi + vec3(0, 0, 0))), 
                dot(pf - vec3(1, 0, 0), hash3(pi + vec3(1, 0, 0))),
                w.x),
            mix(
                dot(pf - vec3(0, 0, 1), hash3(pi + vec3(0, 0, 1))), 
                dot(pf - vec3(1, 0, 1), hash3(pi + vec3(1, 0, 1))),
                w.x),
      w.z),
        mix(
            mix(
                dot(pf - vec3(0, 1, 0), hash3(pi + vec3(0, 1, 0))), 
                dot(pf - vec3(1, 1, 0), hash3(pi + vec3(1, 1, 0))),
                w.x),
            mix(
                dot(pf - vec3(0, 1, 1), hash3(pi + vec3(0, 1, 1))), 
                dot(pf - vec3(1, 1, 1), hash3(pi + vec3(1, 1, 1))),
                w.x),
       w.z),
  w.y);
}

float noise_sum_abs3(vec3 p) {
    float f = 0.;
    p = p * 3.;
    f += 1.0000 * abs(perlin_noise3(p)); p = 2. * p;
    f += 0.5000 * abs(perlin_noise3(p)); p = 3. * p;
  f += 0.2500 * abs(perlin_noise3(p)); p = 4. * p;
  f += 0.1250 * abs(perlin_noise3(p)); p = 5. * p;
  f += 0.0625 * abs(perlin_noise3(p)); p = 6. * p;

    return f;
}

vec3 ligtning( vec2 fragCoord, vec2 uv )
{
    vec3 p3 = vec3(
        fragCoord.xy/iResolution.x,
        iTime*0.04
    );

    float noise = noise_sum_abs3(vec3(p3*12.0+12.0));

    float t = clamp((uv.x * -uv.x * 0.16) + 0.15, 0.0, 1.);
    float l = uv.y; //mix( abs( uv.y ), 0.05, abs( uv.x ) );
  //  l =  ( smoothstep( -0.7, 0.0, uv.x ) * (
  //        1.0 - smoothstep( 0.0, 0.7, uv.x ) ) ) * abs( uv.y );

    float r = 0.6;
     l = sphere2( uv, r );


    float i = pow( abs( noise * -t  + l ), 0.3 );

    vec3 col = vec3(1.8, 1.60, 1.8);

    col = col * -i + col;                    
  //  col = col * col;
    col = col * col;

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * uv - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float pulsSpeed = 2.0;
    float r = 1.0;

    float val = pow( sphere2( uv, 1.0 ) * 1.4, 2.2 );

    val = val + val * abs( sin( pow( length( uv ), 0.04 ) * 10.0 - iTime*pulsSpeed ) );

    vec3 color = vec3( 0.6, 1.3, 1.5 );


    fragColor.xyz = color * val;
    fragColor.xyz *= ligtning( fragCoord, uv );
    fragColor.xyz += color * val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const bool lines = true;
    int scale = 3;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(fragCoord.x/(iResolution.x / 3.0), fragCoord.y/(iResolution.y / 2.0));

    float speed = 32.0; //divisor

    // Time varying pixel color    
    float r = 20.0 + 64.0*cos(sin(iTime/(speed * 2.0))+uv.x) + 64.0*sin(cos(iTime/(speed / 2.0))+uv.y);
    float g = 40.0 + 50.0*sin(sin(iTime/(speed))+uv.x) + 50.0*cos(cos(iTime/(speed))+uv.y);
    float b = 60.0 + 64.0*sin(sin(iTime/(speed / 2.0))+uv.x) + 64.0*sin(cos(iTime/(speed * 2.0))+uv.y);


    if(lines) {

    if(int(r) % scale == 0)
    {
        r = 0.0;
    }

    if(int(g) % scale == 0)
    {
        g = 0.0;
    }

    if(int(b) % scale == 0)
    {
        b = 0.0;
    }

    }

    vec3 col = vec3(r/255.0, g/255.0, b/255.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

