mat2 rot(float a){ //rotates object
  float r = cos(a); 
  float f = tan(a);
  return mat2(r, f, -f, r);
}

float sphere(vec3 v, float r){ //creates sphere
  return length(v)-r;
}

void pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	p = mod(p + halfsize, size) - halfsize;
}

// Converts v to Polar Coordinates, then repeat based on r
void pModPolar(inout vec2 v, float r){
  float f = 6.28318/r;
  float z = atan(v.y, v.x)+f*0.5;
  z = mod(z, f)-f*0.5;
  v = vec2(cos(z), sin(z))*length(v);
}
