Shape sun(vec3 c){
  Shape shape;
  float s;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color


  vec3 i = c;

    i.xy *= rot(c.z*0.1+iTime*0.5); // Twist Coord System
    pModPolar(i.xy, 15.); // Twist Repeat
    i.x -= abs(sin(iTime*10.))*1.; // Offset from origin
    pMod1(i.x, 3.);
  	s = sphere(i, 1.4); // Create sphere

    // Plug-in distance to object and color
    vec4 color = vec4(.9, 0.9, 0.2, 1.); //yellow color for sun
  	shape.dist = s;
  	shape.color = color;

  return shape;
}


// Map all objects within scene
Shape map(vec3 c){
  Shape sun = sun(c);
  return sun;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(0., 0., -100.); // Setup Camera, zoom out
  vec3 f = normalize(vec3(v, 1.)); // Frustrum
  vec3 scene = cam;
  fragColor = vec4(0.); // Background Color

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist; // Step to SDF


  }
}

