float map(vec3 p)
{
    float radius = 0.05;

    // Transform coordinate space so spheres repeat
    vec3 q = fract(p)*0.45 -0.37;

    // Signed distance of sphere
    return sphere(q, radius);
}


float trace(vec3 origin, vec3 r) 
{
  float t = 0.0;
    for (int i = 0; i < 75; ++i) {
        vec3 p = origin +r *t ;
        float d = map(p);
        t += d;
        }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv*3.0- 1.5; // Remap the space to -1. to 1.
    uv.x *= iResolution.x/iResolution.y;   

   	float FOV = 1.0;
   	vec3 ray = normalize(vec3(uv, FOV));

    // Rotate Y towards Z
    //ray.yz *= -rot(iTime*0.1);    

    vec3 origin = vec3(random(uv), tan(random(uv))*8.+iTime *2., 0.0);
    float t = trace(origin, ray);

    float linearFog = 2.0 / (1.0 - t*0.2);
    float expFog = 3.0 / (t*t*0.1);

    vec3 color = vec3(0.1, 0.1, 1.0);

    vec3 fc = vec3(expFog)*color;
    //vec3 fc = vec3(expFog) / cos(color)- color*0.7

    // Output pixels to screen
    fragColor = vec4(fc,2.0);


}

