Shape character(vec3 c){
  Shape shape;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  vec3 p = c; // Body
  vec3 e = c; // Eyes
  vec3 a = c; // Arms
  vec3 m = c; // Mouth
  vec3 h = c; //


    // Body
    float body = fBox(p, vec3(1.));

    // Eyes
    e.x = abs(e.x)-1.; // Mirror
    float eye = fBox(e+vec3(0.5, -0.2, 1.0), 
                     vec3(0.25, 0.25, 0.5));
    float pupil = sphere(e + vec3(0.475, -0.15, .5 + abs(sin(iTime))), 0.2);
    // Arms
    a.x = abs(a.x)-2.3;
    float arm = fBox(a+vec3(1.1, 0.2, 0.85),
                     vec3(0.25, 0.5, 0.25));


    // Mouth
    float mouth = fBox(m+vec3(0., 0.5, 0.75),
                      vec3(0.5, 0.1, 0.5));
    // Hat
    float hat = sphere(h - vec3(.0, 1.25, 0.), .5);
    h.yz *= rot(radians(90.));
    h.xy *= rot(iTime*.5);
    pModPolar(h.yx, 20.);
    h.y /= cos(sin(c.z));
   	float spin = fBox(h - vec3(.0, 1.5, -1.3), 
                      vec3(.1, 1., .05));
    spin -= cos(sin(c.x *.5));
    //
    shape.dist = max(body, -eye); // Difference
    shape.dist = min(shape.dist, arm);
    shape.dist = max(shape.dist, -mouth);
    shape.dist = fOpUnionColumns(shape.dist, hat, .5, 3.);
    shape.dist = min(shape.dist, spin);
    shape.dist = fOpUnionColumns(shape.dist, pupil, .45, 5.);
    //shape.dist = min(body, eye); // Union
    //shape.dist = max(body, eye); // Intersect

  return shape;
}


Shape map(vec3 c){
  Shape face = character(c);
  return face;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;


  vec3 cam = vec3(0., 0., -5.);
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
    scene.yx *= rot(iTime);
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.00001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}

/**
 * Part 3 Challenges
 * - Make the camera move up and down while still pointing at the cube
 * - Make the camera roll (stay looking at the cube, and don't change the eye point)
 * - Make the camera zoom in and out
 */

const int MAX_MARCHING_STEPS = 1024;
const float MIN_DIST = 0.0;
const float MAX_DIST = 1000.0;
const float EPSILON = 0.0001;
const float PI = 3.1415926535897932384626433832795;

/**
 * Signed distance function for a cube centered at the origin
 * with width = height = length = 2.0
 */
float cubeSDF(vec3 p) {
    // If d.x < 0, then -1 < p.x < 1, and same logic applies to p.y, p.z
    // So if all components of d are negative, then p is inside the unit cube
    vec3 d = abs(p) - vec3(1.0, 1.0, 1.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float sphereSDF(vec3 p) {
    return length(p) - 5.0;
}

float torusSDF( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float myAtan(float z, float x) {
    // return of (x, y) from 0 (inclusive) up to 2 pi
    if (x > 0.0 && z >= 0.0) {
        return atan(z, x);
    } else if (x == 0.0 && z >= 0.0) {
        return PI / 2.0;
    } else if (x < 0.0 && z >= 0.0) {
        return PI - atan(z, -x);
    } else if (x > 0.0 && z < 0.0) {
        return 2.0*PI - atan(-z, x);
    } else if (x == 0.0 && z < 0.0) {
        return 3.0 * PI / 2.0;
    } else if (x < 0.0 && z < 0.0) {
        return PI + atan(-z, -x);
    }
}

float twistyTorusSDF(vec3 p, vec2 t) {
    float shift = (myAtan(p.z, p.x)) / (2.0 * PI);
    vec3 new_p = p;
    new_p.y = mod(p.y, 5.0);
    return torusSDF(new_p - vec3(0, shift * 5.0, 0), t);
}


float groundSDF(vec3 p, float level) {
    return (p.y - level);
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 p) {
    return twistyTorusSDF(p, vec2(2.0, 0.5));
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the eye to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += 0.1*dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

/**
Returns a number to multiply the illumination by.
k is softness of shadows
*/
float softShadowMarch(vec3 eye, vec3 marchingDirection, float start, float end, float k) {
    float depth = start;
    float res = 1.0;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return 0.0;
        }
         depth += 0.1*dist;
        //res = min(res, k*dist/depth);
        if (depth >= end) {
            return res;
        }
    }
    return res;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    float lightToPointDist = length(p - lightPos);
    float res = softShadowMarch(lightPos, normalize(p - lightPos),
                                EPSILON, lightToPointDist - 22.0*EPSILON, 50.0);
	res = 1.0;

    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return res * lightIntensity * (k_d * dotLN);
    }
    return res * lightIntensity * (k_d * dotLN + 0.0 * k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(10.0 * sin(1.8*iTime),
                          9.0,
                          8.0 * cos(1.8*iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    /*
    vec3 light2Pos = vec3(10.0 * sin(1.3 * iTime),
                          15.0 * cos(1.3 * iTime),
                          8.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);
*/

    //vec3 light3Pos = vec3(0,1.0,0.0);
    //vec3 light3Intensity = vec3(5, 1, 0);

    //color += phongContribForLight(k_d, k_s, alpha, p, eye,
                            //      light3Pos,
                           //       light3Intensity); 
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(27.0 * sin(0.5 * iTime), 14.0, 27.0 * cos(0.5 * iTime));

    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 2.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    vec3 K_a = vec3(1.2, 1.2, 1.4);
    vec3 K_d = vec3(0.8, 0.8, 0.8);
    vec3 K_s = vec3(0.1, 0.1, 0.1);
    float shininess = 2.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

#define ITERS 1000.
#define RADIUS 256.
#define SCALE 1./256.
#define C vec2(0.004*sin(iTime*0.5), 0.001*cos(iTime*0.4))
#define STEPS 5

vec3 palette[STEPS];

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

float mandelbrot(vec2 st) {
    float iter = 0., maxIter = ITERS;
    vec2 s = st;
    while ((st.x * st.x + st.y * st.y) < RADIUS && iter < maxIter) {
        float x = st.x;
        st.x = x * x - st.y * st.y + s.x;
        st.y = 2.*x*st.y + s.y;
        st += C;
        iter += 1.;
    }
    return iter;
}

void buildPalette() {
    palette[0] = vec3(0., 0.004, 0.0);
    palette[1] = vec3(0.055, 0.227, 0.153);
    palette[2] = vec3(0.173, 0.294, 0.075);
    palette[3] = vec3(0.278, 0.318, 0.078);
    palette[4] = vec3(0.49, 0.706, 0.612);
}

vec3 palletize(float c) {
    float stp = 1.0/float(STEPS);
    vec3 col = mix(palette[0], palette[1], smoothstep(0., stp, c));
    for (int i = 1; i < STEPS; i++) {
        float weight = smoothstep(stp*(float(i) + 1.), stp*(float(i)  + 2.), c);
        col = mix(col, palette[i], weight);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    buildPalette();
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //start in the mandelbrot space
	uv.x *= 3.5;
    uv.x -= 2.5;
    uv.y *= 2.;
    uv.y -= 1.;
    //find your spot in the fractal
    uv *= scale(vec2(SCALE, SCALE));
    uv -= vec2(0.835, 0.203);

    //draw it
    float c = 1. - mandelbrot(uv) / ITERS;
    vec3 col = palletize(c);

    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));	
}

float deLine(vec3 p, vec3 v, vec3 c)
{
	return length(cross(p - c, normalize(v)))-0.05;
}

float dePoint(vec3 p, vec3 c)
{
	return length(p - c) -0.2;
}

float deCircle(vec3 p, vec3 c, vec3 n, float r)
{
	p -= c;
	return max(abs(dot(p, n))-0.01, abs(length(cross(p, n))-r))-0.05;
}

#define Info \
    vec3 axis = normalize(vec3(1,5,1));\
    vec3 ro = vec3(-1,sin(iTime), 8.0+4.0*sin(iTime));\
    vec3 rd = normalize(vec3(1,0,3));\
    vec3 o = vec3(0);\
    vec3 u = normalize(cross(ro,rd));\
    vec3 w = normalize(cross(u,rd));\
    float l = dot(ro,w);\
    float r =5.0;\
    vec3 v = w*l;\
    vec3 a = v -sqrt(r*r-l*l)*rd;\
    vec3 b = v +sqrt(r*r-l*l)*rd;

#define V_axis(p) deLine(p,axis,o)
#define V_ro(p) deLine(p,ro,o)
#define V_rd(p) deLine(p,rd,ro)
#define V_u(p) deLine(p,u,ro)
#define V_w(p) deLine(p,w,o)
#define P_o(p) dePoint(p,o)
#define P_ro(p) dePoint(p,ro)
#define P_v(p) dePoint(p,v)
#define P_a(p) dePoint(p,a)
#define P_b(p) dePoint(p,b)
#define C_axis(p) deCircle(p,o,axis,r)
#define C_a(p) deCircle(p,o,normalize(cross(a,axis)),r)
#define C_b(p) deCircle(p,o,normalize(cross(b,axis)),r)

#define Color(a,b) if (a(p)<0.01) return b;
#define R vec3(1,0,0)
#define G vec3(0,1,0)
#define B vec3(0,0,1)

vec3 doColor(vec3 p)
{
    Info
	Color(V_axis, R)
	Color(V_axis, G)
	Color(V_ro, G)
	Color(V_rd, vec3(1,0.5,0))
	Color(V_u, G)
	Color(V_w, G)
	Color(P_o, G)
	Color(P_ro, vec3(1))
	Color(P_v, B)
	Color(P_a, R)
	Color(P_b, R)
	Color(C_axis, B)
	Color(C_a, B)
	Color(C_b, B)

	return vec3(1);
}

#define De(a) de = min(de, a(p)); 
float map(vec3 p)
{
    Info
	float de = 1.0;
	De(V_axis)
	De(V_ro)
	De(V_rd)
	De(V_u)
	De(V_w)
	De(P_o)
	De(P_ro)
	De(P_v)
	De(P_a)
	De(P_b)
	De(C_axis)
	De(C_a)
	De(C_b)
	return de;
}

mat3 lookat(vec3 eye, vec3 target, vec3 up)
{
    vec3 w = normalize(target-eye), u = normalize(cross(w,up));
    return mat3(u,cross(u,w),w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 d = normalize(vec3((fragCoord * 2.0 - iResolution.xy) / iResolution.y,2));
 	vec3 p = vec3(0,5,18);
	p.xz = rotate(iTime*0.5) * p.xz;
	vec3 ta = vec3(0);
	d = lookat(p,ta,vec3(0,1,0)) * d;
	float t=0., x;
	vec3 col = vec3(0.2);
	for (float i=1.; i>0.; i-=.01) 
  	{
		t +=x =map(p);
		if(x < 0.01){
			col = i*i*i*(1.0+doColor(p));
			break;
		}
		p+=d*x;
	}
    col = pow(col, vec3(2));
    fragColor = vec4(col, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
