float line(vec2 uv, float m, float b, float thickness, float fuzz) {
    float y = m * uv.x + b;
    float d = thickness - distance(uv, vec2(uv.x, y));
    d =  smoothstep(0., thickness, d);
    if (uv.y >= y - thickness && uv.y <= y + thickness) {
        return mix(1., d, fuzz);
    }
    return 0.;
}

float line(vec2 uv, float m, float b) {
    return line(uv, m, b, iResolution.y / 100000., 0.);
}

float line(vec2 uv, float m, float b, float thickness) {
    return line(uv, m, b, thickness, 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float v = 0.;
    float slopMod = 20.0 * smoothstep(-1., 1., sin(iTime * 0.25));
    for (float i = 0.; i < 250.; i++) {
    	v += line(uv, i / slopMod, 0., 2./iResolution.y, 1.);
        v += line(uv, -i / slopMod, 1., 2./iResolution.y, 1.);
    }
    vec3 col = vec3(v);

    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float line(vec2 uv, float m, float b, float thickness, float fuzz) {
