void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv += vec2(-0.5, -0.5);
    uv = rotate2d(iTime) * uv;
    vec3 point = vec3(
        line(uv, 0., 0., 1./iResolution.y, 0.25)
    );
    for (float i = -10.; i < 10.; i+=1.) {
        uv += vec2(0., (1.0 / 1000.)*i);
        uv *= rotate2d(sin(iTime*0.125*i));
        point += vec3(
            line(uv, 0., 0., 0.5/iResolution.y, 0.25)
        );
    }

    // Output to screen
    fragColor = vec4(point,1.0);
}

