	*/
    //vec2 p1 = vec2 (50, 50);
    /*vec2 p1 = iMouse.xy;
   	vec2 p2 = vec2 (300, 80);*/
    vec2 p1 = vec2 (.5 + .3 * cos (iTime * .5), .5 + .3 * sin (iTime * .7)) * iResolution.xy;
   	vec2 p2 = vec2 (.5 + .2 * cos (iTime * 1.1), .5 + .2 * sin (iTime * .9)) * iResolution.xy;

    vec4 colA = vec4(clamp (5.0 - length (fragCoord - p1), 0.0, 1.0));
    vec4 colB = vec4(clamp (5.0 - length (fragCoord - p2), 0.0, 1.0));

    vec2 a_p1 = fragCoord - p1;
