    vec4 colC = clamp (vec4(.5 - 5.0 * cos ((d - iTime * 10.0) * 3.1415926 / 20.0)), 0.0, 1.0) * vec4 (1.0, .73, .0, .0);
    vec4 colD = vec4(clamp (2.5 - d, 0.0, 1.0));

    fragColor = max (max (colA, colB), max (colC, colD));
