  r = 20. - 0.7 * q.y / h;
  s = 0.2 * (Noisefv2 (4. * q.xz) - 1.);
  d = 0.9 * max (SmoothMin (PrRoundCylDf (q.xzy, r * (1. + 0.1 * s), h + s, h + s),
     PrCylDf (q.xzy, r + 0.5, 0.01), 0.5), 0.01 - q.y);
  DMINQ (idMnt);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  vec2 cs;
  float dMin, d, da, db, wr;
  dMin = dstFar;
  dMin = MountDf (p, dMin);
  q = p;
  d = PrBoxDf (q, vec3 (8.6, 0.101, 12.6));
  q.y -= 0.3;
  d = max (min (d, PrBoxDf (q, vec3 (8.2, 0.201, 12.2))),
     - PrBoxDf (q, vec3 (2., 0.25, 6.)));
  DMINQ (idBase);
  q.y -= 5.52;
  d = max (PrBoxDf (q, vec3 (7.5, 0.05, 11.5)),
     - PrBoxDf (q, vec3 (2.5, 5., 6.5)));
  q.xz = mod (q.xz + vec2 (1.), 2.) - 1.;
  d = max (d, - PrBoxDf (q, vec3 (0.5, 5., 0.5)));
  DMINQ (idGal);
  q = p;  q.y -= 1.;
  db = max (PrBoxDf (q, vec3 (8., 4.9, 12.)), - PrBox2Df (q.xz, vec2 (2., 6.)));
  q = p;  q.xz = mod (q.xz, 2.) - 1.;  q.y -= 3.14;
  wr = q.y / 2.36;
  d = max (PrCylDf (q.xzy, 0.3 * (1.05 - 0.05 * wr * wr), 2.36), db);
  DMINQ (idCol);
  q = p;  q.xz = mod (q.xz, 2.) - 1.;  q.y = abs (q.y - 3.14) - 2.43;
  d = PrCylDf (q.xzy, 0.4, 0.07);
  q.y -= 0.14;
  d = max (min (d, PrBoxDf (q, vec3 (0.5, 0.07, 0.5))), db);
  DMINQ (idColEnd);
  q = p;  q.x = abs (q.x) - 3.;  q.y -= 8.2;
  q.xy = Rot2D (q.xy, 0.15 * pi);
  d = PrBoxDf (q, vec3 (6., 0.07, 12.3));
  q.x += 0.25;
  q.xz = mod (q.xz, vec2 (1., 2.)) - vec2 (0.5, 1.);
  d = max (d, - PrBox2Df (q.xz, vec2 (0.25, 0.5)));
  DMINQ (idRoof);
  q = p;  q.xz = abs (q.xz);  q -= vec3 (4.1, 7.68, 11.6);
  d = PrBoxDf (q, vec3 (4.3, 1.9, 0.1));
  q.xy = Rot2D (q.xy, 0.15 * pi);
  q.xy -= vec2 (-0.4, -2.);
  d = max (d, PrBoxDf (q, vec3 (4.3, 1.9, 0.1)));
  q = p;  q.xz = abs (q.xz);  q -= vec3 (2.89, 7.7, 11.6);
  da = PrBoxDf (q, vec3 (3.2, 1.4, 1.));
  q.xy = Rot2D (q.xy, 0.15 * pi);
  q.xy -= vec2 (-0.25, -1.5);
  d = max (d, - max (da, PrBoxDf (q, vec3 (3.2, 1.4, 1.))));
  DMINQ (idRoofV);
  q = p;  q.y -= 7.7;  q.z = abs (q.z) - 11.6;
  d = PrCylDf (q.xzy, 0.09, 1.4);
  DMINQ (idRoofV);
  q.y -= 0.4;
  d = PrSphDf (q, 0.4);
  DMINQ (idBall);
  q = p;  q.xz = abs (q.xz);  q -= vec3 (8.5, 0.6, 12.5);
  d = PrCylDf (q.xzy, 0.05, 0.5);
  DMINQ (idPost);
  q.y -= 0.7;
  d = PrSphDf (q, 0.2);
  DMINQ (idBall);
  q = p;  q.y -= 1.5;
  d = min (max (max (PrSphDf (q, 0.78), -0.01 + q.y), -0.3 - q.y),
     PrCylDf ((q - vec3 (0., -0.7, 0.)).xzy, 0.15, 0.42));
  DMINQ (idAltr);
  qq = p;  qq.y -= fCylPos.y - fCylLen + 0.09;
  d = PrCylDf (qq.xzy, fCylRad, 0.1);
  if (d < 0.05) {
    qq.xz = Rot2D (qq.xz, -0.5 * pi);
    cs = sin (pi * vec2 (1.3, 0.8));
    for (int j = 0; j < 5; j ++) {
      qq.xz = Rot2Cs (qq.xz, cs);
      q = qq;  q.x += 0.21;
      d = PrRoundCylDf (q, 0.05 - 0.01 * sin (10. * pi * q.z), 0.02, 0.666);
      DMINQ (idLogs);
    }
    q = p;  q.y -= fCylPos.y - fCylLen - 0.02;
    d = PrCylDf (q.xzy, fCylRad- 0.03, 0.01);
    DMINQ (idCoal);
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd, float dLight)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dLight) break;
  }
  return 0.5 + 0.5 * sh;
}

float BlkHit (vec3 ro, vec3 rd, vec3 blkSize)
{
  vec3 v, tm, tp, u;
  float dn, df;
  rd += 0.00001;
  v = ro / rd;
  tp = blkSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  return (df > 0. && dn < df) ? dn : dstFar;
}

vec2 CylHit (vec3 ro, vec3 rd, float cylRad, float cylHt)
{
  vec3 s;
  float dCylIn, dCylOut, a, ai, b, w, ws, srdy;
  dCylIn = dstFar;
  dCylOut = dstFar;
  rd += 0.00001;
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - cylRad * cylRad);
  if (w > 0.) {
    ws = sqrt (w);
    srdy = sign (rd.y);
    if (a > 0.) {
      ai =  1. / a;
      dCylIn = (- b - ws) * ai;
      dCylOut = (- b + ws) * ai;
    }
    if (a > 0.) s = ro + dCylIn * rd;
    else s.y = cylHt;
    if (abs (s.y) > cylHt) {
      if (srdy * ro.y < - cylHt) {
        dCylIn = - (srdy * ro.y + cylHt) / abs (rd.y);
        if (length (ro.xz + dCylIn * rd.xz) > cylRad) dCylIn = dstFar;
      } else dCylIn = dstFar;
    }
    if (dCylIn < dstFar) {
      if (a > 0.) s = ro + dCylOut * rd;
      else s.y = cylHt;
      if (abs (s.y) > cylHt && srdy * ro.y < cylHt)
         dCylOut = (- srdy * ro.y + cylHt) / abs (rd.y);
    }
  }
  return vec2 (dCylIn, dCylOut);
}

float FlmAmp (vec3 ro, vec3 rd, vec2 dst)
{
  vec3 p, q;
  float fh, fr, aSum, a, d;
  const float ns = 24.;
  p = ro + dst.x * rd;
  d = dst.x + fCylRad / ns;
  aSum = 0.;
  for (float j = 0.; j < ns; j ++) {
    p = ro + d * rd;
    fr = 1. - length (p.xz) / fCylRad;
    fh = 0.5 * (1. - p.y / fCylLen);
    q = 2. * p;
    q.xz = Rot2D (q.xz, 0.3 * q.y);
    a = 1.1 * Fbm3 (q - vec3 (0., 4. * tCur, 0.));
    q = 5. * p;
    q.xz = Rot2D (q.xz, -0.4 * q.y);
    a += 0.9 * Fbm3 (q - vec3 (0., 5. * tCur, 0.));
    aSum += max (0.3 * fr * fr * fh * (a * a - 0.6), 0.);
    q = 73. * p;
    aSum += step (0.85, Fbm3 (q - vec3 (0., 16. * tCur, 0.))) * smoothstep (0.1, 0.2, fr) *
       smoothstep (0.3, 0.4, fh);
    d += fCylRad / ns;
    if (d > dst.y || aSum > 1.) break;
  }
  return clamp (aSum, 0., 1.);
}

vec4 GrndCol (vec2 p)
{
  return vec4 (mix (vec3 (0.22, 0.2, 0.2), vec3 (0.2, 0.2, 0.22),
     smoothstep (0.3, 0.7, Fbm2 (4. * p))), 0.);
}

vec4 ObjCol ()
{
  vec4 col;
  vec2 vc;
  vc = vec2 (0.);
  if (idObj == idMnt) {
    col = GrndCol (qHit.xz) * (1. + 0.2 * smoothstep (0.5, 0.9 * grDep, qHit.y));
  } else if (idObj == idBase) {
    col = vec4 (0.9, 0.9, 0.95, 0.1);
    vc = vec2 (0.5, 32.);
  } else if (idObj == idCol) {
    col = vec4 (0.9, 0.9, 0.7, 0.1) * (1. - 0.3 * Fbm2 (128. * vec2 (qHit.y, 4. * angHit))) *
     (0.6 + 0.4 * SmoothBump (0.02, 0.98, 0.02, mod (8. * qHit.y / 2.36, 1.)));
  } else if (idObj == idColEnd) {
    col = vec4 (0.9, 0.9, 0.7, 0.1);
    vc = vec2 (0.3, 128.);
  } else if (idObj == idRoof) {
    col = vec4 (0.8, 0.3, 0.1, 0.1) *
       (1. - 0.4 * SmoothBump (0.98, 1.02, 0.01, mod (qHit.z, 2.))) *
       (1. - 0.4 * SmoothBump (0.48, 0.52, 0.01, mod (qHit.x, 1.)));
  } else if (idObj == idRoofV) {
    col = vec4 (0.8, 0.3, 0.1, 0.1);
  } else if (idObj == idGal) {
    col = vec4 (0.9, 0.9, 0.7, 0.1);
    vc = vec2 (0.3, 128.);
  } else if (idObj == idPost) {
    col = vec4 (0.8, 0.1, 0.1, 0.1);
  } else if (idObj == idAltr) {
    col = vec4 (0.3, 0.3, 0.9, 0.2);
    if (qHit.y < -0.4) col = mix (col, vec4 (0.5, 0., 0., -1.), SmoothBump (0.3, 0.6, 0.05,
       0.5 + 0.5 * sin (2. * pi * (4. * qHit.y - 0.5 * tCur))));
  } else if (idObj == idBall) {
    col = vec4 (0.2, 0.5, 0.3, -1.);
  }
  if (vc.x > 0.) col *= 1. - vc.x * Fbm3 (vc.y * qHit);
  return col;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, rds, mDir, vn, p;
  float mRad, bs, ts, ds, fd, att, attSum, d, sd;
  if (isNt) {
    mDir = normalize (vec3 (0., 0.04, -1.));
    mRad = 0.02;
    col = vec3 (0.02, 0.02, 0.04) + vec3 (0.06, 0.04, 0.02) *
       pow (clamp (dot (rd, mDir), 0., 1.), 16.);
    bs = dot (rd, mDir);
    ts = bs * bs - dot (mDir, mDir) + mRad * mRad;
    if (ts > 0.) {
      ts = bs - sqrt (ts);
      if (ts > 0.) {
        vn = normalize ((ts * rd - mDir) / mRad);
        col += 0.8 * vec3 (1., 0.9, 0.5) * clamp (dot (vec3 (-0.77, 0.4, 0.5), vn) *
           (1. - 0.3 * Noisefv3 (4. * vn)), 0., 1.);
      }
    }
    if (ts < 0.) {
      rds = floor (2000. * rd);
      rds = 0.00015 * rds + 0.1 * Noisefv3 (0.0005 * rds.yzx);
      for (int j = 0; j < 19; j ++) rds = abs (rds) / dot (rds, rds) - 0.9;
      col += 0.5 * smoothstep (0.01, 0.04, rd.y) * vec3 (0.8, 0.8, 0.6) *
         min (1., 0.5e-3 * pow (min (6., length (rds)), 5.));
    }
  } else {
    p = ro + rd * (200. - ro.y) / rd.y;
    ds = 0.1 * sqrt (length (ro - p));
    fd = 0.001 / (smoothstep (0., 10., ds) + 0.1);
    p.xz *= fd;
    p.xz += 0.05 * tCur;
    p *= 3.;
    att = Fbm2 (p.xz);
    attSum = att;
    fd *= 3.;
    d = fd;
    ds *= fd;
    for (int j = 0; j < 4; j ++) {
      attSum += Fbm2 (p.xz + d * sunDir.xz);
      d += ds;
    }
    sd = clamp (dot (sunDir, rd), 0., 1.);
    col = mix (vec3 (0.5, 0.75, 1.), mix (vec3 (0.7, 1., 1.), vec3 (1., 0.4, 0.1),
       0.25 + 0.75 * sd), exp (-2. * (3. - sd) * max (rd.y - 0.1, 0.))) +
       0.3 * (vec3 (1., 0.4, 0.2) * pow (sd, 256.) + vec3 (1., 0.8, 0.7) * pow (sd, 1024.));
    attSum = 1. - smoothstep (1., 9., attSum);
    col = mix (vec3 (0.4, 0., 0.2), mix (col, vec3 (0.2),
       att * (0.001 + 0.999 * smoothstep (0.001, 0.004, rd.y))), attSum) +
       vec3 (1., 0.4, 0.) * pow (attSum * att, 4.) * (pow (sd, 8.) + 0.5);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 row, rdw, rdo, foVec, col, vn, vnw;
  vec2 dstFlm, vf;
  float dstObj, dstWat, dstGrnd, fIntens, lDist, sh, a, f;
  int idObjF;
  bool flmRefl, objRefl, hideFlm, ltSun;
  grDep = 3.;
  fCylPos = vec3 (0., 3.53, 0.);
  fCylRad = 0.8;
  fCylLen = 2.;
  dstFlm = CylHit (ro - fCylPos, rd, fCylRad, fCylLen);
  dstWat = BlkHit (ro - vec3 (0., 0.4, 0.), rd, vec3 (2., 0.01, 6.));
  row = ro + dstWat * rd;
  vnw = VaryNf (0.4 * row + 0.1 * tCur, vec3 (0., 1., 0.), 0.1);
  flmRefl = false;
  if (dstWat < min (dstFlm.x, dstFar)) {
    rdw = reflect (rd, vnw);
    row += 0.01 * rdw;
    dstFlm = CylHit (row - fCylPos, rdw, fCylRad, fCylLen);
    flmRefl = true;
  } else {
    row = ro;
    rdw = rd;
  }
  fIntens = (dstFlm.x < dstFar) ? FlmAmp (row - fCylPos, rdw, dstFlm) : 0.;
  flmFlkr = Noiseff (tCur * 64.);
  dstObj = ObjRay (ro, rd);
  objRefl = false;
  rdo = rd;
  if (dstWat < min (dstObj, dstFar)) {
    ro += dstWat * rd;
    rd = reflect (rd, vnw);
    dstObj = ObjRay (ro, rd);
    objRefl = true;
  }
  idObjF = idObj;
  ltSun = false;
  sh = 1.;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idCol) {
      angHit = atan (qHit.z, - qHit.x) / (2. * pi);
      vn.xz = Rot2D (vn.xz, -0.15 * pi * sin (pi * (0.5 - mod (12. * (angHit + 0.5), 1.))));
    }
    vf = vec2 (0.);
    if (idObj == idCol || idObj == idColEnd) vf = vec2 (16., 0.3);
    else if (idObj == idAltr) vf = vec2 (8., 1.);
    else if (idObj == idBase) vf = vec2 (16., 1.);
    else if (idObj == idRoof) vf = vec2 (32., 0.5);
    else if (idObj == idMnt) vf = mix (vec2 (4., 2.), vec2 (16., 4.), smoothstep (0., 0.05, qHit.y));
    if (vf.x > 0.) vn = VaryNf (vf.x * qHit, vn, vf.y);
    if (idObj == idLogs || idObj == idCoal) {
       f = clamp (1.2 * Fbm3 ((idObj == idLogs) ? 32. * vec3 (qHit.z,
          atan (qHit.y, - qHit.x) / (2. * pi), 2. * length (qHit.xy) - 0.03 * tCur) :
          vec3 (64. * qHit.xz, qHit.y + 0.5 * tCur).xzy) - 0.2, 0.1, 1.);
       col = (idObj == idLogs) ? vec3 (1., 0.7 * f, 0.3 * f * f) * (0.5 +
          0.5 * max (- dot (rd, VaryNf (4. * qHit, vn, 1.)), 0.)) *
          (1. - 0.5 * smoothstep (0.5, 0.666, abs (qHit.z))) :
          f * vec3 (1., 0.2, 0.1) * (1. - 0.5 * pow (length (qHit.xz) / fCylRad, 4.));
       col = min (3. * f * col * (1. + 0.1 * flmFlkr), 1.);
    } else {
      col4 = ObjCol ();
      if (col4.a < 0.) {
        col = col4.rgb * (0.2 + 0.8 * max (- dot (rd, vn), 0.));
      } else {
        if (isNt) {
          foVec = fCylPos - ro;
          lDist = length (foVec);
          foVec /= lDist;
        }
        sh = ObjSShadow (ro, (isNt ? foVec : sunDir), (isNt ? lDist : dstFar));
        if (isNt) {
          f = (0.3 + 0.7 * smoothstep (0., 0.05, - dot (rd, vn))) * max (dot (vn, foVec), 0.);
          col = col4.rgb * (0.05 + 0.15 * f + 5. * sh * f * (0.6 + 0.4 * flmFlkr) *
             pow (lDist, -1.5) * vec3 (1., 0.3, 0.2));
        } else ltSun = true;
      }
    }
  } else if (rd.y < 0.) {
    if (! isNt) sh = ObjSShadow (ro - ((ro.y + grDep)) / rd.y * rd, sunDir, dstFar);
    dstGrnd = - (ro.y + grDep) / rd.y;
    ro += dstGrnd * rd;
    vn = VaryNf (4. * ro, vec3 (0., 1., 0.), 2. * (1. - smoothstep (0.5, 1., dstGrnd / dstFar)));
    col4 = GrndCol (ro.xz);
    if (isNt) col4 *= 0.1;
    ltSun = true;
  } else {
    a = atan (rd.x, - rd.z);
    if (rd.y < 0.02 * Fbm1 (32. * a) + 0.005) {
      col = mix (vec3 (0.1, 0.1, 0.11), vec3 (0.2, 0.1, 0.),
         0.5 + 0.5 * dot (rd, - normalize (vec3 (sunDir.xz, 0.).xzy))) *
         (1. - 0.3 * Fbm2 (128. * vec2 (a, rd.y)));
      if (isNt) col*= 0.1;
    } else col = SkyCol (ro, rd);
  }
  if (ltSun) {
    col = col4.rgb * (0.1 + 0.1 * max (dot (vn, - normalize (vec3 (sunDir.xz, 0.).xzy)), 0.) +
       0.1 * max (vn.y, 0.) + 0.8 * sh * max (dot (sunDir, vn), 0.)) +
       col4.a * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col *= vec3 (1., 0.9, 0.8);
  }
  hideFlm = (dstObj < dstFar && idObjF == idAltr);
  if (flmRefl == objRefl) hideFlm = hideFlm || (dstObj < dstFlm.x);
  else hideFlm = hideFlm || (objRefl ? (dstWat < dstFlm.x) : (dstObj < dstWat));
  if (! hideFlm) col = mix (col, mix (vec3 (1., 0.2, 0.2), vec3 (0.8, 0.6, 0.2),
     smoothstep (0.5, 0.8, fIntens)), fIntens);
  if (objRefl) col = mix (col, max (- dot (rdo, VaryNf (0.4 * row + 0.1 * tCur,
     vec3 (0., 1., 0.), 0.5)), 0.) * vec3 (0., 0.2, 0.8), 0.15);
  return col;
}

void TrackInit ()
{
  wPt[0] = vec3 (0., 3., -16.);
  wPt[1] = vec3 (0., 3., -10.);
  wPt[2] = vec3 (-0.5, 3., -6.2);
  wPt[3] = vec3 (-2.6, 3., -6.2);
  wPt[4] = vec3 (-2.6, 3., 8.1);
  wPt[5] = wPt[4] * vec3 (-1., 1., 1.);
  wPt[6] = wPt[3] * vec3 (-1., 1., 1.);
  wPt[7] = wPt[2];
  wPt[8] = wPt[1];
  wPt[9] = wPt[0];
  tp[0]  = 0.;
  for (int j = 0; j < 9; j ++) tp[j + 1] = tp[j] + length (wPt[j + 1] - wPt[j]); 
  vel = 0.5;
  tCyc = tp[9] / vel;
}

vec3 TrackPath (float t)
{
  vec4 va, vb;
  vec3 ro;
  float tVu, s;
  int jVu;
  tVu = mod (t, tCyc) * vel;
  for (int j = 0; j < 9; j ++) {
    if (tVu < tp[j + 1]) {
      va = vec4 (wPt[j], tp[j]);
      vb = vec4 (wPt[j + 1], tp[j + 1]);
      jVu = j;
      break;
    }
  }
  s = (tVu - va.w) / (vb.w - va.w);
  ro = mix (va.xyz, vb.xyz, s);
  if (jVu == 3 || jVu == 5) ro.y += SmoothBump (0.2, 0.8, 0.1, s);
  return ro;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, u, col;
  vec2 canvas, uv, ori, ca, sa, mMid[2], ut[2], mSize;
  float el, az, asp, zmFac, hLkAt, vuId, regId;
  bool vuTrk;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  isNt = true;
  vuTrk = true;
  asp = canvas.x / canvas.y;
  mSize = (1./6.) * vec2 (asp, 1.);
  mMid[0] = vec2 (asp, 1. - mSize.y) * vec2 (1. - mSize.y, -1.);
  mMid[1] = vec2 (asp, -1. + mSize.y) * vec2 (1. - mSize.y, -1.);
  ut[0] = abs (uv - mMid[0]) - mSize;
  ut[1] = abs (uv - mMid[1]) - mSize;
  vuId = 0.;
  if (max (ut[0].x, ut[0].y) < 0.) {
    uv = (uv - mMid[0]) / mSize.y;
    isNt = ! isNt;
    vuId = 1.;
  } else if (max (ut[1].x, ut[1].y) < 0.) {
    uv = (uv - mMid[1]) / mSize.y;
    vuTrk = false;
    vuId = 2.;
  }
  regId = 0.;
  if (min (mPtr.x, - mPtr.y) > 0.5 - mSize.y) regId = 1.;
  else if (min (mPtr.x, mPtr.y) > 0.5 - mSize.y) regId = 2.;
  if (mPtr.z > 0. && regId == 2.) {
    vuTrk = (vuId == 1.);
    isNt = (vuId == 2.);
    if (mPtr.x > 0.5 - 0.5 * mSize.y) isNt = ! isNt;
  } else {
    if (vuId == 2.) isNt = false;
  }
  if (mPtr.z > 0. && regId == 1.) isNt = ! isNt;
  TrackInit ();
  if (vuTrk) {
    hLkAt = 3.;
    zmFac = 1.2;
    if (mPtr.z > 0. && regId == 0.) {
      ro = TrackPath (tCyc * (mPtr.x + 0.5));
      hLkAt += 5. * mPtr.y;
    } else {
      ro = 0.5 * (TrackPath (tCur + 0.2) + TrackPath (tCur - 0.2));
    }
    vd = normalize (vec3 (0., hLkAt, 0.) - ro);
    u = - vd.y * vd;
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
       vec3 (u.x, 1. + u.y, u.z) / sqrt (1. - vd.y * vd.y), vd);
  } else {
    az = 0.1 * pi * (floor (0.3 * tCur) + smoothstep (0.9, 1., mod (0.3 * tCur, 1.)));
    el = -0.02 * pi;
    el = clamp (el, -0.4 * pi, 0. * pi);
    ori = vec2 (el, az);
    ca = cos (ori);
    sa = sin (ori);
    vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
            mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
    ro = vuMat * vec3 (0., 1., -40.);
    zmFac = 2.7;
  }
  sunDir = normalize (vec3 (1., 0.3, 1.));
  dstFar = 100.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 4.;
#endif  
  col = vec3 (0.);
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.71 / canvas.y, 0.),
       0.5 * pi * (a + 0.5)), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < 2; k ++) {
    if (max (ut[k].x, ut[k].y) < 0. && min (abs (ut[k].x), abs (ut[k].y)) * canvas.y < 2.)
       col = vec3 (0.2, 0.2, 0.1);
  }
  fragColor = vec4 (pow (clamp (col, 0., 1.), vec3 (0.8)), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

