#define brickColor(t) palette(t, vec3(.5), vec3(.5), vec3(1.), vec3(0.00, 0.33, 0.67))


vec3 palette (in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d)
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 brickTile(vec2 _st, float _zoom){
    _st *= _zoom;

    // Here is where the offset is happening
    _st.x += 2.*(step(1., mod(_st.y,2.0))-0.500) * ( fract(iTime)) * step(1.,mod(iTime,2.));

    _st.y += 2.*(step(1., mod(_st.x,2.0))-0.500) * ( fract(iTime+1.)) * step(1.,mod(iTime+1.,2.));

    return fract(_st);
}

float box(vec2 _st, vec2 _size){
    _size = vec2(0.5)-_size*0.5;
    vec2 uv = smoothstep(_size,_size+vec2(1e-4),_st);
    uv *= smoothstep(_size,_size+vec2(1e-4),vec2(1.0)-_st);
    return uv.x*uv.y;
}

float circle (vec2 _st, float radius)
{	_st -= 0.5;
	return 1.-smoothstep(radius, radius*1.1,dot(_st,_st)*4.);    
}

float saw (in float t)
{
    return
    fract(t*step(1., mod(t,2.))
    +
    (1.- t*step(1., mod(t+1.,2.))));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 _st = fragCoord/iResolution.xy;
    vec3 color = vec3(0.0);

    _st = _st*2. -1.;

    _st.x *= iResolution.x/iResolution.y;

    // Apply the brick tiling
    vec2 st = brickTile(_st,5.0 + 2.*saw(iTime/50.));

    color = circle(st, 0.4 + 0.2*sin(iTime*6.)) * 0.7 *(vec3(.7) + brickColor(iTime/5. + _st.x)*0.4);

    fragColor = vec4(color,1.0);
}

