Shape environment(vec3 c){
  Shape shape;
  shape.dist = 1000.; // Draw Distance
  shape.color = vec4(1.); // Initial Color

  vec3 g = c; // Ground
  vec3 ph = c; // Placeholder
  vec3 w = c; // Blob Walls
  vec3 s = c; // Lamp Stand
  vec3 a = c; // Lamp
  vec3 h = c; // Holes
  vec3 as = c; // Arch Stand
  vec3 ar = c; // Arch
  vec3 sig = c; // Sign
  vec3 ab = c; // Arrow Base 
  vec3 ah = c; // Arrow Head 


  // Ground
  vec4 gColor = vec4(0.8, 1.0, 0.4, 1.0); 
  pMod1(g.z, 1.); 
  float ground = fBox(g+vec3(0.,1.8,0.4), vec3(4.5, 0.5, 3.)); 

  // PlaceHolder
  vec4 pColor = vec4(0.,0.,0.,0.); 
  float placeHolder = fBox(ph+vec3(2.,2. ,4.), vec3(3., 0., 3.)); 


  // Blob Walls 
  vec4 wColor = vec4(1., 0.1, 0.2, 1.0); 
  w.x = abs(w.x) - 7.;
  pMod1(w.y, -1.); 
  pMod1(w.z, 4.); 
  float wall = fSphere(w+vec3(2.5,0.5,-0.5), 0.4); 
  wall = fOpPipe(ground, wall, 2.); 

  // Lamp Stand
  vec4 sColor = vec4(1., 1., 1., 1.); 
  s.x = abs(s.x) - 2.3; 
  //s.z = abs(s.z) - 4.; 
  pMod1(s.z, 16.); 
  float stand = fBox(s+vec3(0., 0., -1.), vec3(0.2, 1.5, 0.2)); 

  // Lamp
  vec4 aColor = vec4(1., 1., 0., 1.); 
  pMod1(a.z, 16.); 
  float lamp = fBox(a+vec3(0., -1.3, -1.), vec3(2., 0.2, 0.2)); 
  lamp = fOpPipe(stand, lamp, .3); 

  // Arch Stand
  vec4 asColor = vec4(0., sin(iTime), 1., 1.); 
  as.x = abs(as.x) - 2.3; 
  pMod1(as.z, 16.); 
  float aStand = fBox(as+vec3(0.,0., -7.5), vec3(0.2, 1.5, 0.2)); 

  // Arch
  vec4 arColor = vec4(0., sin(iTime), 1., 1.); 
  pMod1(ar.z, 16.); 
  float arch = fBox(ar+vec3(-0.2, -1.3, -7.5), vec3(2.3, 0.2, 0.2)); 

  // Holes
  vec4 hColor = vec4(1.0, 0., 1., 1.); 
  pMod1(h.z, 4.); 
  float hole = fSphere(h+vec3(0.,1.8, 0.), 0.5); 

  // Sign 
  pMod1(sig.z, 16.); 
  float signn = fBox(sig+vec3(0., -1.3, -7.4), vec3(1.25, 0.75, 0.2)); 

  // Arrow 
  vec4 abColor = vec4(1., 0.5, 0.5, 1.); 
  vec4 ahColor = vec4(1., 0.5, 0.5, 1.); 
  pMod1(ab.z, 16.);
  pMod1(ah.z, 16.); 
  float arrowBase = fBox(ab+vec3(0., -1., -7.3), vec3(0.2, 0.3, 0.2));
  float arrowHead = sdTriPrism(ah+vec3(0., -1.3, -7.2), vec2(0.4, .1));

  ground = fOpUnionColumns(ground, wall, 1.3, 4.);    
  ground = fOpEngrave(ground, hole, 0.5); 

  shape.dist = max(ground, -placeHolder); 
  shape.dist = min(shape.dist, wall); 
  shape.dist = min(shape.dist, stand); 
  shape.dist = min(shape.dist, lamp); 
  shape.dist = min(shape.dist, hole); 
  shape.dist = min(shape.dist, aStand); 
  shape.dist = min(shape.dist, arch); 
  shape.dist = min(shape.dist, signn);
  shape.dist = min(shape.dist, arrowBase); 
  shape.dist = min(shape.dist, arrowHead);

  shape.color = mix(gColor, pColor, mixColors(placeHolder, ground, 0.0));  
  shape.color = mix(shape.color, wColor, mixColors(wall, shape.dist, 1.0));
  shape.color = mix(shape.color, sColor, mixColors(stand, shape.dist, 0.0));
  shape.color = mix(shape.color, aColor, mixColors(lamp, shape.dist, 0.1)); 
  shape.color = mix(shape.color, hColor, mixColors(hole, shape.dist, 0.1)); 
  shape.color = mix(shape.color, arColor, mixColors(arch, shape.dist, 0.1)); 
  shape.color = mix(shape.color, asColor, mixColors(aStand, shape.dist, 0.1)); 
  shape.color = mix(shape.color, abColor, mixColors(arrowBase, shape.dist, 0.1));
  shape.color = mix(shape.color, ahColor, mixColors(arrowHead, shape.dist, 0.1)); 

  return shape; 
}


Shape map(vec3 c){
  Shape enviro = environment(c);
  return enviro;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 v = (fragCoord.xy-0.5 * iResolution.xy) / iResolution.y;

  vec3 cam = vec3(0., 0., iTime*3.);
  cam.xy *= rot(radians(90.));
  vec3 f = normalize(vec3(v, 1.));
  vec3 scene = cam;
  //  scene.yx *= rot(iTime);
  fragColor = vec4(0.);

  // Ray Marcher
  for(float z = 0.1 ; z <= 1.; z += 0.03){
    Shape c = map(scene); // Calc SDF
    if(c.dist < 0.0001){
      fragColor = c.color*(1.-z); // Hit  - invert pixels
      break;
    }
    scene += f * c.dist;


  }
}

#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define HASHSCALE4 vec4(1031, .1030, .0973, .1099)

#define dir3(num) vec3(equal(abs(dir),vec3(num)))

#define dot2(p) dot(p,p)

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

#define pi acos(-1.0) //3.14
#define FAR 20.0      //raymarching and fog max distance
#define portals 0.22   //portal amount, from 0.0 to 1.0

//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
float hash31(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.x + p3.y)*p3.z);
}

float mid(vec3 p) {
    //if(var3)
    p = min(p,p.yzx);
    return max(max(p.x,p.y),p.z);
}

float torus(vec3 p, vec2 r) {//creates 4 toruses
    return length(vec2(abs(abs(length(p.xy)-r.x)-0.05),abs(p.z)-0.05))-r.y;
}

float map(vec3 p) {

    vec3 p2 = mod(p,2.0)-1.0;
    vec3 floorpos = floor(p*0.5);
    float len = 1e10;

    //the truchet flipping
    vec3 flipping = floor(hash33(floorpos)+0.5)*2.0-1.0;

    //actually flipping the truchet
    vec3 p3 = p2*flipping;

    //positions relative to truchet centers
    mat3 truchet = mat3(
        vec3(+p3.yz+vec2(-1.0, 1.0),p3.x),
        vec3(+p3.zx+vec2(-1.0, 1.0),p3.y),
        vec3(+p3.yx+vec2( 1.0,-1.0),p3.z)
    );

    //finding distance to truchet
    len = min(min(
        torus(truchet[0],vec2(1.0,0.01)),
        torus(truchet[1],vec2(1.0,0.01))),
        torus(truchet[2],vec2(1.0,0.01)));

    return len;
}

//normal calculation
vec3 findnormal(vec3 p, float len) {
    const vec2 eps = vec2(0.01,0.0);

    return normalize(vec3(
        len-map(p-eps.xyy),
        len-map(p-eps.yxy),
        len-map(p-eps.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 floorpos = vec3(0.0,0.0,0.0);
    vec3 pos = vec3(1.0,1.0,0.0);
    vec3 dir = vec3(3.0,2.0,1.0);
    int num = 2;
    float time = abs(mod(iTime*0.4+50.0,100.0)-50.0);
    for (float i = 0.0; i <= floor(time); i++) {

        pos += dir*dir3(1);

    	vec3 flipping = floor(hash33(floorpos)+0.5)*2.0-1.0; //the truchet flipping

        dir *= flipping;

        int num2 = (num-int(dot(dir,dir3(1)))+3)%3;
        float back = dir[num2];
        dir[num2] = dir[num];
        dir[num] = -back;
        num = num2;

        dir *= flipping;

        vec3 portalpos = floorpos+dir*dir3(1)*0.5;
        if (hash31(portalpos) >= portals) {
            portalpos += dir*dir3(1)*2.0;
        }
        if (hash31(portalpos) < portals) {
            floorpos += dir*dir3(1)*2.0;
        	pos += dir*dir3(1)*4.0;
        }
        floorpos += dir*dir3(1);
        pos += dir*dir3(1);
    }

    vec3 flipping = floor(hash33(floorpos)+0.5)*2.0-1.0; //the truchet flipping
    vec3 dir2 = dir;

    dir *= flipping;

    int num2 = (num-int(dot(dir,dir3(1)))+3)%3;
    float back = dir[num2];
    dir[num2] = dir[num];
    dir[num] = -back;
    num = num2;

    dir *= flipping;

    //animation
    pos += dir2*vec3(equal(abs(dir2),vec3(1.0)))*(sin(fract(time)*3.14*0.5));
    pos += dir*dir3(1)*(1.0-cos(fract(time)*3.14*0.5));

    //normal pointing towards where the camera moves, would be nice if the camera was looking in that direction
    vec3 forward = dir2*vec3(equal(abs(dir2),vec3(1.0)))*cos(fract(time)*3.14*0.5)+dir*dir3(1)*sin(fract(time)*3.14*0.5);

    /*
    mat3 rotation = mat3(
        vec3(0.0),
        vec3(0.0),
        vec3(0.0));
    rotation[2] = forward;
    rotation[1] = normalize(cross(forward,vec3(1)));
    rotation[0] = cross(rotation[1],forward);
    */

    mat3 rotation = mat3(
        vec3(0.0),
        vec3(0.0),
        vec3(0.0));

    vec2 t = vec2(cos(fract(time)*3.14*0.5),sin(fract(time)*3.14*0.5));
	rotation[2] = normalize(dir2*vec3(equal(abs(dir2),vec3(1)))*t.x+dir*dir3(1)*t.y);
    rotation[1] = normalize(dir2*vec3(equal(abs(dir2),vec3(2)))*t.x+dir*dir3(2)*t.y);
    rotation[0] = normalize(dir2*vec3(equal(abs(dir2),vec3(3)))*t.x+dir*dir3(3)*t.y);



    vec3 ro = pos+rotation[1]*0.125;
    vec3 rd = normalize(vec3(uv,1.0));

    if (length(iMouse.xy) > 40.0) {
    	rd.yz *= rot(iMouse.y/iResolution.y*3.14-3.14*0.5);
    	rd.xz *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
    }

    rd = rd*transpose(rotation);


    bool hit = true;
    float len;
    float dist = 0.0;

    //floorpos = floor(ro*0.5);
    vec3 signdir = sign(rd);
    vec3 invdir = 2.0/(abs(rd)+0.0001);
    vec3 dists = abs(-signdir*0.5-0.5+ro*0.5-floorpos)*invdir;

    for (int i = 0; i < 100; i++) {
        float smallest = min(min(dists.x,dists.y),dists.z);
        len = map(ro);
        if (len < 1.0/iResolution.y*(dist+1.0)||dist>FAR) {
            hit = dist < FAR;
            break;
        }

        len = min(len,smallest);

        ro += rd*len;
        dist += len;

        smallest -= len;
        dists -= len;
        if (smallest <= 0.0) {
            vec3 mask = step(dists,vec3(smallest));
            vec3 localpos = (ro+dot(mask*dists,vec3(1))*rd) - floorpos*2.0-1.0;

            vec2 q = vec2(dot(mask.yzx,localpos),dot(mask.zxy,localpos));

            vec3 portalpos = floorpos+mask*signdir*0.5;
            vec4 random = vec4(hash31(portalpos),hash33(portalpos));
            random.x = random.x/portals;
            //vec3 randompos = floor(hash33(portalpos)*100.0-50.0);

            float portalsize = 0.0;

            if (random.x > 1.0) {

            	portalpos += mask*signdir*2.0;
                random = vec4(hash31(portalpos),hash33(portalpos));
                random.x = random.x/portals;

            }
            if (random.x > 1.0) {
                portalsize=100.0;
        	} else {
                float t = iTime*random.x+random.y;

                portalsize =
                    length(q)
                    +sin(atan(q.x,q.y)* 8.0+t    )*0.04*sin(t*2.0)
                    +sin(atan(q.x,q.y)*16.0+t*1.5)*0.02*sin(t*4.0);
            }


            if (portalsize < 0.4) {
                //vec3 randdirection = vec3(equal(vec3(1,2,3),vec3(clamp(floor(random.x*3.0),0.0,3.0))));

                floorpos += mask*signdir;
                vec3 localpos = ro-floorpos*2.0;
                floorpos += mask*signdir*2.0;
                ro = floorpos*2.0+localpos;

                //randdirection *= floor(0.5+random.yzw)*2.0-1.0;

            } else if (portalsize < 0.45) {
                fragColor = vec4(-mask*signdir*0.5+0.5,1);
                fragColor = vec4(random.xzwy);
                return;
            } else {
                floorpos += mask*signdir;
            }
            dists = (1.0-mask)*dists+mask*invdir;
        }

    }
    if (hit) {

		fragColor = vec4(findnormal(ro,len)*0.5+0.5,1.0);
        fragColor /= (dist*dist*0.0005*FAR+1.0);
        //if (all(equal(floor(ro*0.5),floorpos))) fragColor = 1.0-fragColor;
    }
}

