const float PI = 3.141592654;
const float TWO_PI = 6.283185307;
const float HALF_PI = 1.5707963267948966;

// Some useful colours:
const vec3 white =      vec3(1.,   1.,   1.  );
const vec3 crimson =      vec3(1.,   0.,   0.3  );
const vec3 yellow =      vec3(1.,   1.,   0.  );
