vec3 drawBorder(vec3 col, float b, vec3 insetColour, vec2 pp, vec2 hr, float tileIndex) {
    // `b` is the border width
    // `col` is the input (and output) colour
    // `pp` is (unnormalised) coordinate of pixel
    // `hr` is (unnormalised) resolution
    // Make a border: `b` px solid black; with `insetColour` inset:
    if (tileIndex != -1.) {
    	//  any special code for single-pane use goes here    
    }
    // Make a line inset:
    if ((pp.x > b - 1. && pp.x <= b + 1.) || (pp.x > hr.x - b - 1. && pp.x < hr.x - b + 1.)) col = insetColour;
    if ((pp.y > b - 1. && pp.y <= b + 1.) || (pp.y > hr.y - b - 1. && pp.y < hr.y - b + 1.)) col = insetColour;

    // Now put a black border on top:
    col *= step(b, pp.x);
    col *= step(b, pp.y);
    col *= (1. - step(hr.x - b, pp.x));
    col *= (1. - step(hr.y - b, pp.y));
    return col;
}

vec2 psrandv2(float seed, int i) {
    vec2 a;
    a.x = cos(fract(cos(seed * 458333.003736561)) * (319.50207 + 10352.9911 * float(i)));
	a.y = sin(cos(fract(sin(seed * 4032.9908442016)) * (2786.2227 + 7046.88813 * float(i))));
	a.x *= fract(100000. * fract(dot(a, a.yx + vec2(26332.16598469, 7004.8112))));
    a.y *= fract(1000. * fract(dot(rotate(a, float(i) * 69.4177), a.yx + vec2(91104.33554432, 8112.7004))));
    return a * 0.5;
}

float minkd(vec2 u, vec2 v, float order) {	// Minkowski distance; order = 1
    if (order <= 0.) return 0.;				// => Manhattan distance
	return abs(pow(abs(pow(v.x - u.x, order)) + abs(pow(v.y - u.y, order)), 1. / order)); 
}


float mtime(float period) {
	return mod(iTime, period) / period;
}



// ..........................................................

float f(float x, float tile) {
    float t = iTime * 0.01;
    return exp(cos(15. * mtime(182.) * x) / 4. + cos(11. * mtime(113.) * x) * 1.2);       
}

float g(float x, float tile) {
 	float t = iTime * 0.01;
    return log(sin(3. * mtime(193.) * x) * 69. + cos(119. * mtime(252.) * x) / 12. + sin(7. * x) / 17.);       
}


// MAIN METHOD:

vec3 doStuff(vec2 pixel, vec2 res) {
    // just takes a pixel and a context and outputs a
    // colour to mainImage, which keeps things organised
    // and encapsulated.


    // Set this var to the number of tiles across and down:
    float tileDim = 3.;
    float numTiles = tileDim * tileDim;

    // the output vector, before normalisation,
    // giving the position the program needs to know!-
    vec2 pp = pixel;
    vec2 hr = res / tileDim;	// resolution of one tile
    vec3 col = magenta;

    // ===============================================================

    // Normalisation and tiling:
    // ========================

    // Make numTiles sub-frames:
    vec2 n = vec2(float(int(pixel.x / res.x * tileDim)), float(int(pixel.y / res.y * tileDim)));

    float tile = numTiles -(n.y * tileDim + n.x) - 1.; 
    // start at 1 so we don't lose stuff when multiplying
    float toe = fract(tile / 2.) * 4. - 1.; // returns 1. if tile index odd, -1. if even;
    float tile2 = tile * tile;

    // Offset the start of each rendition:
    float time = tile * 32. + iTime;
    // shift back to the first tile if in any other tile:
    pp.x -= hr.x * n.x;
    pp.y -= hr.y * n.y;
    // normalise to [0, 1[, then shift to make unit quad with origin in centre
    vec2 q = pp / hr - 0.5;     
    // then scale:
    float scaleFactor = 1.0;
    // q /= scaleFactor;

    // ===============================================================================================
    // ===============================================================================================

    // Main code for the shader goes here:
    // ===============================================================================================

    if (tile != 4.) {
    // q *= 2.; 
    q = rotate(q, toe * (iTime * 0.02 * (numTiles - tile - 1.) + (tile - 1.) * 0.25 * PI));	// vary angle for each tile

    float inc = 0.003;
    vec2[4] k = vec2[](vec2(-1., -1.), vec2(-1., 1.), vec2(1., -1.), vec2(1., 1.));

    float order = 1.;	// minkowski order (1 = Euclidean, 2 = Manhattan)


    //Initialise points[] array:

    // int numPoints = 6 + int(mod(iTime * 0.125, 128.));
    int numPoints = 8 + int(128. * sin (iTime / 512.));

    vec2 points[140];

    float sa = 313.95920007897932 * (numTiles - tile);
    float sb = 25.2649677703343 * tile;
    float rseed = fract(cos(fract(sin(sa + cos(fract(sa) * sb)))));

    for (int i = 0; i < numPoints; i++) {
    	points[i] = psrandv2(rseed, i) + k[int(mod(float(i), 4.))] * inc * mod(iTime, 128.) * 2.8;
        float hue = float(i) / float(numPoints - 1);
        col = hue2rgb(hue);
    }

    // My algorithm to draw the edges:
    // Iterate through the points and find the two nearest to q.
    // Give our point a hue determined by the i0, index of the 
    // closest point.
    // Then if the differences in the distances of q to each of
    // these two points is less than some small threshold value, 
    // it's roughly equidistant from the two nearest points, so 
    // make q black, as it's on the border.

    // Note: old code commented out and left ror reference and possible later re-use

    float mind0 = 1.;   // set min distances high
    float mind1 = 1.;  // 2nd smallest distance
    int i0 = 144;
    int i1 = 144;
    for (int i = 0; i < numPoints; i++) {   // find two closest points

        // TEST:
        float k = 0.01;
        q += k * sin(iTime / 20.);

        float d = minkd(q, points[i], 1.);
        if (d <= mind0) {		// new closest pt.
            mind1 = mind0;
            mind0 = d;
            i1 = i0;
            i0 = i;
        }
        else if (d <= mind1) {	// new next-closest pt.
            mind1 = d;
            i1 = i;
        }        
    }

    // float hue = float(toe < 0. ? i0 : (numPoints - 1 - i0)) / float(numPoints - 1);
    // float hueInv = 1. - hue;
    // col = hue2rgb(toe > 0. ? hue : hueInv);
    // col.g *= 0.8;

    col = vec3(abs(mod(float(i0), 3.) - 1.));
    // col = vec3(mod(float(i0), 3.) / 2., mod(float(i0), 5.) / 4., mod(float(i0), 7.) / 6.);
    col.g *=  sqrt(mind1);
    //col.b *= distance(q, points[i0]);
    col.b *= length(q) * nsin(iTime / 3.);

    //col *= smoothstep(0.01, 0.02, abs(minkd(q, points[i0], order) - minkd(q, points[i1], order)));
    col = toe > 0. ? col : col.bgr;
    col.g *= 0.8;
    // draw coloured dots:
    float d = 0.005;
    float m = mod(iTime, 32.) - 28.;
    if (m > 0.) {
        d *=2. * (m + tile / numTiles);
        col *= step(0.5, 1. - col.g); // make white rings black
    }
    float s = iTime + 50. * length(q);
    float c = float(cols.length());
    if (col.r == 0. && mind0 < d) col = cols[int(mod(s, c))];

    // todo: convert above to step /smoothstep
    col.g *= nsin(tile * tile);
    }
    // .................................




    else {

    // .................................
    // other code for centre tile:

    float t = iTime / 2048.;;

    // first scale:
    float scaleFactor = 15.0;
    float yscale = 0.2;
    q *= scaleFactor;
    q.y *= yscale;
    t = t * 128.;
    q.y *= hr.x /hr.y;
    q.x *= 0.35;
    q = abs(q);
    q = rotate(q, t * 6.);
    float phi = abs(arg(q));
    q = polar(length(q), mod(phi, PI / 16.)); 

    vec2 s0 = vec2(q.x - t, q.y);
    vec2 s1 = vec2(q.x + 2. * t, q.y);
    float k = 1.;
    float delta = 0.4;
    float yd0 = s0.y + delta;
    float yd1 = s1.y + delta;

    float rc = f(s0.x, k) * q.x;
    float bc = g(s1.x, k) * q.x;

    float hue = (smoothstep(s0.y, yd0, rc) - smoothstep(yd0, yd0 + delta, rc))
     + (smoothstep(s1.y, yd1, bc) - smoothstep(yd1, yd1 + delta, bc));
    col = hue2rgb(hue);
    col.r = 0.6;
    col.g = 0.;
    col *= 0.7;
    hue = rgb2hsl(col).x;

    col.g  = 0.;
    col.b += nsin(iTime / 4.) / 2.;    
    col.r += nsin(iTime / 7.) / 5.;    
    //col = black;

    }

    // ===============================================================================================
    // ===============================================================================================

    // Border code:    
    // ===============================================================================================

    float borderWidth = 3.;
    vec3 borderInsetLineColour = white;
    col = drawBorder(col, borderWidth, borderInsetLineColour, pp, hr, tile);

    // finally return the colour to caller(mainImage()):
    return col;
}	// END doStuff()

// ===============================================================================

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    float borderWidth = 6.;
    vec3 borderInsetLineColour = white;
 	vec3 col = doStuff(fragCoord, iResolution.xy);
    col = drawBorder(col, borderWidth, borderInsetLineColour, fragCoord, iResolution.xy, -1.);
    // finally return the colour:
    fragColor = vec4(col, 1.0);        
}

#define T iTime * 8.
#define PI2 6.28318
#define R iResolution.xy

//Dave Hoskins
//https://www.shadertoy.com/view/4djSRW
float H1(float p) {
	vec3 x  = fract(vec3(p) * .1031);
    x += dot(x, x.yzx + 19.19);
    return fract((x.x + x.y) * x.z);
}

//IQ cosine palattes
//http://www.iquilezles.org/www/articles/palettes/palettes.htm
vec3 PT(float t) {return vec3(.5) + vec3(.5) * cos(6.28318 * (vec3(1) * t + vec3(0, .33, .67)));}

vec3 render(vec3 rd) {
    float a = (atan(rd.y, rd.x) / PI2) + .5, //polar  0-1
          l = floor(a * 24.) / 24.; //split into 24 segemnts
    vec3 c = PT(H1(l + T * .0001)) * step(.1, fract(a * 24.)); //segment colour and edge
    float m = mod(abs(rd.y) + H1(l) * 4. - T * .01, .3); //split segments 
    return c * step(m, .16) * m * 16. * max(abs(rd.y), 0.); //split segments
}

void mainImage(out vec4 C, vec2 U) {

    //ray direction
    vec2 uv = (U - R * .5) / R.y;
    vec3 f = vec3(0, 0, 1),
         r = vec3(f.z, 0, -f.x),
         d = normalize(f + 1. * uv.x * r + 1. * uv.y * cross(f, r));

    C = vec4(render(d), 1.);
}

void mainVR(out vec4 C, vec2 U, vec3 ro, vec3 rd) {
    C = vec4(render(rd), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = (
        vec3(
            //cos(abs(uv.y-0.5)+iTime*2.0),
            //cos(abs(uv.y-0.5)-iTime),
            0.5*(0.5-abs(uv.y-0.5+cos(iTime*8.0)/3.0))*6.0,
            0.5*(0.5-abs(uv.x-0.5+cos(iTime*4.0)/3.0))*8.0,
            0.5*(0.5-abs(uv.x-0.5+cos(iTime*2.0)/3.0))*8.0
        )

    );
    col=col+vec3(0,-0.5,-0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 iCol(int i){
    switch(i){
        case 0 : return vec3(0.8,0.9,.1);
        case 1 : return vec3(1,.6,0.6);
        case 2 : return vec3(1,1,.6);
    }
}

vec3 dCol(float d){
    return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float asp = iResolution.x/iResolution.y;
    uv.x *= asp;

    // Time varying pixel color
    vec3 col = vec3(0);

    vec2[3] p;
    p[0] = vec2(0.9 + 0.3 * sin(iTime),0.5 + 0.4*cos(iTime));
    p[1] =vec2(0.9 + 0.6 * sin(iTime*1.72),0.4 + 0.3*cos(iTime*1.31));
    p[2] =vec2(0.9 + 0.3 * sin(iTime*1.14),0.5 + 0.5*cos(iTime*0.72));

    float dmin = 999.0f;
    int minI = 0;

    for(int i=0;i<3;i++){
        if(length(uv-p[i])<0.01f)col = vec3(1);
        float d = length(uv - p[i]);
        if(abs(d) < 0.01f){
            col = dCol(d);
            fragColor = vec4(col,1.0);
            return;
        }
        if(dmin > d){
            dmin = d;
            minI = i;
        }

    }
    col = iCol(minI);
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;


    // Time varying pixel color
    vec3 col = vec3(1., .05, .05);

    uv.x *= 1.2; //pulls in the top 
    uv.y -= sqrt(abs(uv.x))*.5; //creates shape of the heart

    uv.xy *= sin(iTime); //creates motion of beating heart

    float d = length(uv);

    float c = smoothstep(.3, .28,d); //draws shape

    float pct = distance(uv, vec2(0.5) *-.9)*smoothstep(.8, uv.x, uv.y); //distance to centre, creates gradient

    col = vec3(c)*col * pct; //adds shape, color and gradient 

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 rand(vec3 xyz) {
    return fract(vec3(sin(dot(xyz, vec3(43.238, 27.874, 57.982))),
    				  sin(dot(xyz, vec3(91.922, 11.838, 77.133))),
    				  sin(dot(xyz, vec3(43.238, 27.874, 57.982))))*
                 vec3(9283.9502,8329.9128,3201.1984));
}


vec3 curp(vec3 v0, float t, vec3 v1) {
    t = t * t * (3.0 - 2.0 * t);
    return mix(v0, v1, t);
}

vec3 lerp(vec3 v0, float t, vec3 v1) {
    return mix(v0, v1, t);
}

vec3 noise(vec3 xyz) {
    vec3 v0 = floor(xyz);
    vec3 f = xyz - v0;
    return
      curp(curp(lerp(rand(v0),               f.z, rand(v0 + vec3(0,0,1))),
                f.y,
                lerp(rand(v0 + vec3(0,1,0)), f.z, rand(v0 + vec3(0,1,1)))),
           f.x,
           curp(lerp(rand(v0 + vec3(1,0,0)), f.z, rand(v0 + vec3(1,0,1))),
                f.y,
                lerp(rand(v0 + vec3(1,1,0)), f.z, rand(v0 + vec3(1,1,1)))));
}

vec3 simplex(vec3 xyz, int octaves) {
    float a = 1.0;
    vec3 result = vec3(0);
    for (int o = 1 << octaves; o > 0; o >>= 1, a /= 2.) {
        result += (2. * noise(xyz / float(o)) - 1.) * a;
    }
    return result;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.x - vec2(0.5, 0.5 * iResolution.y / iResolution.x);
    vec3 per = simplex(vec3(uv * 120., iTime * 2.), 5);
    float a = dot(normalize(per), normalize(vec3(uv, 0.1)));
    a = 1. * pow(a, 5.);
    fragColor.rgb = vec3(a) * 1.;
    fragColor.a = 1.0;
}

