// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
    vec2 bound = sphIntersect( ro, rd, 2.1 );
    tmin = max(tmin,bound.x);
    tmax = min(tmax,bound.y);

	float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
    	vec4 kk;
		float h = map( ro + rd*t, kk, false );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float raycast(in vec3 ro, in vec3 rd, in float tmin, in float tmax  )
{
    vec4 kk;
    float t = tmin;
	for( int i=0; i<512; i++ )
    {
		vec3 p = ro + t*rd;
        float h = map(p,kk,false);
		if( abs(h)<(0.15*t/iResolution.x) ) break;
		t += h*0.5;
        if( t>tmax ) return -1.0;;
	}
    //if( t>tmax ) t=-1.0;

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float an = (iTime-10.0)*0.05;

    // camera	
    vec3 ro = vec3( 4.5*sin(an), 0.0, 4.5*cos(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera-to-world rotation
    mat3 ca = makeBase( normalize(ta-ro) );

    // render    
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.2) );

        // background
        vec3 col = vec3(0.1,0.14,0.18) + 0.1*rd.y;

        // bounding volume
        vec2 bound = sphIntersect( ro, rd, 2.1 );
		if( bound.x>0.0 )
        {
        // raycast
        float t = raycast(ro, rd, bound.x, bound.y );
        if( t>0.0 )
        {
            // local geometry            
            vec3 pos = ro + t*rd;
        	vec3 nor = calcNormal(pos, 0.01);
            vec3 upp = normalize(pos);

            // color and occlusion
            vec4 mate; map(pos, mate, true);

            // lighting            
            col = vec3(0.0);

            // key ligh
            {
                // dif
                vec3 lig = normalize(vec3(1.0,0.0,0.7));
                float dif = clamp(0.5+0.5*dot(nor,lig),0.0,1.0);
                float sha = calcSoftshadow( pos+0.0001*nor, lig, 0.0001, 2.0, 6.0 );
                col += mate.xyz*dif*vec3(1.8,0.6,0.5)*1.1*vec3(sha,sha*0.3+0.7*sha*sha,sha*sha);
				// spec
                vec3 hal = normalize(lig-rd);
                float spe = clamp( dot(nor,hal), 0.0, 1.0 );
                float fre = clamp( dot(-rd,lig), 0.0, 1.0 );
                fre = 0.2 + 0.8*pow(fre,5.0);
                spe *= spe;
                spe *= spe;
                spe *= spe;
                col += 1.0*(0.25+0.75*mate.x)*spe*dif*sha*fre;
            }

            // back light
           	{
                vec3 lig = normalize(vec3(-1.0,0.0,0.0));
                float dif = clamp(0.5+0.5*dot(nor,lig),0.0,1.0);
                col += mate.rgb*dif*vec3(1.2,0.9,0.6)*0.2*mate.w;
            }

            // dome light
            {
                float dif = clamp(0.3+0.7*dot(nor,upp),0.0,1.0);
                #if 0
                dif *= 0.05 + 0.95*calcSoftshadow( pos+0.0001*nor, upp, 0.0001, 1.0, 1.0 );
                col += mate.xyz*dif*5.0*vec3(0.1,0.1,0.3)*mate.w;
                #else
                col += mate.xyz*dif*3.0*vec3(0.1,0.1,0.3)*mate.w*(0.2+0.8*mate.w);
                #endif
            }

            // fake sss
            {
                float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
                col += 0.3*vec3(1.0,0.3,0.2)*mate.xyz*mate.xyz*fre*fre*mate.w;
            }

            // grade/sss
            {
            	col = 2.0*pow( col, vec3(0.7,0.85,1.0) );
            }

            // exposure control
            col *= 0.7 + 0.3*smoothstep(0.0,25.0,abs(iTime-31.0));

            // display fake occlusion
            //col = mate.www;
        }
        }


        // gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // vignetting
 	vec2 q = fragCoord/iResolution.xy;
    tot *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.2 );

    fragColor = vec4( tot, 1.0 );
}

float wave(float x, float y) 
{
    return sin(10.0*x+10.0*y) / 5.0 +
           sin(20.0*x+15.0*y) / 3.0 +
           sin(4.0*x+10.0*y) / -4.0 +
           sin(y) / 2.0 +
           sin(x*x*y*20.0) + 
           sin(x * 20.0 + 4.0) / 5.0 +
           sin(y * 30.0) / 5.0 + 
    	   sin(x) / 4.0; 

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float z = wave(uv.x, uv.y) + 2.0;


    z *= 4.0 * sin(1.57 + iTime / 5.0);
    float d = fract(z);
    if(mod(z, 2.0) > 1.) d = 1.-d;


    d = d/fwidth(z);
    fragColor = vec4(d);

}

#define smooth (1.0 / iResolution.x)
#define PI      3.14159265359
#define lines 5.0
#define rings 77.0
#define swirl 2.0
#define size 0.005
#define poss 0.12
#define widths 0.015
#define time (iTime * 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = (fragCoord.xy -0.5 * iResolution.xy)/ iResolution.x;
    float width = widths;
    float pos = poss;

    vec2 mouse = (iMouse.xy -0.5 * iResolution.xy) / iResolution.x;
    {
        float a =(atan(st.x,st.y) + PI)/PI/2.;
        float aa = (floor(a * rings) + 0.5)/rings;
        aa = aa * 2.0 * PI - PI;
        float ll = pos;

        vec2 center = vec2(ll*sin(aa),ll*cos(aa));
    	float dist= texture(iChannel0,vec2(floor(a * rings)/rings,0.0)).x; 
    	//float dist = length(center - mouse);
        //dist = 1.0 - dist;
        //dist = pow(dist,14.);
        width = widths + 0.05 * dist;
        pos += 0.1 * dist;
    }


    vec3 finCol = vec3(0.0);
    float maxz = 0.0;


    for(float i = 0.0; i < lines; ++i){

        float il = i /lines;

    	float a =(atan(st.x,st.y) + PI)/PI/2.;
    	float aa = (floor(a * rings) + 0.5)/rings;
    	aa = aa * 2.0 * PI - PI;
    	float offset = floor(a * rings)/rings * 2.0 * PI * swirl + il * 2.0 * PI;
    	float rt = cos(time + offset)/2.0 + 0.5;
    	float rm = sin(time + offset)/2.0 + 0.5;
    	float ll = mix(pos - width,pos + width,rt);

    	vec2 center = vec2(ll*sin(aa),ll*cos(aa));

        float ring = length(center - st);
        ring = smoothstep(smooth,-smooth,ring -size *(1.0 +  rm)/2.0);
        finCol = max(finCol,ring * (0.25 + rm)/1.25);
    }

	fragColor = vec4(finCol,1.0);
}

float quintic(float x) {
 	return x*x*x*(6.*x*x-15.*x+10.);
}

float fft(float p) {
    return texture(iChannel0, vec2(p, 0.25)).x;
}

const float fac = 43758.5453123;

float hash(float p) {
    return fract(fac*sin(p));
}

float noise(in vec3 p) {
    vec3 i = floor(p);
    vec3 f = fract(p);

    float fac = 43758.5453123;
    const float upper = 100.;
    vec3 m = vec3(1., 10., 100.);
    vec2 o = vec2(1., 0.);

    float n000 = hash(dot((i + o.yyy), m));
    float n001 = hash(dot((i + o.xyy), m));
    float n010 = hash(dot((i + o.yxy), m));
    float n011 = hash(dot((i + o.xxy), m));
    float n100 = hash(dot((i + o.yyx), m));
    float n101 = hash(dot((i + o.xyx), m));
    float n110 = hash(dot((i + o.yxx), m));
    float n111 = hash(dot((i + o.xxx), m));

    float fx = quintic(f.x);
    float fy = quintic(f.y);
    float fz = quintic(f.z);

    float px00 = mix(n000, n001, fx);
    float px01 = mix(n010, n011, fx);

    float px10 = mix(n100, n101, fx);
    float px11 = mix(n110, n111, fx);

    float py0 = mix(px00, px01, fy);
    float py1 = mix(px10, px11, fy);

    return mix(py0, py1, fz);
}


float noise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    vec2 m = vec2(100., 1.);
    vec2 o = vec2(1., 0.);

    float n00 = hash(dot((i + o.yy), m));
    float n01 = hash(dot((i + o.xy), m));
    float n10 = hash(dot((i + o.yx), m));
    float n11 = hash(dot((i + o.xx), m));

    float fx = quintic(f.x);
    float px0 = mix(n00, n01, fx);
    float px1 = mix(n10, n11, fx);

    return mix(px0, px1, quintic(f.y));
}

float ocean(vec2 p) {
    float f = fft(abs(1.-p.x))*0.2 + fft(abs(p.x))*0.52;

    float speed = .8;
    vec2 v01 = vec2( 1.,  0.) * iTime*speed;
    vec2 v02 = vec2( 0.,  1.) * iTime*speed;
    vec2 v03 = vec2( 1.,  1.) * iTime*speed;
    vec2 v04 = vec2(-1.,  0.) * iTime*speed;
    vec2 v05 = vec2(-1.,  0.) * iTime*speed;
    vec2 v06 = vec2(-1., -1.) * iTime*speed;

    f += 0.50000*noise(p*1.0  + v01); //*fft(p.x);
    f += 0.25000*noise(p*2.1  + v02);
    f += 0.12500*noise(p*3.9  + v03);
    f += 0.06250*noise(p*8.1  + v04);
    f += 0.03215*noise(p*15.8 + v05);
    f += 0.01608*noise(p*32.3 + v06);

    f = (3.-2.*f)*f*f;

    return f;

}

float map(in vec3 p) {   
    float o = ocean(p.xz * 0.08) * 3.;
    return p.y + 0.5 + o;
}

float calcShadow(in vec3 ro, in vec3 rd, float tmax) {
    float r = 1.;
    float t = 0.;
    for(int i = 0; i < 128; i++) {
        float h = map(ro + t * rd);
        r = min(r, tmax*h/t);
        if (r < 0.01) break;
        if (t > tmax) break;
        t += h;
    }
    return clamp(r, 0., 1.);
}

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(0.01, 0.);
    return normalize(vec3(
        map(p + e.xyy) - map(p - e.xyy),
        map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
    ));

}


const vec3 SUN_DIR = normalize(vec3(-0.2, 0.15, -0.8));
const vec3 SUN_COL = vec3(0.95, 0.8, 0.85);

vec3 sky(in vec3 rd, float fac) {
    vec3 stars = pow(vec3(noise(rd.xy*iResolution.x)), vec3(90.));
    rd.y = max(0., rd.y);

    vec3 blue = 0.6* vec3(0.02, 0.09, 0.2) -rd.y*0.12;
    vec3 sunset = blue * (cos(fac * 6.28)*.7 + 1.); 
    return sunset + stars;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord - iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0., 0., 0.);
    vec3 ta = vec3(0., 0., 1000.);
    float an = 1.2;
    vec3 up = normalize(vec3(cos(an), 1., sin(an)));
    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww, up));
    vec3 vv = normalize(cross(uu, ww));

    vec3 rd = normalize(vec3(p.x*uu + p.y*vv - 2.*ww));

    float m = -1.;
    float t = 0.;
    float tmax = 300.;
    for(int i = 0; i<512; i++) {
        float h  = map(ro + rd * t);
        if ( h<0.01 ) { m = 1.; break; };
        if ( t>tmax ) break;
        t += h;
    }

    float sunsetFac = mod(iTime*0.12 + fft(0.1), 1.);
    vec3 skyCol = sky(rd, sunsetFac);
    vec3 col = skyCol;


    vec3 vol = vec3(0.);
    float den = 0.;
    float h = noise(fragCoord+p);
    float dh = 0.1*tmax / 32.0;
    rd.y  = -rd.y;
    rd.xz = rd.xz*mat2(0.8, -0.6, 0.6, 0.8);
    for (int i = 0; i < 32; i++) {
        vec3 pos = ro + h*rd;
        vec3 dir = SUN_DIR - pos;
        vec3 l = (vec3(0.1, 0.99, 0.1)*calcShadow(pos, normalize(dir), length(dir)));
        float d = noise(pos + 2.*vec3(iTime, -iTime, -iTime));

        d *= exp(-0.85*pos.y);

        den += d*0.001;
        vol += l*den;

        if(den > 1.) break;

        h += dh;
    }
    col += pow(vec3(vol), vec3(1.5));

    col = pow(col, vec3(0.4545));

    fragColor = vec4(col,1.0);
}

#define S(a,b,t) smoothstep(a,b,t)
#define WD 2.
#define nTime (iTime*0.3)+5000.
#define FACT vec2(0.3,0.13)

float Dline(vec2 p, vec2 a, vec2 b) {
	// line drawing function from BigWings
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.1);
    return length(pa-ba*t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.);

    float param = asin(cos(float(iFrame)/4000.))*20.; // triangular parameter

    for ( float i = 0.; i<7.; i+=0.1)
    {
    for ( float ii = 14.; ii<20.; ii+=0.5)
    {
    float d1 = sin(nTime-ii)-cos(i+nTime+param);
    float d2 = sin(nTime-i)+cos(ii+i)-sin(ii);
    float d3 = sin(nTime-ii)+cos(i+nTime-param);
    float d4 = sin(nTime-i)-cos(ii+i)+sin(ii-param);

    float d = Dline(uv, vec2(d1,d2)*FACT, vec2(d3,d4)*FACT);

    float m = S(WD/200., (WD/200.)-(0.02), d)*WD;

    float col1 = (cos(nTime+ii)/2.+0.6);
    float col2 = (sin((ii+nTime))/2.+0.5);
   // float col3 = (cos((i+nTime)/9.)/ii+0.5);

    col = max(col,vec3(m*col1,m*col2,m*col2));
    }
    }

    // Output to screen

    fragColor = vec4(col,1.0);
}

// "Hilbert 3D" by dr2 - 2018
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// 3D Hilbert curves (algorithm derived from al13n's "Space Filling Curve")

#define AA  0

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
vec3 HsvToRgb (vec3 c);
vec3 RotToDir (vec3 v1, vec3 v2, vec3 p);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);

vec3 ltPos, qHilb;
float dstFar, tCur, szFac, cFac, vShift, zDisp;
int idObj, nIter;
bool doSh;
const int maxIter = 5;
const float pi = 3.14159, sqrt3 = 1.73205;

vec3 HilbCrv (vec3 p)
{
  vec3 s;
  vec2 cv;
  float lLen, cLen, dp, xRev, zRev, zAdd;
  cLen = 1. / (2. * cFac - 1.);
  lLen = cFac * cFac * (1. - ((1. - 0.25 * pi) * cFac - 1.) * cLen);
  dp = 1. + cLen;
  xRev = 1.;
  zRev = 1.;
  zAdd = 0.;
  for (int j = 0; j < maxIter; j ++) {
    lLen *= 0.125;
    dp *= 0.5;
    s = sign (p);
    zAdd += lLen * zRev * s.z * (4. - s.y * (2. - s.x));
    if (s.x > 0.) p = p.zxy * vec3 (- s.z, 1., - s.y);
    else if (s.y > 0.) p = p.yxz * vec3 (-1., -1., - s.z);
    else if (s.z > 0.) p = p.yzx * vec3 (1., -1., 1.);
    else p = p.zyx;
    p += dp * vec3 (1., ((s.x + s.y < 0.) ? 1. : -1.), 1.);
    if (s.x + s.z < 0.) xRev = - xRev;
    else if (s.x > 0.) {
      if (s.y != s.z) zRev = - zRev;
    } else zRev = - zRev;
    if (j == nIter - 1) break;
  }
  cv = vec2 (p.y, - p.z) + cLen;
  if (min (cv.x, cv.y) > 0.) p.yz = vec2 (length (cv) - cLen, (atan (cv.x, cv.y) - 0.25 * pi) * cLen);
  else if (p.y + p.z < 0.) p.yz = p.zy * vec2 ( -1., 1.) + vec2 (0., (1. - 0.25 * pi) * cLen);
  else p.z -= (1. - 0.25 * pi) * cLen;
  p.xz *= vec2 (xRev, zRev);
  p.z += zAdd;
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, w;
  q = p / szFac;
  q.y -= vShift;
  q.xz = Rot2D (q.xz, pi + 0.25 * pi * sin (0.03 * 2. * pi * tCur));
  q = RotToDir (vec3 (-1., 1., 1.) / sqrt3, vec3 (0., 1., 0.), q);
  w = 0.5 / cFac;
  d = PrRoundBoxDf (q, vec3 (1. - 0.26 * w), 0.01);
  if (doSh || d < 0.1) {
    qHilb = HilbCrv (q);
    q = qHilb;
    q.z = mod (q.z + zDisp, w) - 0.5 * w;
    d = max (d, min (PrRoundBox2Df (q.xy, vec2 (0.1 * w), 0.05 * w), PrSphDf (q, 0.4 * w)));
    idObj = 1;
  }
  dMin = 0.7 * szFac * d;
  q = p;
  q.y -= 0.02;
  d = PrCylDf (q.xzy, 0.8, 0.02);
  if (d < dMin) { dMin = d;  idObj = 2; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  doSh = true;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.4 + 0.6 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, ltDir;
  float dstObj, dstGrnd, vdl, sh, att, h;
  cFac = pow (2., float (nIter));
  zDisp = 1. * tCur / cFac;
  szFac = 1. + 0.8 / cFac;
  vShift = (sqrt3 - 1.3 / cFac) + 0.04;
  doSh = false;
  dstObj = ObjRay (ro, rd);
  dstGrnd = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  if (min (dstObj, dstGrnd) < dstFar) {
    ltDir = normalize (ltPos);
    if (dstObj < dstGrnd) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      vdl = max (dot (vn, ltDir), 0.);
      h = 0.05 * zDisp * cFac;
      if (idObj == 1) h += 0.5 + 0.5 * qHilb.z / (cFac * cFac);
      if (idObj == 1 || length (ro.xz) < 0.6) {
        col = HsvToRgb (vec3 (mod (h, 1.), 1., 1.));
        vdl *= vdl * vdl;
      } else col = vec3 (0.5);
    } else {
      ro += dstGrnd * rd;
      vn = vec3 (0., 1., 0.);
      col = vec3 (0.4) * (0.7 + 0.3 * Fbm2 (4. * ro.xz));
      vdl = max (dot (vn, ltDir), 0.);
    }
    att = smoothstep (0.9, 0.95, dot (normalize (ltPos - ro), ltDir));
    sh = ObjSShadow (ro, ltDir);
    col = att * (col * (0.2 + 0.8 * sh * vdl) +
       0.2 * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.));
  } else col = vec3 (0.);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, ltEl, ltAz, ns;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  }
  el = clamp (el, -0.25 * pi, 0.05 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., sqrt3, -10.);
  zmFac = 5. + 10. * abs (az) / pi;
  ltEl = -0.2 * pi * (1. + 0.2 * sin (0.2 * 2. * pi * tCur));
  ltAz = pi + 0.2 * pi * cos (0.25 * 2. * pi * tCur);
  ltPos = vec3 (0., 0., 20.);
  ltPos.yz = Rot2D (ltPos.yz, ltEl);
  ltPos.xz = Rot2D (ltPos.xz, ltAz);
  dstFar = 100.;
  ns = mod (0.25 * tCur, float (2 * (maxIter - 1) - 1));
  nIter = 2 + int (min (ns, 2. * float (maxIter - 1) - ns));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 4.;
#endif  
  col = vec3 (0.);
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.71 / canvas.y, 0.),
       0.5 * pi * (a + 0.5)), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

