vec4 BGLayer = vec4(horizon,1.0);
vec4 LayerOne = vec4(0.0,0.0,0.0,0.0);
vec4 LayerTwo = vec4(0.0,0.0,0.0,0.0);

// Makes the moon / eclipse animate
moonPos.x += sin(iTime*ECLIPSE_FREQ)*ECLIPSE_MAGN;
// Creates the sun and moon circles
float sun = smoothstep(sunRadius, sunRadius+SMOOTH_BY,distance(sunPos,uv));
float moon = smoothstep(moonRadius, moonRadius+SMOOTH_BY, distance(moonPos,uv)); 

// Draws Layer One (The Sun) and Layer Two (The Eclipse / Moon)
LayerOne = vec4(1.0,0.9,0.2, moon);
LayerTwo = vec4(horizon,sun);

