// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
        (c - a)* u.y * (1.0 - u.x) +
        (d - b) * u.x * u.y;
}
mat2 m = mat2(0.8, 0.6, -0.6, 0.8);
float fbm(vec2 p) {
	float f = 0.0;
    f += 0.5 * noise(p); p *= m * 2.0;
    f += 0.25 * noise(p); p *= m * 2.0;
    f += 0.125 * noise(p); p *= m * 2.0;
    f += 0.0625 * noise(p); p *= m * 2.0;
    f /= 0.9375;
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float e = 2./iResolution.y;
	vec2 uv = (fragCoord - 0.5 * iResolution.xy)*2.0 / iResolution.y;
    float radius = 0.7;
   	float r = length(uv);

    float ss = 0.5 + 0.5 * sin(3.5 * iTime);
    float anim = 1. + 0.2 * ss * clamp(1. - r, 0., 1.);
    r *= anim;

    vec3 col = vec3(1., 1., 1.);
    if (length(uv) < radius) {
        col = vec3(0.31, 0.235, 0.043);
        float angle = atan(uv.y / uv.x);
        angle += 0.05 * fbm(20.0 * uv);
        col = mix(col, vec3(0.682, 0.475, 0.113), fbm(vec2(r, angle * 0.3)));

        float white_fiber = smoothstep(0.3, 1.0, fbm(vec2(r * 6., angle * 20.)));
        col = mix(col, vec3(1., 1., 1.), white_fiber);

        float black_fiber = smoothstep(0.3, 1.0, fbm(vec2(r * 7., angle * 15.)));
        col = mix(col, vec3(0.3, 0.3, 0.3), black_fiber);
        float outter_pupil = smoothstep(0.12, 0.35, r);
        col = mix(vec3(0.9, 0.6, 0.2), col, outter_pupil);
        float pupil = smoothstep(0.12 - 5. * e, 0.12 + 5. * e, r);
        col = mix(vec3(0.094, 0.063, 0.0078), col, pupil);

        float f = smoothstep(radius - 0.3, radius, r);
        col *= 1. - 0.5 * f;

        float border = smoothstep(radius - 0.1, radius, r);
    	col = mix(col, vec3(1., 1., 1.), border);

        float high_light = smoothstep(0.35, 0., length(uv - vec2(0.15, 0.2)));
        col = mix(col, vec3(0.9), high_light);
    }

    fragColor = vec4(col,1.0);
}

