  vec4 bodCol = vec4(1., 1., 1. + cos(iTime)*.5, 1.);
  vec4 halCol = bodCol;
  vec4 orCol = vec4(1. * cos(iTime)*10., 24./255., 1./255., 1.);
  vec4 orTwoCol = orCol;
  vec4 frCol = vec4(fr.x + sin(iTime) + 244./255., fr.y*cos(iTime) - 10./255., cos(fr.y*fr.x*iTime) + 1./255., 1.);
  //shape.color = lanCol;
  shape.color = mix(lanCol, bodCol, mixColors(body, lantern, 0.));
  shape.color = mix(shape.color, orCol, mixColors(orb, shape.dist, 1.));
  shape.color = mix(shape.color, orTwoCol, mixColors(orbtwo, shape.dist, 1.));
  shape.color = mix(shape.color, frCol, mixColors(frame, shape.dist, 1.));
    lanCol.rg += snoise(lan.xy*.1) + .5*sin(iTime);
    lanCol.gb -= snoise(lan.yz*.25) + cos(iTime);
  //MIXING COLORS

  return shape;
}


